import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function AddCourse() {
    const navigate = useNavigate();
    const [categories, setCategories] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);
    const [selectedCategory, setSelectedCategory] = useState({ id: "", name: "", path: "" });
    const categoryDropdownRef = useRef(null);

    const [courseData, setCourseData] = useState({
        title: "",
        description: "",
        instructor: "You", // Default instructor
        price: 0,
        category: "",
        level: "",
        duration: "",
        thumbnailUrl: "",
        videoUrl: "",
        tags: [""],
        language: "",
        isPublished: false,
    });

    // Fetch categories from backend
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const res = await axios.get("http://localhost:5000/api/admin/categories", {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
                    },
                });
                setCategories(res.data);
            } catch (err) {
                console.error("Error fetching categories", err);
            }
        };

        fetchCategories();
    }, []);

    // Close dropdown when clicking outside
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (categoryDropdownRef.current && !categoryDropdownRef.current.contains(event.target)) {
                setShowCategoryDropdown(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    // Organize categories into hierarchical structure
    const organizeCategories = (cats) => {
        const categoryMap = {};
        const rootCategories = [];

        // Create a map of all categories
        cats.forEach(cat => {
            categoryMap[cat._id] = { ...cat, children: [] };
        });

        // Organize them hierarchically
        cats.forEach(cat => {
            if (cat.parentCategory && categoryMap[cat.parentCategory]) {
                categoryMap[cat.parentCategory].children.push(categoryMap[cat._id]);
            } else {
                rootCategories.push(categoryMap[cat._id]);
            }
        });

        return rootCategories;
    };

    const organizedCategories = organizeCategories(categories);

    // Category selection handler
    const handleCategorySelect = (category, parentNames = []) => {
        const categoryPath = [...parentNames, category.name].join(' > ');
        setSelectedCategory({
            id: category._id,
            name: category.name,
            path: categoryPath
        });
        setCourseData(prev => ({ ...prev, category: category.name }));
        setShowCategoryDropdown(false);
    };

    // Hierarchical Category Dropdown Component
    const HierarchicalCategoryDropdown = ({ categories, parentNames = [], level = 0 }) => {
        const [hoveredCategory, setHoveredCategory] = useState(null);

        return (
            <div className={`category-level-${level}`}>
                {categories.map((category) => (
                    <div
                        key={category._id}
                        className="position-relative"
                        onMouseEnter={() => setHoveredCategory(category._id)}
                        onMouseLeave={() => setHoveredCategory(null)}
                    >
                        <div
                            className={`dropdown-item d-flex justify-content-between align-items-center ${category.children.length > 0 ? 'has-children' : ''
                                }`}
                            onClick={() => {
                                if (category.children.length === 0) {
                                    handleCategorySelect(category, parentNames);
                                }
                            }}
                            style={{
                                cursor: 'pointer',
                                backgroundColor: hoveredCategory === category._id ? 'rgba(0,123,255,0.1)' : 'transparent',
                                paddingLeft: `${level * 10 + 16}px`
                            }}
                        >
                            <div className="d-flex align-items-center">
                                <span className={`badge me-2 ${category.level === 1 ? 'bg-primary' :
                                        category.level === 2 ? 'bg-success' : 'bg-warning'
                                    }`}>
                                    {category.level === 1 ? 'Main' :
                                        category.level === 2 ? 'Sub' : 'Sub-Sub'}
                                </span>
                                <span>{category.name}</span>
                            </div>
                            {category.children.length > 0 && (
                                <i className="fas fa-chevron-right text-muted"></i>
                            )}
                        </div>

                        {/* Submenu */}
                        {category.children.length > 0 && hoveredCategory === category._id && (
                            <div
                                className="dropdown-submenu position-absolute top-0 start-100 bg-white border rounded shadow-lg"
                                style={{
                                    minWidth: '250px',
                                    zIndex: 1050,
                                    maxHeight: '300px',
                                    overflowY: 'auto'
                                }}
                            >
                                <HierarchicalCategoryDropdown
                                    categories={category.children}
                                    parentNames={[...parentNames, category.name]}
                                    level={level + 1}
                                />
                            </div>
                        )}
                    </div>
                ))}
            </div>
        );
    };

    const [activeTab, setActiveTab] = useState("basic");
    const [dragActive, setDragActive] = useState(false);

    const levels = ["Beginner", "Intermediate", "Advanced"];
    const languages = [
        "English",
        "Hindi",
        "Spanish",
        "French",
        "German",
        "Chinese",
        "Japanese",
    ];

    const handleInputChange = (field, value) => {
        setCourseData((prev) => ({ ...prev, [field]: value }));
    };

    const handleArrayChange = (field, index, value) => {
        setCourseData((prev) => ({
            ...prev,
            [field]: prev[field].map((item, i) => (i === index ? value : item)),
        }));
    };

    const addArrayItem = (field) => {
        setCourseData((prev) => ({
            ...prev,
            [field]: [...prev[field], ""],
        }));
    };

    const removeArrayItem = (field, index) => {
        setCourseData((prev) => ({
            ...prev,
            [field]: prev[field].filter((_, i) => i !== index),
        }));
    };

    const handleDrag = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (e.type === "dragenter" || e.type === "dragover") {
            setDragActive(true);
        } else if (e.type === "dragleave") {
            setDragActive(false);
        }
    };

    const handleDrop = (e, fileType) => {
        e.preventDefault();
        e.stopPropagation();
        setDragActive(false);

        const files = e.dataTransfer.files;
        if (files && files[0]) {
            handleFileUpload(files[0], fileType);
        }
    };

    const handleFileUpload = (file, fileType) => {
        if (fileType === "thumbnail") {
            setCourseData((prev) => ({
                ...prev,
                thumbnailFile: file,
                thumbnailUrl: URL.createObjectURL(file),
            }));
        } else if (fileType === "promoVideo") {
            setCourseData((prev) => ({
                ...prev,
                promoVideoFile: file,
                videoUrl: URL.createObjectURL(file),
            }));
        }
    };

    const handleSubmit = async (action) => {
        try {
            setIsLoading(true);
            setError(null);

            const formData = new FormData();
            formData.append("title", courseData.title);
            formData.append("description", courseData.description);
            formData.append("instructor", courseData.instructor);
            formData.append("price", courseData.price);
            formData.append("category", courseData.category);
            formData.append("level", courseData.level);
            formData.append("duration", courseData.duration);
            formData.append("language", courseData.language);
            formData.append("isPublished", action === "published");
            formData.append("thumbnail", courseData.thumbnailFile);

            formData.append("videoUrl", courseData.promoVideoFile || "");
            formData.append("tags", JSON.stringify(courseData.tags));

            const token = localStorage.getItem("adminToken");
            const response = await axios.post(
                "http://localhost:5000/api/admin/add-course",
                formData,
                {
                    headers: {
                        "Content-Type": "multipart/form-data",
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (response.data && response.data.message === "Course added successfully") {
                alert(`Course ${action === "published" ? "published" : "saved as draft"} successfully!`);
                navigate("/courses");
            } else {
                throw new Error("Failed to add course");
            }
        } catch (err) {
            setError(err.response?.data?.message || err.message || "Failed to save course");
            console.error("Course submission error:", err);
        } finally {
            setIsLoading(false);
        }
    };

    const TabButton = ({ id, label, icon }) => (
        <button
            onClick={() => setActiveTab(id)}
            className={`btn me-2 d-flex align-items-center ${activeTab === id ? "btn-primary" : "btn-outline-secondary"}`}
        >
            <span className="me-2">{icon}</span>
            <span>{label}</span>
        </button>
    );

    const FileUploadZone = ({ fileType, accept, label, description, file }) => (
        <div
            className={`border border-2 border-dashed rounded p-4 text-center ${dragActive ? "border-secondary bg-light" : "border-secondary"}`}
            style={{ minHeight: "200px" }}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={(e) => handleDrop(e, fileType)}
        >
            {file ? (
                <div className="d-flex flex-column align-items-center">
                    <div
                        className="bg-success bg-opacity-25 rounded-circle p-3 mb-3"
                        style={{ width: "80px", height: "80px" }}
                    >
                        <span style={{ fontSize: "2rem" }}>
                            {fileType === "thumbnail" ? "ðŸ–¼ï¸" : "ðŸŽ¥"}
                        </span>
                    </div>
                    <p className="fw-bold mb-2">
                        {typeof file === "string" ? file.split("/").pop() : file.name}
                    </p>
                    <button
                        onClick={() => {
                            if (fileType === "thumbnail") {
                                setCourseData((prev) => ({
                                    ...prev,
                                    thumbnailUrl: "",
                                    thumbnailFile: null
                                }));
                            } else if (fileType === "promoVideo") {
                                setCourseData((prev) => ({
                                    ...prev,
                                    videoUrl: "",
                                    promoVideoFile: null
                                }));
                            }
                        }}
                        className="btn btn-sm btn-outline-danger"
                    >
                        Remove
                    </button>
                </div>
            ) : (
                <div className="d-flex flex-column align-items-center">
                    <div
                        className="bg-light rounded-circle p-3 mb-3"
                        style={{ width: "80px", height: "80px" }}
                    >
                        <span style={{ fontSize: "2rem" }}>ðŸ“¤</span>
                    </div>
                    <div className="mb-3">
                        <p className="fw-bold mb-1">{label}</p>
                        <p className="text-muted small">{description}</p>
                    </div>
                    <input
                        type="file"
                        accept={accept}
                        onChange={(e) =>
                            e.target.files[0] && handleFileUpload(e.target.files[0], fileType)
                        }
                        className="d-none"
                        id={`${fileType}-upload`}
                    />
                    <label htmlFor={`${fileType}-upload`} className="btn btn-primary">
                        Choose File
                    </label>
                </div>
            )}
        </div>
    );

    return (
        <div className="min-vh-100 bg-light">
            {/* Custom CSS for dropdown styling */}
            <style jsx>{`
                .category-dropdown {
                    max-height: 400px;
                    overflow-y: auto;
                }
                .dropdown-item.has-children:hover {
                    background-color: rgba(0,123,255,0.1) !important;
                }
                .dropdown-submenu {
                    z-index: 1051 !important;
                }
                .dropdown-item {
                    transition: background-color 0.2s ease;
                }
                .category-selector {
                    position: relative;
                }
            `}</style>

            {/* Header */}
            <div className="bg-white border-bottom">
                <div className="container-fluid py-4">
                    <div className="d-flex justify-content-between align-items-center">
                        <div className="d-flex align-items-center">
                            <div
                                className="bg-primary rounded p-2 me-3"
                                style={{ width: "48px", height: "48px" }}
                            >
                                <span className="text-white fs-4"></span>
                            </div>
                            <div>
                                <h1 className="h2 mb-1">Add New Course</h1>
                                <p className="text-muted mb-0">
                                    Create and publish your course content
                                </p>
                            </div>
                        </div>
                        <div className="d-flex gap-2">
                            <button
                                onClick={() => handleSubmit("draft")}
                                className="btn btn-outline-secondary"
                                disabled={isLoading}
                            >
                                {isLoading ? "Saving..." : "Save Draft"}
                            </button>
                            <button
                                onClick={() => handleSubmit("published")}
                                className="btn btn-primary"
                                disabled={isLoading}
                            >
                                {isLoading ? "Publishing..." : "Publish Course"}
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            {error && (
                <div className="container-fluid mt-3">
                    <div className="alert alert-danger">{error}</div>
                </div>
            )}

            <div className="container-fluid py-4">
                {/* Navigation Tabs */}
                <div className="bg-white rounded shadow-sm p-3 mb-4">
                    <div className="d-flex flex-wrap gap-2">
                        <TabButton id="basic" label="Basic Info" icon="ðŸ“" />
                        <TabButton id="media" label="Media" icon="ðŸ–¼ï¸" />
                        <TabButton id="content" label="Content" icon="ðŸ“š" />
                        <TabButton id="pricing" label="Pricing" icon="ðŸ’°" />
                    </div>
                </div>

                <div className="row">
                    {/* Main Content */}
                    <div className="col-lg-8">
                        {/* Basic Information Tab */}
                        {activeTab === "basic" && (
                            <div className="card shadow-sm">
                                <div className="card-body">
                                    <h2 className="card-title h4 mb-4">Basic Information</h2>

                                    <div className="row g-3">
                                        <div className="col-12">
                                            <label className="form-label fw-bold">
                                                Course Title *
                                            </label>
                                            <input
                                                type="text"
                                                value={courseData.title}
                                                onChange={(e) =>
                                                    handleInputChange("title", e.target.value)
                                                }
                                                placeholder="Enter a compelling course title"
                                                className="form-control"
                                                required
                                            />
                                        </div>

                                        <div className="col-12">
                                            <label className="form-label fw-bold">
                                                Course Description *
                                            </label>
                                            <textarea
                                                value={courseData.description}
                                                onChange={(e) =>
                                                    handleInputChange("description", e.target.value)
                                                }
                                                placeholder="Provide a detailed description of your course content, objectives, and target audience..."
                                                rows={6}
                                                className="form-control"
                                                style={{ resize: "none" }}
                                                required
                                            />
                                        </div>

                                        {/* Hierarchical Category Selector */}
                                        <div className="col-md-4">
                                            <label className="form-label fw-bold">Category *</label>
                                            <div className="category-selector" ref={categoryDropdownRef}>
                                                <div
                                                    className="form-control d-flex justify-content-between align-items-center"
                                                    style={{ cursor: 'pointer' }}
                                                    onClick={() => setShowCategoryDropdown(!showCategoryDropdown)}
                                                >
                                                    <span className={selectedCategory.name ? 'text-dark' : 'text-muted'}>
                                                        {selectedCategory.path || 'Select Category'}
                                                    </span>
                                                    <i className={`fas fa-chevron-${showCategoryDropdown ? 'up' : 'down'}`}></i>
                                                </div>

                                                {showCategoryDropdown && (
                                                    <div className="position-absolute w-100 bg-white border rounded shadow-lg mt-1 category-dropdown" style={{ zIndex: 1050 }}>
                                                        {organizedCategories.length > 0 ? (
                                                            <HierarchicalCategoryDropdown categories={organizedCategories} />
                                                        ) : (
                                                            <div className="dropdown-item text-muted">
                                                                No categories available
                                                            </div>
                                                        )}
                                                    </div>
                                                )}
                                            </div>
                                            {selectedCategory.name && (
                                                <div className="form-text">
                                                    Selected: <span className="fw-bold">{selectedCategory.path}</span>
                                                </div>
                                            )}
                                        </div>

                                        <div className="col-md-4">
                                            <label className="form-label fw-bold">Level *</label>
                                            <select
                                                value={courseData.level}
                                                onChange={(e) =>
                                                    handleInputChange("level", e.target.value)
                                                }
                                                className="form-select"
                                                required
                                            >
                                                <option value="">Select Level</option>
                                                {levels.map((level) => (
                                                    <option key={level} value={level}>
                                                        {level}
                                                    </option>
                                                ))}
                                            </select>
                                        </div>

                                        <div className="col-md-4">
                                            <label className="form-label fw-bold">Language *</label>
                                            <select
                                                value={courseData.language}
                                                onChange={(e) =>
                                                    handleInputChange("language", e.target.value)
                                                }
                                                className="form-select"
                                                required
                                            >
                                                <option value="">Select Language</option>
                                                {languages.map((lang) => (
                                                    <option key={lang} value={lang}>
                                                        {lang}
                                                    </option>
                                                ))}
                                            </select>
                                        </div>

                                        <div className="col-12">
                                            <label className="form-label fw-bold">
                                                Instructor Name
                                            </label>
                                            <input
                                                type="text"
                                                value={courseData.instructor}
                                                onChange={(e) =>
                                                    handleInputChange("instructor", e.target.value)
                                                }
                                                placeholder="Course instructor name"
                                                className="form-control"
                                            />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Media Tab */}
                        {activeTab === "media" && (
                            <div className="card shadow-sm">
                                <div className="card-body">
                                    <h2 className="card-title h4 mb-4">Media Assets</h2>

                                    <div className="row g-4">
                                        <div className="col-12">
                                            <h3 className="h5 mb-3">Course Thumbnail</h3>
                                            <FileUploadZone
                                                fileType="thumbnail"
                                                accept="image/*"
                                                label="Upload Course Thumbnail"
                                                description="Recommended: 1280x720px, JPG or PNG"
                                                file={courseData.thumbnailUrl ? { name: "Thumbnail uploaded" } : null}
                                            />
                                        </div>

                                        <div className="col-12">
                                            <h3 className="h5 mb-3">Promotional Video</h3>
                                            <FileUploadZone
                                                fileType="promoVideo"
                                                accept="video/*"
                                                label="Upload Promo Video"
                                                description="Optional: MP4 format, max 100MB"
                                                file={courseData.videoUrl ? { name: "Video uploaded" } : null}
                                            />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Content Tab */}
                        {activeTab === "content" && (
                            <div className="card shadow-sm">
                                <div className="card-body">
                                    <h2 className="card-title h4 mb-4">Course Content</h2>

                                    <div className="row g-4">
                                        <div className="col-12">
                                            <label className="form-label fw-bold">Course Tags</label>
                                            <p className="text-muted small mb-3">
                                                Add relevant tags to help students find your course
                                            </p>
                                            {courseData.tags.map((tag, index) => (
                                                <div
                                                    key={index}
                                                    className="d-flex align-items-center mb-3"
                                                >
                                                    <input
                                                        type="text"
                                                        value={tag}
                                                        onChange={(e) =>
                                                            handleArrayChange("tags", index, e.target.value)
                                                        }
                                                        placeholder={`Tag ${index + 1}`}
                                                        className="form-control me-2"
                                                    />
                                                    {courseData.tags.length > 1 && (
                                                        <button
                                                            onClick={() => removeArrayItem("tags", index)}
                                                            className="btn btn-outline-danger btn-sm"
                                                        >
                                                            âŒ
                                                        </button>
                                                    )}
                                                </div>
                                            ))}
                                            <button
                                                onClick={() => addArrayItem("tags")}
                                                className="btn btn-outline-primary btn-sm"
                                            >
                                                âž• Add Tag
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Pricing Tab */}
                        {activeTab === "pricing" && (
                            <div className="card shadow-sm">
                                <div className="card-body">
                                    <h2 className="card-title h4 mb-4">Pricing & Details</h2>

                                    <div className="row g-3">
                                        <div className="col-md-6">
                                            <label className="form-label fw-bold">
                                                Course Price (â‚¹)
                                            </label>
                                            <input
                                                type="number"
                                                value={courseData.price}
                                                onChange={(e) =>
                                                    handleInputChange("price", e.target.value)
                                                }
                                                placeholder="0.00"
                                                min="0"
                                                step="0.01"
                                                className="form-control"
                                            />
