import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageCategories = () => {
  // Load categories from localStorage or use default
  const [categories, setCategories] = useState(() => {
    const saved = localStorage.getItem("categories");
    return saved ? JSON.parse(saved) : [
      { id: 1, name: "Web Development", slug: "web-development", isActive: true },
      { id: 2, name: "Mobile Development", slug: "mobile-development", isActive: true },
      { id: 3, name: "Data Science", slug: "data-science", isActive: true },
    ];
  });

  // Sync categories to localStorage on change
  useEffect(() => {
    localStorage.setItem("categories", JSON.stringify(categories));
  }, [categories]);


//   // State management
//   const [categories, setCategories] = useState(initialCategories);
  const [name, setName] = useState("");
  const [slug, setSlug] = useState("");
  const [isActive, setIsActive] = useState(true);
  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Basic form handlers
  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingId) {
      setCategories(
        categories.map((cat) =>
          cat.id === editingId ? { ...cat, name, slug, isActive } : cat
        )
      );
    } else {
      const newCategory = {
        id: Math.max(...categories.map((c) => c.id), 0) + 1,
        name,
        slug: slug || name.toLowerCase().replace(/\s+/g, "-"),
        isActive,
      };
      setCategories([...categories, newCategory]);
    }
    resetForm();
    setShowModal(false);
  };

  const resetForm = () => {
    setName("");
    setSlug("");
    setIsActive(true);
    setEditingId(null);
  };

  const handleEdit = (category) => {
    setName(category.name);
    setSlug(category.slug);
    setIsActive(category.isActive);
    setEditingId(category.id);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this category?")) {
      // First filter out the deleted category
      const updatedCategories = categories.filter((cat) => cat.id !== id);

      // Then reindex the remaining categories
      const reindexedCategories = updatedCategories.map((cat, index) => ({
        ...cat,
        id: index + 1,
      }));

      setCategories(reindexedCategories);
    }
  };

  const toggleStatus = (id) => {
    setCategories(
      categories.map((cat) =>
        cat.id === id ? { ...cat, isActive: !cat.isActive } : cat
      )
    );
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-5">
        <h2 className="mb-0">Manage Categories</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
        >
          Add New Category
        </button>
      </div>

      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>No</th>
            <th>Name</th>
            <th>Slug</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category.id}>
              <td>{category.id}</td>
              <td>{category.name}</td>
              <td>{category.slug}</td>
              <td>
                <span
                  className={`badge bg-${
                    category.isActive ? "success" : "secondary"
                  }`}
                >
                  {category.isActive ? "Active" : "Inactive"}
                </span>
              </td>
              <td>
                <button
                  className="btn btn-sm btn-outline-primary me-2"
                  onClick={() => handleEdit(category)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-sm btn-outline-danger me-2"
                  onClick={() => handleDelete(category.id)}
                >
                  Delete
                </button>
                <button
                  className={`btn btn-sm btn-outline-${
                    category.isActive ? "warning" : "success"
                  }`}
                  onClick={() => toggleStatus(category.id)}
                >
                  {category.isActive ? "Deactivate" : "Activate"}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal */}
      {showModal && (
        <div
          className="modal"
          style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {editingId ? "Edit Category" : "Add New Category"}
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label className="form-label">Category Name</label>
                    <input
                      type="text"
                      className="form-control"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Slug</label>
                    <input
                      type="text"
                      className="form-control"
                      value={slug}
                      onChange={(e) => setSlug(e.target.value)}
                      placeholder="Will be auto-generated if empty"
                    />
                  </div>
                  <div className="form-check form-switch mb-3">
                    <input
                      className="form-check-input"
                      type="checkbox"
                      id="activeSwitch"
                      checked={isActive}
                      onChange={(e) => setIsActive(e.target.checked)}
                    />
                    <label className="form-check-label" htmlFor="activeSwitch">
                      Active Category
                    </label>
                  </div>
                  <div className="modal-footer">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      onClick={() => setShowModal(false)}
                    >
                      Cancel
                    </button>
                    <button type="submit" className="btn btn-primary">
                      {editingId ? "Update" : "Save"}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageCategories;
