// src/pages/ManageCourses.jsx
import React, { useEffect, useState } from "react";
import api from "../utils/api"; // Axios instance

function ManageCourses() {
    const [courses, setCourses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [showModal, setShowModal] = useState(false);
    const [editingCourse, setEditingCourse] = useState(null);
    const [formData, setFormData] = useState({
        title: "",
        instructor: "",
        price: "",
        description: "",
        duration: "",
        category: "",
        level: "Beginner",
        isActive: true
    });

    // Format currency in INR
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('en-IN', {
            style: 'currency',
            currency: 'INR',
            minimumFractionDigits: 0,
        }).format(amount);
    };

    // Fetch courses
    const fetchCourses = async () => {
        try {
            setLoading(true);
            const response = await api.get("/courses");
            setCourses(response.data);
            setError("");
        } catch (err) {
            console.error("Error fetching courses:", err);
            setError("Failed to fetch courses. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchCourses();
    }, []);

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    // Open modal for edit
    const openModal = (course) => {
        setEditingCourse(course);
        setFormData({
            title: course.title || "",
            instructor: course.instructor || "",
            price: course.price || "",
            description: course.description || "",
            duration: course.duration || "",
            category: course.category || "",
            level: course.level || "Beginner",
            isActive: course.isActive !== undefined ? course.isActive : true
        });
        setShowModal(true);
    };

    // Close modal
    const closeModal = () => {
        setShowModal(false);
        setEditingCourse(null);
        setFormData({
            title: "",
            instructor: "",
            price: "",
            description: "",
            duration: "",
            category: "",
            level: "Beginner",
            isActive: true
        });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Update existing course
            await api.put(`/courses/${editingCourse._id}`, formData);

            closeModal();
            fetchCourses();
            setError("");
        } catch (err) {
            console.error("Error updating course:", err);
            setError("Failed to update course.");
        }
    };

    // Delete course
    const handleDelete = async (courseId) => {
        if (window.confirm("Are you sure you want to delete this course?")) {
            try {
                await api.delete(`/courses/${courseId}`);
                fetchCourses();
                setError("");
            } catch (err) {
                console.error("Error deleting course:", err);
                setError("Failed to delete course.");
            }
        }
    };

    // Toggle course status
    const toggleCourseStatus = async (courseId, currentStatus) => {
        try {
            await api.patch(`/courses/${courseId}`, { isActive: !currentStatus });
            fetchCourses();
            setError("");
        } catch (err) {
            console.error("Error updating course status:", err);
            setError("Failed to update course status.");
        }
    };

    if (loading) {
        return (
            <div className="container mt-4">
                <div className="d-flex justify-content-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>Manage Courses</h2>
            </div>

            {error && (
                <div className="alert alert-danger alert-dismissible fade show" role="alert">
                    {error}
                    <button
                        type="button"
                        className="btn-close"
                        onClick={() => setError("")}
                    ></button>
                </div>
            )}

            <div className="card">
                <div className="card-body">
                    {courses.length === 0 ? (
                        <div className="text-center py-4">
                            <i className="fas fa-book fa-3x text-muted mb-3"></i>
                            <h5>No courses found</h5>
                            <p className="text-muted">No courses are currently available.</p>
                        </div>
                    ) : (
                        <div className="table-responsive">
                            <table className="table table-hover">
                                <thead className="table-dark">
                                    <tr>
                                        <th>Title</th>
                                        <th>Instructor</th>
                                        <th>Category</th>
                                        <th>Level</th>
                                        <th>Duration</th>
                                        <th>Price</th>
                                        <th>Status</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {courses.map((course) => (
                                        <tr key={course._id}>
                                            <td>
                                                <div>
                                                    <strong>{course.title}</strong>
                                                    {course.description && (
                                                        <div className="text-muted small">
                                                            {course.description.length > 50
                                                                ? `${course.description.substring(0, 50)}...`
                                                                : course.description}
                                                        </div>
                                                    )}
                                                </div>
                                            </td>
                                            <td>{course.instructor}</td>
                                            <td>
                                                {course.category && (
                                                    <span className="badge bg-secondary">{course.category}</span>
                                                )}
                                            </td>
                                            <td>
                                                <span className={`badge ${course.level === 'Beginner' ? 'bg-success' :
                                                        course.level === 'Intermediate' ? 'bg-warning' : 'bg-danger'
                                                    }`}>
                                                    {course.level || 'Beginner'}
                                                </span>
                                            </td>
                                            <td>{course.duration || 'N/A'}</td>
                                            <td>
                                                <strong className="text-success">
                                                    {formatCurrency(course.price)}
                                                </strong>
                                            </td>
                                            <td>
                                                <button
                                                    className={`btn btn-sm ${course.isActive !== false ? 'btn-success' : 'btn-secondary'
                                                        }`}
                                                    onClick={() => toggleCourseStatus(course._id, course.isActive !== false)}
                                                >
                                                    {course.isActive !== false ? 'Active' : 'Inactive'}
                                                </button>
                                            </td>
                                            <td>
                                                <div className="btn-group" role="group">
                                                    <button
                                                        className="btn btn-sm btn-outline-primary"
                                                        onClick={() => openModal(course)}
                                                        title="Edit Course"
                                                    >
                                                        <i className="fas fa-edit"></i>
                                                    </button>
                                                    <button
                                                        className="btn btn-sm btn-outline-danger"
                                                        onClick={() => handleDelete(course._id)}
                                                        title="Delete Course"
                                                    >
                                                        <i className="fas fa-trash"></i>
                                                    </button>
                                                </div>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>

            {/* Add/Edit Course Modal */}
            {showModal && (
                <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Edit Course</h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    onClick={closeModal}
                                ></button>
                            </div>
                            <form onSubmit={handleSubmit}>
                                <div className="modal-body">
                                    <div className="row">
                                        <div className="col-md-6 mb-3">
                                            <label htmlFor="title" className="form-label">Course Title *</label>
                                            <input
                                                type="text"
                                                className="form-control"
                                                id="title"
                                                name="title"
                                                value={formData.title}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="col-md-6 mb-3">
                                            <label htmlFor="instructor" className="form-label">Instructor *</label>
                                            <input
                                                type="text"
                                                className="form-control"
                                                id="instructor"
                                                name="instructor"
                                                value={formData.instructor}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-4 mb-3">
                                            <label htmlFor="price" className="form-label">Price (₹) *</label>
                                            <input
                                                type="number"
                                                className="form-control"
                                                id="price"
                                                name="price"
                                                value={formData.price}
                                                onChange={handleInputChange}
                                                min="0"
                                                step="1"
                                                required
                                            />
                                        </div>
                                        <div className="col-md-4 mb-3">
                                            <label htmlFor="duration" className="form-label">Duration</label>
                                            <input
                                                type="text"
                                                className="form-control"
                                                id="duration"
                                                name="duration"
                                                value={formData.duration}
                                                onChange={handleInputChange}
                                                placeholder="e.g., 4 weeks, 20 hours"
                                            />
                                        </div>
                                        <div className="col-md-4 mb-3">
                                            <label htmlFor="level" className="form-label">Level</label>
                                            <select
                                                className="form-select"
                                                id="level"
                                                name="level"
                                                value={formData.level}
                                                onChange={handleInputChange}
                                            >
                                                <option value="Beginner">Beginner</option>
                                                <option value="Intermediate">Intermediate</option>
                                                <option value="Advanced">Advanced</option>
                                            </select>
                                        </div>
                                    </div>

                                    <div className="mb-3">
                                        <label htmlFor="category" className="form-label">Category</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="category"
                                            name="category"
                                            value={formData.category}
                                            onChange={handleInputChange}
                                            placeholder="e.g., Programming, Design, Business"
                                        />
                                    </div>

                                    <div className="mb-3">
                                        <label htmlFor="description" className="form-label">Description</label>
                                        <textarea
                                            className="form-control"
                                            id="description"
                                            name="description"
                                            value={formData.description}
                                            onChange={handleInputChange}
                                            rows="3"
                                            placeholder="Brief description of the course..."
                                        ></textarea>
                                    </div>

                                    <div className="form-check">
                                        <input
                                            className="form-check-input"
                                            type="checkbox"
                                            id="isActive"
                                            name="isActive"
                                            checked={formData.isActive}
                                            onChange={handleInputChange}
                                        />
                                        <label className="form-check-label" htmlFor="isActive">
                                            Course is active and available for enrollment
                                        </label>
                                    </div>
                                </div>
                                <div className="modal-footer">
                                    <button
                                        type="button"
                                        className="btn btn-secondary"
                                        onClick={closeModal}
                                    >
                                        Cancel
                                    </button>
                                    <button type="submit" className="btn btn-primary">
                                        Update Course
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ManageCourses;
