import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const UpdateCourse = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    const [course, setCourse] = useState({
        title: "",
        description: "",
        instructor: "",
        price: "",
        category: "",
        level: "Beginner",
        duration: "",
        thumbnailUrl: "",
        videoUrl: "",
        tags: "",
        language: "English",
        isPublished: false,
    });

    const [message, setMessage] = useState("");

    // Fetch course data by ID to prefill form
    useEffect(() => {
        const fetchCourse = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/admin/course/${id}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
                    },
                });

                // Convert tags array to comma-separated string for input
                const tagsString = Array.isArray(res.data.tags) ? res.data.tags.join(", ") : "";

                setCourse({
                    ...res.data,
                    price: res.data.price || "",
                    tags: tagsString,
                    isPublished: !!res.data.isPublished,
                    level: res.data.level || "Beginner",
                    language: res.data.language || "English",
                });
            } catch (error) {
                console.error("Error fetching course:", error);
                setMessage("Failed to load course details");
            }
        };
        fetchCourse();
    }, [id]);

    // Handle form input changes
    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;

        if (type === "checkbox") {
            setCourse(prev => ({ ...prev, [name]: checked }));
        } else {
            setCourse(prev => ({ ...prev, [name]: value }));
        }
    };

    // Handle form submit to update course
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Convert tags string back to array
        const tagsArray = course.tags.split(",").map(tag => tag.trim()).filter(tag => tag.length > 0);

        try {
            const res = await axios.put(
                `http://localhost:5000/api/admin/course/${id}`,
                {
                    ...course,
                    price: Number(course.price),
                    tags: tagsArray,
                },
                {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
                    },
                }
            );

            if (res.data.message) {
                setMessage("✅ Course updated successfully");
                setTimeout(() => {
                    navigate("/admin/manage-courses"); // adjust route as needed
                }, 1500);
            }
        } catch (error) {
            console.error("Update error:", error);
            setMessage("❌ Failed to update course");
        }
    };

    return (
        <div>
            <h2>Update Course</h2>
            {message && <p>{message}</p>}
            <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px", maxWidth: "400px" }}>
                <input
                    type="text"
                    name="title"
                    placeholder="Course Title"
                    value={course.title}
                    onChange={handleChange}
                    required
                />
                <textarea
                    name="description"
                    placeholder="Course Description"
                    value={course.description}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="instructor"
                    placeholder="Instructor Name"
                    value={course.instructor}
                    onChange={handleChange}
                />
                <input
                    type="number"
                    name="price"
                    placeholder="Course Price"
                    value={course.price}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="category"
                    placeholder="Category"
                    value={course.category}
                    onChange={handleChange}
                />
                <select name="level" value={course.level} onChange={handleChange}>
                    <option value="Beginner">Beginner</option>
                    <option value="Intermediate">Intermediate</option>
                    <option value="Advanced">Advanced</option>
                </select>
                <input
                    type="text"
                    name="duration"
                    placeholder="Duration (e.g. 5 hours)"
                    value={course.duration}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="thumbnailUrl"
                    placeholder="Thumbnail URL"
                    value={course.thumbnailUrl}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="videoUrl"
                    placeholder="Video URL"
                    value={course.videoUrl}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="tags"
                    placeholder="Tags (comma separated)"
                    value={course.tags}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="language"
                    placeholder="Language"
                    value={course.language}
                    onChange={handleChange}
                />
                <label>
                    <input
                        type="checkbox"
                        name="isPublished"
                        checked={course.isPublished}
                        onChange={handleChange}
                    />
                    {" "}Published
                </label>

                <button type="submit">Update Course</button>
            </form>
        </div>
    );
};

export default UpdateCourse;
