import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageCategories = () => {
    const [categories, setCategories] = useState([]);
    const [name, setName] = useState("");
    const [slug, setSlug] = useState("");
    const [parentCategory, setParentCategory] = useState("");
    const [level, setLevel] = useState(1);
    const [editingId, setEditingId] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [expandedCategories, setExpandedCategories] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    // Auto-generate slug when name changes
    useEffect(() => {
        if (name && !editingId) {
            const generatedSlug = name.toLowerCase()
                .replace(/\s+/g, "-")
                .replace(/[^a-z0-9-]/g, "");
            setSlug(generatedSlug);
        }
    }, [name, editingId]);

    // Fetch categories from backend
    const fetchCategories = useCallback(async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem("adminToken");
            if (!token) {
                setError("No admin token found. Please login again.");
                return;
            }

            const res = await axios.get("http://localhost:5000/api/admin/categories", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setCategories(res.data);
            setError("");
        } catch (err) {
            console.error("Error fetching categories", err);
            setError(err.response?.data?.message || "Failed to fetch categories");
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchCategories();
    }, [fetchCategories]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        const token = localStorage.getItem("adminToken");
        if (!token) {
            setError("No admin token found. Please login again.");
            setLoading(false);
            return;
        }

        const categoryPayload = {
            name: name.trim(),
            slug: slug.trim() || name.toLowerCase().replace(/\s+/g, "-"),
            parentCategory: parentCategory || null,
            level: level,
        };

        // Validation
        if (!categoryPayload.name) {
            setError("Category name is required");
            setLoading(false);
            return;
        }

        if (level > 1 && !parentCategory) {
            setError("Parent category is required for subcategories");
            setLoading(false);
            return;
        }

        try {
            if (editingId) {
                const res = await axios.put(
                    `http://localhost:5000/api/admin/category/${editingId}`,
                    categoryPayload,
                    {
                        headers: { Authorization: `Bearer ${token}` }
                    }
                );

                setCategories(prev => prev.map(cat =>
                    cat._id === editingId ? res.data.category : cat
                ));
                setError("");
                alert("Category updated successfully!");
            } else {
                const res = await axios.post(
                    "http://localhost:5000/api/admin/add-category",
                    categoryPayload,
                    {
                        headers: { Authorization: `Bearer ${token}` },
                    }
                );

                setCategories(prev => [...prev, res.data.category]);
                setError("");
                alert("Category added successfully!");
            }

            resetForm();
            setShowModal(false);
        } catch (err) {
            console.error("Error submitting category:", err);
            const errorMessage = err.response?.data?.message || "Failed to submit category";
            setError(errorMessage);
            alert(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    const resetForm = () => {
        setName("");
        setSlug("");
        setParentCategory("");
        setLevel(1);
        setEditingId(null);
        setError("");
    };

    const handleEdit = (category) => {
        setName(category.name);
        setSlug(category.slug);
        setParentCategory(category.parentCategory || "");
        setLevel(category.level || 1);
        setEditingId(category._id);
        setShowModal(true);
        setError("");
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Are you sure you want to delete this category? This will also delete all its subcategories.")) return;

        setLoading(true);
        try {
            const token = localStorage.getItem("adminToken");
            await axios.delete(`http://localhost:5000/api/admin/category/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setCategories(prev => {
                const removeCategory = (cats, targetId) => {
                    return cats.filter(cat => {
                        if (cat._id === targetId) return false;
                        if (cat.parentCategory === targetId) return false;
                        return true;
                    });
                };
                return removeCategory(prev, id);
            });

            alert("Category deleted successfully!");
        } catch (err) {
            console.error("Delete error:", err);
            const errorMessage = err.response?.data?.message || "Failed to delete category";
            alert(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    // Organize categories into hierarchical structure
    const organizeCategories = (cats) => {
        const categoryMap = {};
        const rootCategories = [];

        cats.forEach(cat => {
            categoryMap[cat._id] = { ...cat, children: [] };
        });

        cats.forEach(cat => {
            if (cat.parentCategory && categoryMap[cat.parentCategory]) {
                categoryMap[cat.parentCategory].children.push(categoryMap[cat._id]);
            } else {
                rootCategories.push(categoryMap[cat._id]);
            }
        });

        return rootCategories;
    };

    const toggleExpand = (categoryId) => {
        setExpandedCategories(prev => ({
            ...prev,
            [categoryId]: !prev[categoryId]
        }));
    };

    // Enhanced category tree rendering with better visual hierarchy
    const renderCategoryTree = (cats, depth = 0) => {
        return cats.map((category, index) => (
            <React.Fragment key={category._id}>
                {/* Main Category Row */}
                <tr className={depth === 0 ? 'table-primary' : depth === 1 ? 'table-light' : ''}>
                    <td className="fw-bold">{depth === 0 ? index + 1 : ''}</td>
                    <td style={{ paddingLeft: `${depth * 25}px` }}>
                        <div className="d-flex align-items-center">
                            {category.children.length > 0 && (
                                <button
                                    className="btn btn-sm btn-link p-0 me-2 text-primary"
                                    onClick={() => toggleExpand(category._id)}
                                    disabled={loading}
                                >
                                    <i className={`fas ${expandedCategories[category._id] ? 'fa-chevron-down' : 'fa-chevron-right'}`}></i>
                                </button>
                            )}

                            {/* Visual hierarchy indicators */}
                            {depth === 0 && <i className="fas fa-folder me-2 text-primary"></i>}
                            {depth === 1 && <i className="fas fa-folder-open me-2 text-success"></i>}
                            {depth === 2 && <i className="fas fa-file me-2 text-warning"></i>}

                            <span className={`badge me-2 ${category.level === 1 ? 'bg-primary' :
                                    category.level === 2 ? 'bg-success' : 'bg-warning'
                                }`}>
                                {category.level === 1 ? 'MAIN' : category.level === 2 ? 'SUB' : 'SUB-SUB'}
                            </span>

                            <span className={depth === 0 ? 'fw-bold fs-6' : depth === 1 ? 'fw-medium' : 'text-muted'}>
                                {category.name}
                            </span>

                            {/* Show children count for main categories */}
                            {depth === 0 && category.children.length > 0 && (
                                <span className="ms-2 badge bg-secondary rounded-pill">
                                    {category.children.length} sub{category.children.length > 1 ? 's' : ''}
                                </span>
                            )}
                        </div>
                    </td>
                    <td>
                        <code className={depth === 0 ? 'fw-bold' : ''}>{category.slug}</code>
                    </td>
                    <td>
                        <div className="btn-group" role="group">
                            <button
                                className="btn btn-sm btn-outline-success"
                                onClick={() => {
                                    setParentCategory(category._id);
                                    setLevel(Math.min(category.level + 1, 3));
                                    setShowModal(true);
                                    resetForm();
                                    setParentCategory(category._id);
                                    setLevel(Math.min(category.level + 1, 3));
                                }}
                                disabled={loading || category.level >= 3}
                                title={category.level >= 3 ? "Maximum depth reached" : "Add subcategory"}
                            >
                                <i className="fas fa-plus me-1"></i>
                                Add Sub
                            </button>
                            <button
                                className="btn btn-sm btn-outline-primary"
                                onClick={() => handleEdit(category)}
                                disabled={loading}
                            >
                                <i className="fas fa-edit me-1"></i>
                                Edit
                            </button>
                            <button
                                className="btn btn-sm btn-outline-danger"
                                onClick={() => handleDelete(category._id)}
                                disabled={loading}
                            >
                                <i className="fas fa-trash me-1"></i>
                                Delete
                            </button>
                        </div>
                    </td>
                </tr>

                {/* Render children with visual separation */}
                {expandedCategories[category._id] && category.children.length > 0 && (
                    <>
                        {/* Subcategory header for main categories */}
                        {depth === 0 && (
                            <tr className="table-secondary">
                                <td></td>
                                <td colSpan="3" className="text-muted small fw-bold" style={{ paddingLeft: '45px' }}>
                                    <i className="fas fa-arrow-right me-2"></i>
                                    Subcategories of "{category.name}":
                                </td>
                            </tr>
                        )}
                        {renderCategoryTree(category.children, depth + 1)}

                        {/* Visual separator after main category's children */}
                        {depth === 0 && index < cats.length - 1 && (
                            <tr>
                                <td colSpan="4" className="border-0 p-1"></td>
                            </tr>
                        )}
                    </>
                )}
            </React.Fragment>
        ));
    };

    // Get available parent categories based on selected level
    const getAvailableParents = () => {
        if (level === 1) return [];
        if (level === 2) return categories.filter(cat => cat.level === 1);
        if (level === 3) return categories.filter(cat => cat.level === 2);
        return [];
    };

    const organizedCategories = organizeCategories(categories);

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <div>
                    <h2 className="mb-1">Manage Categories</h2>
                    <p className="text-muted mb-0">Organize your content with hierarchical categories</p>
                </div>
                <button
                    className="btn btn-primary"
                    onClick={() => {
                        resetForm();
                        setShowModal(true);
                    }}
                    disabled={loading}
                >
                    <i className="fas fa-plus me-2"></i>
                    Add New Category
                </button>
            </div>

            {/* Error Alert */}
            {error && (
                <div className="alert alert-danger alert-dismissible fade show" role="alert">
                    <i className="fas fa-exclamation-triangle me-2"></i>
                    {error}
                    <button
                        type="button"
                        className="btn-close"
                        onClick={() => setError("")}
                        aria-label="Close"
                    ></button>
                </div>
            )}

            {/* Loading Spinner */}
            {loading && (
                <div className="text-center mb-3">
                    <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            )}

            {/* Category Statistics */}
            <div className="row mb-4">
                <div className="col-md-4">
                    <div className="card text-center border-primary">
                        <div className="card-body">
                            <h3 className="text-primary">{categories.filter(cat => cat.level === 1).length}</h3>
                            <p className="mb-0 text-muted">Main Categories</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card text-center border-success">
                        <div className="card-body">
                            <h3 className="text-success">{categories.filter(cat => cat.level === 2).length}</h3>
                            <p className="mb-0 text-muted">Subcategories</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card text-center border-warning">
                        <div className="card-body">
                            <h3 className="text-warning">{categories.filter(cat => cat.level === 3).length}</h3>
                            <p className="mb-0 text-muted">Sub-subcategories</p>
                        </div>
                    </div>
                </div>
            </div>

            {/* Category Tree Table */}
            <div className="card shadow-sm">
                <div className="card-header bg-light d-flex justify-content-between align-items-center">
                    <h5 className="mb-0">
                        <i className="fas fa-sitemap me-2 text-primary"></i>
                        Category Hierarchy
                    </h5>
                    <div className="d-flex gap-2">
                        <button
                            className="btn btn-sm btn-outline-primary"
                            onClick={() => {
                                const allIds = categories.map(cat => cat._id);
                                const newExpanded = {};
                                allIds.forEach(id => newExpanded[id] = true);
                                setExpandedCategories(newExpanded);
                            }}
                            disabled={loading}
                        >
                            <i className="fas fa-expand-alt me-1"></i>
                            Expand All
                        </button>
                        <button
                            className="btn btn-sm btn-outline-secondary"
                            onClick={() => setExpandedCategories({})}
                            disabled={loading}
                        >
                            <i className="fas fa-compress-alt me-1"></i>
                            Collapse All
                        </button>
                        <button
                            className="btn btn-sm btn-outline-info"
                            onClick={fetchCategories}
                            disabled={loading}
                        >
                            <i className="fas fa-sync-alt me-1"></i>
                            Refresh
                        </button>
                    </div>
                </div>
                <div className="card-body p-0">
                    <div className="table-responsive">
                        <table className="table table-hover mb-0">
                            <thead className="table-dark">
                                <tr>
                                    <th width="60">#</th>
                                    <th>Category Name & Hierarchy</th>
                                    <th width="200">Slug</th>
                                    <th width="250">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {organizedCategories.length > 0 ? (
                                    renderCategoryTree(organizedCategories)
                                ) : (
                                    <tr>
                                        <td colSpan="4" className="text-center text-muted py-5">
                                            <i className="fas fa-folder-open fa-3x mb-3 d-block text-muted"></i>
                                            <h5 className="text-muted">No categories found</h5>
                                            <p className="mb-0">Start by adding your first main category!</p>
                                        </td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            {/* Modal - Same as before */}
            {showModal && (
                <div className="modal" style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}>
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">
                                    <i className={`fas ${editingId ? 'fa-edit' : 'fa-plus'} me-2`}></i>
                                    {editingId ? "Edit Category" : "Add New Category"}
                                </h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    onClick={() => setShowModal(false)}
                                    disabled={loading}
                                ></button>
                            </div>
                            <div className="modal-body">
                                {error && (
                                    <div className="alert alert-danger">
                                        <i className="fas fa-exclamation-triangle me-2"></i>
                                        {error}
                                    </div>
                                )}
                                <form onSubmit={handleSubmit}>
                                    <div className="row">
                                        <div className="col-md-6 mb-3">
                                            <label className="form-label">
                                                <i className="fas fa-layer-group me-2"></i>
                                                Category Level
                                            </label>
                                            <select
                                                className="form-select"
                                                value={level}
                                                onChange={(e) => {
                                                    setLevel(parseInt(e.target.value));
                                                    setParentCategory("");
                                                }}
                                                disabled={editingId || loading}
                                            >
                                                <option value={1}>Main Category</option>
                                                <option value={2}>Subcategory</option>
                                                <option value={3}>Sub-subcategory</option>
                                            </select>
                                        </div>

                                        {level > 1 && (
                                            <div className="col-md-6 mb-3">
                                                <label className="form-label">
                                                    <i className="fas fa-sitemap me-2"></i>
                                                    Parent Category
                                                </label>
                                                <select
                                                    className="form-select"
                                                    value={parentCategory}
                                                    onChange={(e) => setParentCategory(e.target.value)}
                                                    required
                                                    disabled={loading}
                                                >
                                                    <option value="">Select Parent Category</option>
                                                    {getAvailableParents().map((cat) => (
                                                        <option key={cat._id} value={cat._id}>
                                                            {cat.name}
                                                        </option>
                                                    ))}
                                                </select>
                                            </div>
                                        )}
                                    </div>

                                    <div className="mb-3">
                                        <label className="form-label">
                                            <i className="fas fa-tag me-2"></i>
                                            Category Name
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={name}
                                            onChange={(e) => setName(e.target.value)}
                                            placeholder="Enter category name"
                                            required
                                            disabled={loading}
                                        />
                                    </div>

                                    <div className="mb-3">
                                        <label className="form-label">
                                            <i className="fas fa-link me-2"></i>
                                            Slug
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={slug}
                                            onChange={(e) => setSlug(e.target.value)}
                                            placeholder="Auto-generated from name"
                                            disabled={loading}
                                        />
                                        <div className="form-text">
                                            Preview: <code>{slug || "auto-generated"}</code>
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={() => setShowModal(false)}
                                    disabled={loading}
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    onClick={handleSubmit}
                                    disabled={loading}
                                >
                                    {loading ? (
                                        <>
                                            <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                                            {editingId ? "Updating..." : "Saving..."}
                                        </>
                                    ) : (
                                        <>
                                            <i className={`fas ${editingId ? 'fa-save' : 'fa-plus'} me-2`}></i>
                                            {editingId ? "Update" : "Save"}
                                        </>
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Enhanced Legend */}
            <div className="mt-4">
                <div className="card border-info">
                    <div className="card-body">
                        <h6 className="card-title">
                            <i className="fas fa-info-circle me-2 text-info"></i>
                            Category System Guide:
                        </h6>
                        <div className="row">
                            <div className="col-md-6">
                                <h6 className="text-muted">Category Levels:</h6>
                                <div className="d-flex flex-column gap-2">
                                    <div>
                                        <span className="badge bg-primary me-2">MAIN</span>
                                        <span>Top level categories (e.g., Electronics, Clothing)</span>
                                    </div>
                                    <div>
                                        <span className="badge bg-success me-2">SUB</span>
                                        <span>Subcategories (e.g., Mobile Phones, Shirts)</span>
                                    </div>
                                    <div>
                                        <span className="badge bg-warning me-2">SUB-SUB</span>
                                        <span>Sub-subcategories (e.g., iPhone, Cotton Shirts)</span>
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-6">
                                <h6 className="text-muted">Visual Indicators:</h6>
                                <div className="d-flex flex-column gap-2">
                                    <div>
                                        <i className="fas fa-folder text-primary me-2"></i>
                                        <span>Main Category</span>
                                    </div>
                                    <div>
                                        <i className="fas fa-folder-open text-success me-2"></i>
                                        <span>Subcategory</span>
                                    </div>
                                    <div>
                                        <i className="fas fa-file text-warning me-2"></i>
                                        <span>Sub-subcategory</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ManageCategories;
