import { useState } from "react";
import AddCourse from "./AddCourse";
import ManageCourses from "./ManageCourses";

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("dashboard"); // changed default tab

  return (
    <div style={{ display: "flex" }}>
      {/* Sidebar */}
      <div style={{ width: "200px", borderRight: "1px solid #ccc", padding: "20px" }}>
        <h3>Admin Panel</h3>
        <ul style={{ listStyle: "none", padding: 0 }}>
          <li
            onClick={() => setActiveTab("dashboard")}
            style={{ cursor: "pointer", margin: "10px 0" }}
          >
            üè† Dashboard
          </li>
          <li
            onClick={() => setActiveTab("add")}
            style={{ cursor: "pointer", margin: "10px 0" }}
          >
            ‚ûï Add Course
          </li>
          <li
            onClick={() => setActiveTab("manage")}
            style={{ cursor: "pointer", margin: "10px 0" }}
          >
            üìã Manage Courses
          </li>
        </ul>
      </div>

      {/* Main Area */}
      <div style={{ flex: 1, padding: "20px" }}>
        {activeTab === "dashboard" && (
          <div>
            <h2>Welcome to Admin Dashboard</h2>
            <p>Here you can see quick stats and overview.</p>
            {/* Add your stats or dashboard widgets here */}
          </div>
        )}
        {activeTab === "add" && <AddCourse />}
        {activeTab === "manage" && <ManageCourses />}
      </div>
    </div>
  );
};

export default AdminDashboard;
