import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import CourseData from "../data/CourseData";
import CourseCard from "../components/CourseCard";

const DUMMY_INSTRUCTORS = [
  {
    id: 1,
    name: "Jane Smith",
    email: "jane.smith@email.com",
    phone: "+1-555-0123",
    location: "San Francisco, CA",
    bio: "Expert in web development with 10+ years of experience. Passionate about teaching modern JavaScript frameworks and helping students build real-world applications.",
    expertise: ["Web Development", "JavaScript", "React"],
    dateJoined: "2023-01-15",
    profileImage: null,
  },
  {
    id: 2,
    name: "Prof. Michael Chen",
    email: "michael.chen@email.com",
    phone: "+1-555-0124",
    location: "New York, NY",
    bio: "Data Science and Machine Learning specialist with extensive experience in Python programming and AI applications.",
    expertise: ["Data Science", "Python", "Machine Learning", "AI"],
    dateJoined: "2022-08-20",
    profileImage: null,
  },
];

const Instructors = () => {
  const { name } = useParams();
  const [instructor, setInstructor] = useState(null);
  const [courses, setCourses] = useState([]);
  const [filter, setFilter] = useState("all");

  useEffect(() => {
    const found = DUMMY_INSTRUCTORS.find(
      (inst) => inst.name.toLowerCase() === name.toLowerCase()
    );
    setInstructor(found);

    if (found) {
      const instructorCourses = CourseData.filter(
        (c) => c.instructor.toLowerCase() === found.name.toLowerCase()
      );
      setCourses(instructorCourses);
    }
  }, [name]);

  if (!instructor) {
    return (
      <div className="container my-5 text-center">
        <h2 className="text-danger">Instructor not found</h2>
      </div>
    );
  }

  const filteredCourses = courses.filter((course) => {
    if (filter === "free") return course.isFree;
    if (filter === "paid") return !course.isFree;
    return true;
  });

  return (
    <div className="container my-5">
      {/* Hero section */}
      <div className="card shadow rounded-4 overflow-hidden mb-5 p-4 d-flex flex-column flex-md-row align-items-center gap-4">
        {/* profile image */}
        <div
          className="d-flex justify-content-center align-items-center bg-primary-subtle p-4"
          style={{ borderRadius: "20px", flexShrink: 0 }}
        >
          {instructor.profileImage ? (
            <img
              src={instructor.profileImage}
              alt={instructor.name}
              className="rounded-circle shadow"
              style={{ width: "120px", height: "120px", objectFit: "cover" }}
            />
          ) : (
            <div
              className="bg-primary rounded-circle d-flex align-items-center justify-content-center text-white shadow"
              style={{ width: "120px", height: "120px", fontSize: "32px" }}
            >
              {instructor.name
                .split(" ")
                .map((n) => n[0])
                .join("")}
            </div>
          )}
        </div>

        {/* info block */}
        <div className="flex-grow-1 text-center text-md-start">
          <h2 className="fw-bold mb-2">{instructor.name}</h2>
          <p className="text-muted mb-3">{instructor.bio}</p>
          <div className="d-flex flex-wrap justify-content-center justify-content-md-start gap-3 mb-2">
            <span className="text-muted">
              <i className="bi bi-envelope me-1"></i>
              {instructor.email}
            </span>
            <span className="text-muted">
              <i className="bi bi-telephone me-1"></i>
              {instructor.phone}
            </span>
            <span className="text-muted">
              <i className="bi bi-geo-alt me-1"></i>
              {instructor.location}
            </span>
          </div>
          <p className="text-muted mb-1">
            <i className="bi bi-calendar me-1"></i>Joined on{" "}
            {new Date(instructor.dateJoined).toLocaleDateString()}
          </p>
          <p className="text-muted mb-0">
            <i className="bi bi-book me-1"></i>
            {courses.length} course{courses.length !== 1 && "s"} published
          </p>
          <div className="d-flex flex-wrap justify-content-center justify-content-md-start gap-2 mt-3">
            {instructor.expertise.map((skill, idx) => (
              <span
                key={idx}
                className="badge rounded-pill text-bg-primary"
                style={{ fontSize: "0.85rem" }}
              >
                {skill}
              </span>
            ))}
          </div>
        </div>
      </div>

      {/* Filter buttons */}
      <div className="mb-4 d-flex justify-content-center gap-2">
        <button
          className={`btn btn-outline-primary ${
            filter === "all" ? "active" : ""
          }`}
          onClick={() => setFilter("all")}
        >
          All Courses
        </button>
        <button
          className={`btn btn-outline-success ${
            filter === "free" ? "active" : ""
          }`}
          onClick={() => setFilter("free")}
        >
          Free
        </button>
        <button
          className={`btn btn-outline-warning ${
            filter === "paid" ? "active" : ""
          }`}
          onClick={() => setFilter("paid")}
        >
          Paid
        </button>
      </div>

      {/* Courses list */}
      <div className="row">
        {filteredCourses.length === 0 ? (
          <div className="text-center text-muted py-5">
            <i className="bi bi-collection display-4 mb-2"></i>
            <p>No courses found for this filter.</p>
          </div>
        ) : (
          filteredCourses.map((course) => (
            <div className="col-6 col-md-4 col-lg-3 mb-4" key={course.id}>
              <CourseCard course={course} />
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Instructors;
