import React, { useState, useEffect } from "react";

export default function AddCourse() {
  const [categories, setCategories] = useState([]);
  const [courseData, setCourseData] = useState({
    title: "",
    subtitle: "",
    description: "",
    category: "",
    level: "",
    language: "",
    price: "",
    duration: "",
    maxStudents: "",
    prerequisites: "",
    learningOutcomes: [""],
    tags: [""],
    thumbnail: null,
    promoVideo: null,
  });

  // Fetch categories from localStorage when component mounts
  useEffect(() => {
    const savedCategories = localStorage.getItem("categories");
    if (savedCategories) {
      // Only active categories to show in dropdown
      const activeCategories = JSON.parse(savedCategories).filter(
        (cat) => cat.isActive
      );
      setCategories(activeCategories);
    }
  }, []);
  const [activeTab, setActiveTab] = useState("basic");
  const [dragActive, setDragActive] = useState(false);

  const levels = ["Beginner", "Intermediate", "Advanced", "Professional"];
  const languages = [
    "English",
    "Hindi",
    "Spanish",
    "French",
    "German",
    "Chinese",
    "Japanese",
  ];

  const handleInputChange = (field, value) => {
    setCourseData((prev) => ({ ...prev, [field]: value }));
  };

  const handleArrayChange = (field, index, value) => {
    setCourseData((prev) => ({
      ...prev,
      [field]: prev[field].map((item, i) => (i === index ? value : item)),
    }));
  };

  const addArrayItem = (field) => {
    setCourseData((prev) => ({
      ...prev,
      [field]: [...prev[field], ""],
    }));
  };

  const removeArrayItem = (field, index) => {
    setCourseData((prev) => ({
      ...prev,
      [field]: prev[field].filter((_, i) => i !== index),
    }));
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e, fileType) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const files = e.dataTransfer.files;
    if (files && files[0]) {
      handleFileUpload(files[0], fileType);
    }
  };

  const handleFileUpload = (file, fileType) => {
    setCourseData((prev) => ({ ...prev, [fileType]: file }));
  };

  const handleSubmit = (action) => {
    console.log(`Course ${action}:`, courseData);
    // Here you would typically send the data to your backend
    alert(`Course ${action} successfully!`);
  };

  const TabButton = ({ id, label, icon }) => (
    <button
      onClick={() => setActiveTab(id)}
      className={`btn me-2 d-flex align-items-center ${
        activeTab === id ? "btn-primary" : "btn-outline-secondary"
      }`}
    >
      <span className="me-2">{icon}</span>
      <span>{label}</span>
    </button>
  );

  const FileUploadZone = ({ fileType, accept, label, description, file }) => (
    <div
      className={`border border-2 border-dashed rounded p-4 text-center ${
        dragActive ? "border-secondary bg-light" : "border-secondary"
      }`}
      style={{ minHeight: "200px" }}
      onDragEnter={handleDrag}
      onDragLeave={handleDrag}
      onDragOver={handleDrag}
      onDrop={(e) => handleDrop(e, fileType)}
    >
      {file ? (
        <div className="d-flex flex-column align-items-center">
          <div
            className="bg-success bg-opacity-25 rounded-circle p-3 mb-3"
            style={{ width: "80px", height: "80px" }}
          >
            <span style={{ fontSize: "2rem" }}>
              {fileType === "thumbnail" ? "ðŸ–¼ï¸" : "ðŸŽ¥"}
            </span>
          </div>
          <p className="fw-bold mb-2">{file.name}</p>
          <button
            onClick={() => handleFileUpload(null, fileType)}
            className="btn btn-sm btn-outline-danger"
          >
            Remove
          </button>
        </div>
      ) : (
        <div className="d-flex flex-column align-items-center">
          <div
            className="bg-light rounded-circle p-3 mb-3"
            style={{ width: "80px", height: "80px" }}
          >
            <span style={{ fontSize: "2rem" }}>ðŸ“¤</span>
          </div>
          <div className="mb-3">
            <p className="fw-bold mb-1">{label}</p>
            <p className="text-muted small">{description}</p>
          </div>
          <input
            type="file"
            accept={accept}
            onChange={(e) =>
              e.target.files[0] && handleFileUpload(e.target.files[0], fileType)
            }
            className="d-none"
            id={`${fileType}-upload`}
          />
          <label htmlFor={`${fileType}-upload`} className="btn btn-primary">
            Choose File
          </label>
        </div>
      )}
    </div>
  );

  return (
    <div className="min-vh-100 bg-light">
      {/* Header */}
      <div className="bg-white border-bottom">
        <div className="container-fluid py-4">
          <div className="d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <div
                className="bg-primary rounded p-2 me-3"
                style={{ width: "48px", height: "48px" }}
              >
                <span className="text-white fs-4"></span>
              </div>
              <div>
                <h1 className="h2 mb-1">Add New Course</h1>
                <p className="text-muted mb-0">
                  Create and publish your course content
                </p>
              </div>
            </div>
            <div className="d-flex gap-2">
              <button
                onClick={() => handleSubmit("saved as draft")}
                className="btn btn-outline-secondary"
              >
                Save Draft
              </button>
              <button
                onClick={() => handleSubmit("published")}
                className="btn btn-primary"
              >
                Publish Course
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid py-4">
        {/* Navigation Tabs */}
        <div className="bg-white rounded shadow-sm p-3 mb-4">
          <div className="d-flex flex-wrap gap-2">
            <TabButton id="basic" label="Basic Info" icon="ðŸ“" />
            <TabButton id="media" label="Media" icon="ðŸ–¼ï¸" />
            <TabButton id="content" label="Content" icon="ðŸ“š" />
            <TabButton id="pricing" label="Pricing" icon="ðŸ’°" />
          </div>
        </div>

        <div className="row">
          {/* Main Content */}
          <div className="col-lg-8">
            {/* Basic Information Tab */}
            {activeTab === "basic" && (
              <div className="card shadow-sm">
                <div className="card-body">
                  <h2 className="card-title h4 mb-4">Basic Information</h2>

                  <div className="row g-3">
                    <div className="col-12">
                      <label className="form-label fw-bold">
                        Course Title *
                      </label>
                      <input
                        type="text"
                        value={courseData.title}
                        onChange={(e) =>
                          handleInputChange("title", e.target.value)
                        }
                        placeholder="Enter a compelling course title"
                        className="form-control"
                      />
                    </div>

                    <div className="col-12">
                      <label className="form-label fw-bold">
                        Course Subtitle
                      </label>
                      <input
                        type="text"
                        value={courseData.subtitle}
                        onChange={(e) =>
                          handleInputChange("subtitle", e.target.value)
                        }
                        placeholder="Brief description of what students will learn"
                        className="form-control"
                      />
                    </div>

                    <div className="col-12">
                      <label className="form-label fw-bold">
                        Course Description *
                      </label>
                      <textarea
                        value={courseData.description}
                        onChange={(e) =>
                          handleInputChange("description", e.target.value)
                        }
                        placeholder="Provide a detailed description of your course content, objectives, and target audience..."
                        rows={6}
                        className="form-control"
                        style={{ resize: "none" }}
                      />
                    </div>

                    <div className="col-md-4">
                      <label className="form-label fw-bold">Category *</label>
                      <select
                        id="category"
                        name="category"
                        className="form-select"
                        value={courseData.category} // string, e.g. "Web Development"
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Select Category</option>
                        {categories.map((cat) => (
                          <option key={cat.id} value={cat.name}>
                            {cat.name}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="col-md-4">
                      <label className="form-label fw-bold">Level *</label>
                      <select
                        value={courseData.level}
                        onChange={(e) =>
                          handleInputChange("level", e.target.value)
                        }
                        className="form-select"
                      >
                        <option value="">Select Level</option>
                        {levels.map((level) => (
                          <option key={level} value={level}>
                            {level}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="col-md-4">
                      <label className="form-label fw-bold">Language *</label>
                      <select
                        value={courseData.language}
                        onChange={(e) =>
                          handleInputChange("language", e.target.value)
                        }
                        className="form-select"
                      >
                        <option value="">Select Language</option>
                        {languages.map((lang) => (
                          <option key={lang} value={lang}>
                            {lang}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="col-12">
                      <label className="form-label fw-bold">
                        Prerequisites
                      </label>
                      <textarea
                        value={courseData.prerequisites}
                        onChange={(e) =>
                          handleInputChange("prerequisites", e.target.value)
                        }
                        placeholder="List any required knowledge or skills (optional)"
                        rows={3}
                        className="form-control"
                        style={{ resize: "none" }}
                      />
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Media Tab */}
            {activeTab === "media" && (
              <div className="card shadow-sm">
                <div className="card-body">
                  <h2 className="card-title h4 mb-4">Media Assets</h2>

                  <div className="row g-4">
                    <div className="col-12">
                      <h3 className="h5 mb-3">Course Thumbnail</h3>
                      <FileUploadZone
                        fileType="thumbnail"
                        accept="image/*"
                        label="Upload Course Thumbnail"
                        description="Recommended: 1280x720px, JPG or PNG"
                        file={courseData.thumbnail}
                      />
                    </div>

                    <div className="col-12">
                      <h3 className="h5 mb-3">Promotional Video</h3>
                      <FileUploadZone
                        fileType="promoVideo"
                        accept="video/*"
                        label="Upload Promo Video"
                        description="Optional: MP4 format, max 100MB"
                        file={courseData.promoVideo}
                      />
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Content Tab */}
            {activeTab === "content" && (
              <div className="card shadow-sm">
                <div className="card-body">
                  <h2 className="card-title h4 mb-4">Course Content</h2>

                  <div className="row g-4">
                    <div className="col-12">
                      <label className="form-label fw-bold">
                        Learning Outcomes
                      </label>
                      <p className="text-muted small mb-3">
                        What will students achieve after completing this course?
                      </p>
                      {courseData.learningOutcomes.map((outcome, index) => (
                        <div
                          key={index}
                          className="d-flex align-items-center mb-3"
                        >
                          <input
                            type="text"
                            value={outcome}
                            onChange={(e) =>
                              handleArrayChange(
                                "learningOutcomes",
                                index,
                                e.target.value
                              )
                            }
                            placeholder={`Learning outcome ${index + 1}`}
                            className="form-control me-2"
                          />
                          {courseData.learningOutcomes.length > 1 && (
                            <button
                              onClick={() =>
                                removeArrayItem("learningOutcomes", index)
                              }
                              className="btn btn-outline-danger btn-sm"
                            >
                              âŒ
                            </button>
                          )}
                        </div>
                      ))}
                      <button
                        onClick={() => addArrayItem("learningOutcomes")}
                        className="btn btn-outline-primary btn-sm"
                      >
                        âž• Add Learning Outcome
                      </button>
                    </div>

                    <div className="col-12">
                      <label className="form-label fw-bold">Course Tags</label>
                      <p className="text-muted small mb-3">
                        Add relevant tags to help students find your course
                      </p>
                      {courseData.tags.map((tag, index) => (
                        <div
                          key={index}
                          className="d-flex align-items-center mb-3"
                        >
                          <input
                            type="text"
                            value={tag}
                            onChange={(e) =>
                              handleArrayChange("tags", index, e.target.value)
                            }
                            placeholder={`Tag ${index + 1}`}
                            className="form-control me-2"
                          />
                          {courseData.tags.length > 1 && (
                            <button
                              onClick={() => removeArrayItem("tags", index)}
                              className="btn btn-outline-danger btn-sm"
                            >
                              âŒ
                            </button>
                          )}
                        </div>
                      ))}
                      <button
                        onClick={() => addArrayItem("tags")}
                        className="btn btn-outline-primary btn-sm"
                      >
                        âž• Add Tag
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Pricing Tab */}
            {activeTab === "pricing" && (
              <div className="card shadow-sm">
                <div className="card-body">
                  <h2 className="card-title h4 mb-4">Pricing & Details</h2>

                  <div className="row g-3">
                    <div className="col-md-6">
                      <label className="form-label fw-bold">
                        Course Price (â‚¹)
                      </label>
                      <input
                        type="number"
                        value={courseData.price}
                        onChange={(e) =>
                          handleInputChange("price", e.target.value)
                        }
                        placeholder="0.00"
                        min="0"
                        step="0.01"
                        className="form-control"
                      />
                      <div className="form-text">Set to 0 for free courses</div>
                    </div>

                    <div className="col-md-6">
                      <label className="form-label fw-bold">
                        Course Duration
                      </label>
                      <input
                        type="text"
                        value={courseData.duration}
                        onChange={(e) =>
                          handleInputChange("duration", e.target.value)
                        }
                        placeholder="e.g., 4 weeks, 20 hours"
                        className="form-control"
                      />
                    </div>

                    <div className="col-md-6">
                      <label className="form-label fw-bold">
                        Maximum Students
                      </label>
                      <input
                        type="number"
                        value={courseData.maxStudents}
                        onChange={(e) =>
                          handleInputChange("maxStudents", e.target.value)
                        }
                        placeholder="No limit"
                        min="1"
                        className="form-control"
                      />
                      <div className="form-text">
                        Leave empty for unlimited enrollment
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Sidebar */}
          <div className="col-lg-4">
            <div className="row g-3">
              {/* Course Preview */}
              <div className="col-12">
                <div className="card shadow-sm">
                  <div className="card-body">
                    <h3 className="card-title h5 mb-3">Course Preview</h3>
                    <div className="mb-3">
                      <div
                        className="bg-light rounded d-flex align-items-center justify-content-center"
                        style={{ height: "180px" }}
                      >
                        {courseData.thumbnail ? (
                          <div className="text-center">
                            <span style={{ fontSize: "3rem" }}>ðŸ–¼ï¸</span>
                            <p className="text-muted small mb-0 mt-2">
                              Thumbnail uploaded
                            </p>
                          </div>
                        ) : (
                          <div className="text-center">
                            <span style={{ fontSize: "3rem" }}>ðŸ“·</span>
                            <p className="text-muted small mb-0 mt-2">
                              No thumbnail
                            </p>
                          </div>
                        )}
                      </div>
                    </div>

                    <div className="mb-3">
                      <h4 className="h6 mb-1 text-truncate">
                        {courseData.title || "Course Title"}
                      </h4>
                      <p className="text-muted small mb-0 text-truncate">
                        {courseData.subtitle ||
                          "Course subtitle will appear here"}
                      </p>
                    </div>

                    <div className="d-flex justify-content-between align-items-center">
                      <span className="text-muted small">
                        {courseData.level || "Level"}
                      </span>
                      <span className="fw-bold text-primary">
                        {courseData.price ? `â‚¹${courseData.price}` : "Free"}
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              {/* Quick Stats */}
              <div className="col-12">
                <div className="card shadow-sm">
                  <div className="card-body">
                    <h3 className="card-title h5 mb-3">Quick Stats</h3>
                    <div className="row g-2">
                      <div className="col-12">
                        <div className="d-flex justify-content-between align-items-center">
                          <div className="d-flex align-items-center">
                            <span className="me-2">â°</span>
                            <span className="small text-muted">Duration</span>
                          </div>
                          <span className="small fw-bold">
                            {courseData.duration || "Not set"}
                          </span>
                        </div>
                      </div>

                      <div className="col-12">
                        <div className="d-flex justify-content-between align-items-center">
                          <div className="d-flex align-items-center">
                            <span className="me-2">ðŸ‘¥</span>
                            <span className="small text-muted">
                              Max Students
                            </span>
                          </div>
                          <span className="small fw-bold">
                            {courseData.maxStudents || "Unlimited"}
                          </span>
                        </div>
                      </div>

                      <div className="col-12">
                        <div className="d-flex justify-content-between align-items-center">
                          <div className="d-flex align-items-center">
                            <span className="me-2">ðŸ“š</span>
                            <span className="small text-muted">Category</span>
                          </div>
                          <span className="small fw-bold text-truncate ms-2">
                            {courseData.category || "None"}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Tips */}
              <div className="col-12">
                <div className="card bg-primary bg-opacity-10 border-primary">
                  <div className="card-body">
                    <h3 className="card-title h5 text-primary mb-3">
                      ðŸ’¡ Tips for Success
                    </h3>
                    <ul className="list-unstyled small text-primary mb-0">
                      <li className="mb-2">
                        â€¢ Use a compelling title that clearly describes what
                        students will learn
                      </li>
                      <li className="mb-2">
                        â€¢ Add a high-quality thumbnail to attract more students
                      </li>
                      <li className="mb-2">
                        â€¢ Write detailed learning outcomes to set clear
                        expectations
                      </li>
                      <li className="mb-0">
                        â€¢ Choose relevant tags to improve discoverability
                      </li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
