import React, { useState, useEffect, useMemo } from "react";
import { Search, Plus, Edit2, Trash2, ChevronDown, ChevronRight, Filter, RotateCcw, AlertTriangle } from "lucide-react";

const ManageCategories = () => {
  // Initial sample data
  const [categories, setCategories] = useState([
    { id: 1, name: "Web Development", isActive: true, parentId: null, level: 0, createdAt: new Date('2024-01-01') },
    { id: 2, name: "Frontend", isActive: true, parentId: 1, level: 1, createdAt: new Date('2024-01-02') },
    { id: 3, name: "React", isActive: true, parentId: 2, level: 2, createdAt: new Date('2024-01-03') },
    { id: 4, name: "Vue.js", isActive: true, parentId: 2, level: 2, createdAt: new Date('2024-01-04') },
    { id: 5, name: "Backend", isActive: true, parentId: 1, level: 1, createdAt: new Date('2024-01-05') },
    { id: 6, name: "Node.js", isActive: true, parentId: 5, level: 2, createdAt: new Date('2024-01-06') },
    { id: 7, name: "Mobile Development", isActive: true, parentId: null, level: 0, createdAt: new Date('2024-01-07') },
    { id: 8, name: "iOS", isActive: true, parentId: 7, level: 1, createdAt: new Date('2024-01-08') },
    { id: 9, name: "Swift", isActive: true, parentId: 8, level: 2, createdAt: new Date('2024-01-09') },
  ]);

  // Form states
  const [formData, setFormData] = useState({
    name: "",
    isActive: true,
    categoryType: "main",
    selectedMainCategory: null,
    selectedSubCategory: null,
  });

  // UI states
  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [expandedCategories, setExpandedCategories] = useState(new Set());
  const [deleteConfirm, setDeleteConfirm] = useState(null);

  // Filter and search states
  const [filters, setFilters] = useState({
    search: "",
    mainCategory: "",
    subCategory: "",
    status: "all", // all, active, inactive
  });

  const [sortConfig, setSortConfig] = useState({
    field: "name",
    direction: "asc"
  });

  // Memoized helper functions for better performance
  const categoryHelpers = useMemo(() => ({
    getByLevel: (level) => categories.filter(cat => cat.level === level),
    getChildren: (parentId) => categories.filter(cat => cat.parentId === parentId),
    getChildrenCount: (categoryId) => categories.filter(cat => cat.parentId === categoryId).length,
    getCategoryPath: (category) => {
      const path = [];
      let current = category;
      while (current) {
        path.unshift(current.name);
        current = categories.find(cat => cat.id === current.parentId);
      }
      return path.join(" → ");
    },
    hasChildren: (categoryId) => categories.some(cat => cat.parentId === categoryId)
  }), [categories]);

  // Filtered and sorted categories
  const filteredCategories = useMemo(() => {
    let filtered = categories.filter(cat => {
      // Status filter
      if (filters.status !== "all" && 
          ((filters.status === "active" && !cat.isActive) || 
           (filters.status === "inactive" && cat.isActive))) {
        return false;
      }

      // Search filter
      if (filters.search && !cat.name.toLowerCase().includes(filters.search.toLowerCase())) {
        return false;
      }

      // Category hierarchy filters
      if (filters.mainCategory && cat.level === 0) {
        return cat.id === parseInt(filters.mainCategory);
      }
      
      if (filters.subCategory && cat.level === 1) {
        return cat.id === parseInt(filters.subCategory);
      }

      return true;
    });

    // Apply sorting
    return filtered.sort((a, b) => {
      let aValue, bValue;
      
      switch (sortConfig.field) {
        case "name":
          aValue = a.name.toLowerCase();
          bValue = b.name.toLowerCase();
          return sortConfig.direction === "asc" 
            ? aValue.localeCompare(bValue) 
            : bValue.localeCompare(aValue);
        case "children":
          aValue = categoryHelpers.getChildrenCount(a.id);
          bValue = categoryHelpers.getChildrenCount(b.id);
          break;
        case "created":
          aValue = new Date(a.createdAt);
          bValue = new Date(b.createdAt);
          break;
        default:
          aValue = a.id;
          bValue = b.id;
      }
      
      if (sortConfig.direction === "asc") {
        return aValue < bValue ? -1 : aValue > bValue ? 1 : 0;
      } else {
        return aValue > bValue ? -1 : aValue < bValue ? 1 : 0;
      }
    });
  }, [categories, filters, sortConfig, categoryHelpers]);

  // Statistics
  const stats = useMemo(() => ({
    total: categories.length,
    main: categoryHelpers.getByLevel(0).length,
    sub: categoryHelpers.getByLevel(1).length,
    subSub: categoryHelpers.getByLevel(2).length,
    active: categories.filter(cat => cat.isActive).length,
    inactive: categories.filter(cat => !cat.isActive).length,
  }), [categories, categoryHelpers]);

  // Form handlers
  const handleFormChange = (field, value) => {
    setFormData(prev => {
      const updated = { ...prev, [field]: value };
      
      // Reset dependent fields when category type changes
      if (field === "categoryType") {
        updated.selectedMainCategory = null;
        updated.selectedSubCategory = null;
      }
      
      // Reset sub category when main category changes
      if (field === "selectedMainCategory") {
        updated.selectedSubCategory = null;
      }
      
      return updated;
    });
  };

  const resetForm = () => {
    setFormData({
      name: "",
      isActive: true,
      categoryType: "main",
      selectedMainCategory: null,
      selectedSubCategory: null,
    });
    setEditingId(null);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Validation
    if (!formData.name.trim()) {
      alert("Category name is required");
      return;
    }

    // Check for duplicate names at the same level
    const isDuplicate = categories.some(cat => 
      cat.name.toLowerCase() === formData.name.toLowerCase() &&
      cat.level === getCategoryLevel(formData.categoryType) &&
      cat.id !== editingId &&
      getParentId() === cat.parentId
    );

    if (isDuplicate) {
      alert("A category with this name already exists at this level");
      return;
    }

    const level = getCategoryLevel(formData.categoryType);
    const parentId = getParentId();

    if (editingId) {
      setCategories(prev => prev.map(cat =>
        cat.id === editingId
          ? { ...cat, name: formData.name.trim(), isActive: formData.isActive, parentId, level }
          : cat
      ));
    } else {
      const newCategory = {
        id: Math.max(...categories.map(c => c.id), 0) + 1,
        name: formData.name.trim(),
        isActive: formData.isActive,
        parentId,
        level,
        createdAt: new Date(),
      };
      setCategories(prev => [...prev, newCategory]);
    }

    resetForm();
    setShowModal(false);
  };

  const getCategoryLevel = (type) => {
    const levels = { main: 0, sub: 1, "sub-sub": 2 };
    return levels[type] || 0;
  };

  const getParentId = () => {
    if (formData.categoryType === "sub") return formData.selectedMainCategory;
    if (formData.categoryType === "sub-sub") return formData.selectedSubCategory;
    return null;
  };

  const handleEdit = (category) => {
    const typeMap = { 0: "main", 1: "sub", 2: "sub-sub" };
    
    setFormData({
      name: category.name,
      isActive: category.isActive,
      categoryType: typeMap[category.level],
      selectedMainCategory: category.level >= 1 ? 
        (category.level === 1 ? category.parentId : 
         categories.find(cat => cat.id === category.parentId)?.parentId) : null,
      selectedSubCategory: category.level === 2 ? category.parentId : null,
    });
    
    setEditingId(category.id);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    if (categoryHelpers.hasChildren(id)) {
      alert("Cannot delete category that has subcategories. Please delete subcategories first.");
      return;
    }
    setDeleteConfirm(id);
  };

  const confirmDelete = () => {
    if (deleteConfirm) {
      setCategories(prev => prev.filter(cat => cat.id !== deleteConfirm));
      setDeleteConfirm(null);
    }
  };

  const toggleExpanded = (categoryId) => {
    setExpandedCategories(prev => {
      const newSet = new Set(prev);
      if (newSet.has(categoryId)) {
        newSet.delete(categoryId);
      } else {
        newSet.add(categoryId);
      }
      return newSet;
    });
  };

  const clearFilters = () => {
    setFilters({
      search: "",
      mainCategory: "",
      subCategory: "",
      status: "all",
    });
  };

  const CategoryBadge = ({ level }) => {
    const badges = {
      0: { text: "Main", class: "bg-blue-100 text-blue-800" },
      1: { text: "Sub", class: "bg-green-100 text-green-800" },
      2: { text: "Sub-Sub", class: "bg-purple-100 text-purple-800" }
    };
    const badge = badges[level] || badges[0];
    return <span className={`px-2 py-1 text-xs font-medium rounded-full ${badge.class}`}>{badge.text}</span>;
  };

  const renderCategoryTree = () => {
    const mainCategories = filteredCategories.filter(cat => cat.level === 0);
    
    if (mainCategories.length === 0) {
      return (
        <div className="text-center py-12 text-gray-500">
          <Search className="mx-auto h-12 w-12 mb-4 opacity-50" />
          <p>No categories found matching your filters</p>
        </div>
      );
    }

    return mainCategories.map((mainCat, index) => (
      <CategoryItem key={mainCat.id} category={mainCat} index={index} />
    ));
  };

  const CategoryItem = ({ category, index, isChild = false }) => {
    const children = categoryHelpers.getChildren(category.id);
    const isExpanded = expandedCategories.has(category.id);
    const hasChildren = children.length > 0;

    return (
      <div className={`border rounded-lg mb-2 ${isChild ? 'ml-8 border-l-4 border-l-blue-200' : ''}`}>
        <div 
          className={`p-4 flex items-center justify-between cursor-pointer hover:bg-gray-50 transition-colors ${
            !category.isActive ? 'opacity-60' : ''
          }`}
          onClick={() => hasChildren && toggleExpanded(category.id)}
        >
          <div className="flex items-center space-x-3">
            {hasChildren ? (
              isExpanded ? <ChevronDown className="h-4 w-4 text-gray-400" /> : <ChevronRight className="h-4 w-4 text-gray-400" />
            ) : (
              <div className="w-4"></div>
            )}
            
            <CategoryBadge level={category.level} />
            
            <div>
              <div className="flex items-center space-x-2">
                <span className="font-medium">
                  {!isChild && `${index + 1}. `}{category.name}
                </span>
                {!category.isActive && (
                  <span className="px-2 py-1 text-xs bg-red-100 text-red-600 rounded-full">Inactive</span>
                )}
              </div>
              {hasChildren && (
                <p className="text-sm text-gray-500 mt-1">
                  {children.length} subcategor{children.length === 1 ? 'y' : 'ies'}
                </p>
              )}
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <button
              onClick={(e) => {
                e.stopPropagation();
                handleEdit(category);
              }}
              className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
            >
              <Edit2 className="h-4 w-4" />
            </button>
            <button
              onClick={(e) => {
                e.stopPropagation();
                handleDelete(category.id);
              }}
              className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
            >
              <Trash2 className="h-4 w-4" />
            </button>
          </div>
        </div>

        {isExpanded && hasChildren && (
          <div className="border-t bg-gray-50 p-2">
            {children.map(child => (
              <CategoryItem key={child.id} category={child} isChild={true} />
            ))}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="max-w-7xl mx-auto p-6 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Category Management</h1>
          <p className="text-gray-600 mt-1">Organize and manage your category hierarchy</p>
        </div>
        <button
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
          className="flex items-center space-x-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
        >
          <Plus className="h-4 w-4" />
          <span>Add Category</span>
        </button>
      </div>

      {/* Statistics Cards */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <div className="bg-white p-4 rounded-lg shadow-sm border">
          <div className="text-sm text-gray-600">Total Categories</div>
          <div className="text-2xl font-bold text-gray-900">{stats.total}</div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow-sm border">
          <div className="text-sm text-gray-600">Main Categories</div>
          <div className="text-2xl font-bold text-blue-600">{stats.main}</div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow-sm border">
          <div className="text-sm text-gray-600">Sub Categories</div>
          <div className="text-2xl font-bold text-green-600">{stats.sub}</div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow-sm border">
          <div className="text-sm text-gray-600">Active</div>
          <div className="text-2xl font-bold text-emerald-600">{stats.active}</div>
        </div>
      </div>

      {/* Filters */}
      <div className="bg-white p-6 rounded-lg shadow-sm border mb-6">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-4">
          <h3 className="text-lg font-medium text-gray-900">Filters & Search</h3>
          <div className="flex items-center space-x-2 mt-4 md:mt-0">
            <button
              onClick={clearFilters}
              className="flex items-center space-x-1 text-gray-600 hover:text-gray-900 px-3 py-1 rounded border"
            >
              <RotateCcw className="h-4 w-4" />
              <span>Clear</span>
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Search</label>
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <input
                type="text"
                value={filters.search}
                onChange={(e) => setFilters(prev => ({ ...prev, search: e.target.value }))}
                className="pl-10 w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Search categories..."
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Status</label>
            <select
              value={filters.status}
              onChange={(e) => setFilters(prev => ({ ...prev, status: e.target.value }))}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="all">All Status</option>
              <option value="active">Active Only</option>
              <option value="inactive">Inactive Only</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Sort By</label>
            <select
              value={sortConfig.field}
              onChange={(e) => setSortConfig(prev => ({ ...prev, field: e.target.value }))}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="name">Name</option>
              <option value="children">Children Count</option>
              <option value="created">Date Created</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Order</label>
            <select
              value={sortConfig.direction}
              onChange={(e) => setSortConfig(prev => ({ ...prev, direction: e.target.value }))}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="asc">Ascending</option>
              <option value="desc">Descending</option>
            </select>
          </div>
        </div>
      </div>

      {/* Categories List */}
      <div className="bg-white rounded-lg shadow-sm border">
        <div className="p-6 border-b">
          <h3 className="text-lg font-medium text-gray-900">Categories</h3>
          <p className="text-sm text-gray-500 mt-1">
            Showing {filteredCategories.filter(c => c.level === 0).length} main categories
          </p>
        </div>
        <div className="p-6">
          {renderCategoryTree()}
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-md w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6 border-b">
              <h3 className="text-lg font-medium text-gray-900">
                {editingId ? "Edit Category" : "Add New Category"}
              </h3>
            </div>

            <div className="p-6 space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Category Type</label>
                <select
                  value={formData.categoryType}
                  onChange={(e) => handleFormChange("categoryType", e.target.value)}
                  className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="main">Main Category</option>
                  <option value="sub">Sub Category</option>
                  <option value="sub-sub">Sub-Sub Category</option>
                </select>
              </div>

              {formData.categoryType !== "main" && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Main Category</label>
                  <select
                    value={formData.selectedMainCategory || ""}
                    onChange={(e) => handleFormChange("selectedMainCategory", e.target.value ? parseInt(e.target.value) : null)}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                  >
                    <option value="">Select Main Category</option>
                    {categoryHelpers.getByLevel(0).map(cat => (
                      <option key={cat.id} value={cat.id}>{cat.name}</option>
                    ))}
                  </select>
                </div>
              )}

              {formData.categoryType === "sub-sub" && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Sub Category</label>
                  <select
                    value={formData.selectedSubCategory || ""}
                    onChange={(e) => handleFormChange("selectedSubCategory", e.target.value ? parseInt(e.target.value) : null)}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    disabled={!formData.selectedMainCategory}
                    required
                  >
                    <option value="">Select Sub Category</option>
                    {formData.selectedMainCategory && 
                      categoryHelpers.getChildren(formData.selectedMainCategory).map(cat => (
                        <option key={cat.id} value={cat.id}>{cat.name}</option>
                      ))
                    }
                  </select>
                </div>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Category Name</label>
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) => handleFormChange("name", e.target.value)}
                  className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Enter category name"
                  required
                />
              </div>

              <div className="flex items-center">
                <input
                  type="checkbox"
                  id="isActive"
                  checked={formData.isActive}
                  onChange={(e) => handleFormChange("isActive", e.target.checked)}
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                />
                <label htmlFor="isActive" className="ml-2 text-sm text-gray-700">
                  Active
                </label>
              </div>

              {formData.name && (
                <div className="bg-blue-50 p-3 rounded-lg">
                  <p className="text-sm text-blue-800 font-medium">Preview:</p>
                  <p className="text-sm text-blue-700">
                    {formData.categoryType === "main" 
                      ? formData.name
                      : formData.categoryType === "sub" && formData.selectedMainCategory
                      ? `${categories.find(c => c.id === formData.selectedMainCategory)?.name} → ${formData.name}`
                      : formData.categoryType === "sub-sub" && formData.selectedMainCategory && formData.selectedSubCategory
                      ? `${categories.find(c => c.id === formData.selectedMainCategory)?.name} → ${categories.find(c => c.id === formData.selectedSubCategory)?.name} → ${formData.name}`
                      : "Please select parent categories"
                    }
                  </p>
                </div>
              )}

              <div className="flex justify-end space-x-3 pt-4">
                <button
                  type="button"
                  onClick={() => setShowModal(false)}
                  className="px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
                <button
                  type="button"
                  onClick={handleSubmit}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                >
                  {editingId ? "Update" : "Create"}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmation */}
      {deleteConfirm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-sm w-full">
            <div className="p-6">
              <div className="flex items-center space-x-3 mb-4">
                <AlertTriangle className="h-6 w-6 text-red-500" />
                <h3 className="text-lg font-medium text-gray-900">Delete Category</h3>
              </div>
              <p className="text-gray-600 mb-6">
                Are you sure you want to delete this category? This action cannot be undone.
              </p>
              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setDeleteConfirm(null)}
                  className="px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={confirmDelete}
                  className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageCategories;
