import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Configure axios base URL - change this to match your backend server
const API_BASE_URL = 'http://localhost:5000'; // Change port if your backend runs on different port
axios.defaults.baseURL = API_BASE_URL;

const ManageContacts = () => {
    const [contacts, setContacts] = useState([]);
    const [selectedContact, setSelectedContact] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [filter, setFilter] = useState('all');
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Get authentication token from localStorage
    const getAuthToken = () => {
        return localStorage.getItem('adminToken') || localStorage.getItem('token');
    };

    // Create axios instance with auth headers
    const createAuthenticatedRequest = () => {
        const token = getAuthToken();
        return {
            headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json'
            }
        };
    };

    // Fetch contacts from backend
    useEffect(() => {
        fetchContacts();
    }, []);

    const fetchContacts = async () => {
        try {
            setLoading(true);
            const token = getAuthToken();

            if (!token) {
                setError('No authentication token found. Please login again.');
                setLoading(false);
                return;
            }

            // Try different possible API endpoints
            let response;
            try {
                // First try the admin route
                response = await axios.get('/api/contact/admin/all', createAuthenticatedRequest());
            } catch (adminError) {
                console.log('Admin route failed, trying alternative routes...');
                try {
                    // Try alternative admin route
                    response = await axios.get('/api/contacts/admin/all', createAuthenticatedRequest());
                } catch (altError) {
                    try {
                        // Try simple contacts route
                        response = await axios.get('/api/contacts', createAuthenticatedRequest());
                    } catch (simpleError) {
                        try {
                            // Try contact route without admin
                            response = await axios.get('/api/contact', createAuthenticatedRequest());
                        } catch (finalError) {
                            throw adminError; // Throw original error
                        }
                    }
                }
            }

            // Handle different response structures
            let contactsData = [];
            if (response.data.contacts) {
                contactsData = response.data.contacts;
            } else if (response.data.data) {
                contactsData = response.data.data;
            } else if (Array.isArray(response.data)) {
                contactsData = response.data;
            } else {
                console.log('Unexpected response structure:', response.data);
                contactsData = [];
            }

            setContacts(contactsData);
            setError('');
            console.log('Contacts fetched successfully:', contactsData.length);
        } catch (error) {
            console.error('Error fetching contacts:', error);
            if (error.response?.status === 401) {
                setError('Authentication failed. Please login again.');
                // Clear all possible token keys
                localStorage.removeItem('adminToken');
                localStorage.removeItem('token');
            } else if (error.response?.status === 404) {
                setError('Contact API endpoint not found. Please check your backend server.');
            } else if (error.response?.status === 403) {
                setError('Access denied. You don\'t have permission to view contacts.');
            } else if (error.code === 'NETWORK_ERROR' || error.message.includes('Network Error')) {
                setError('Cannot connect to server. Please check if your backend is running on the correct port.');
            } else {
                setError(`Failed to fetch contacts: ${error.response?.data?.message || error.message}`);
            }
        } finally {
            setLoading(false);
        }
    };

    // Filter contacts based on status and search term
    const filteredContacts = contacts.filter(contact => {
        const matchesFilter = filter === 'all' || contact.status === filter;
        const matchesSearch = 
            (contact.name && contact.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
            (contact.email && contact.email.toLowerCase().includes(searchTerm.toLowerCase())) ||
            (contact.subject && contact.subject.toLowerCase().includes(searchTerm.toLowerCase()));
        return matchesFilter && matchesSearch;
    });

    const updateStatus = async (id, newStatus) => {
        try {
            // Try different possible API endpoints for updating status
            let response;
            try {
                response = await axios.put(`/api/contact/admin/status/${id}`,
                    { status: newStatus },
                    createAuthenticatedRequest()
                );
            } catch (adminError) {
                try {
                    response = await axios.put(`/api/contacts/admin/status/${id}`,
                        { status: newStatus },
                        createAuthenticatedRequest()
                    );
                } catch (altError) {
                    try {
                        response = await axios.put(`/api/contact/${id}/status`,
                            { status: newStatus },
                            createAuthenticatedRequest()
                        );
                    } catch (finalError) {
                        throw adminError;
                    }
                }
            }

            setContacts(contacts.map(contact =>
                contact._id === id ? { ...contact, status: newStatus } : contact
            ));

            // Update selected contact if it's the one being updated
            if (selectedContact && selectedContact._id === id) {
                setSelectedContact({ ...selectedContact, status: newStatus });
            }
        } catch (error) {
            console.error('Error updating status:', error);
            if (error.response?.status === 401) {
                alert('Authentication failed. Please login again.');
                localStorage.removeItem('adminToken');
                localStorage.removeItem('token');
            } else {
                alert(`Failed to update status: ${error.response?.data?.message || error.message}`);
            }
        }
    };

    const deleteContact = async (id) => {
        if (!window.confirm('Are you sure you want to delete this contact? This action cannot be undone.')) {
            return;
        }

        try {
            // Try different possible API endpoints for deleting
            try {
                await axios.delete(`/api/contact/admin/delete/${id}`, createAuthenticatedRequest());
            } catch (adminError) {
                try {
                    await axios.delete(`/api/contacts/admin/delete/${id}`, createAuthenticatedRequest());
                } catch (altError) {
                    try {
                        await axios.delete(`/api/contact/${id}`, createAuthenticatedRequest());
                    } catch (finalError) {
                        throw adminError;
                    }
                }
            }

            setContacts(contacts.filter(contact => contact._id !== id));
            setShowModal(false);
            setSelectedContact(null);
        } catch (error) {
            console.error('Error deleting contact:', error);
            if (error.response?.status === 401) {
                alert('Authentication failed. Please login again.');
                localStorage.removeItem('adminToken');
                localStorage.removeItem('token');
            } else {
                alert(`Failed to delete contact: ${error.response?.data?.message || error.message}`);
            }
        }
    };

    const handleContactClick = async (contact) => {
        setSelectedContact(contact);
        setShowModal(true);

        // Mark as read when opened (if it's unread)
        if (contact.status === 'unread') {
            await updateStatus(contact._id, 'read');
        }
    };

    const getStatusBadge = (status) => {
        const badges = {
            unread: 'bg-danger',
            read: 'bg-warning',
            resolved: 'bg-success',
            pending: 'bg-info' // Added support for pending status
        };
        return badges[status] || 'bg-secondary';
    };

    const getStatusCount = (status) => {
        return contacts.filter(contact => contact.status === status).length;
    };

    // Fixed formatDate function to handle consistent date formatting
    const formatDate = (dateString) => {
        try {
            // Handle different date formats that might come from backend
            let date;

            if (dateString instanceof Date) {
                date = dateString;
            } else if (typeof dateString === 'string') {
                // Try parsing the date string
                date = new Date(dateString);
            } else if (typeof dateString === 'number') {
                // Handle timestamp
                date = new Date(dateString);
            } else {
                console.warn('Invalid date format:', dateString);
                return 'Invalid Date';
            }

            // Check if date is valid
            if (isNaN(date.getTime())) {
                console.warn('Invalid date:', dateString);
                return 'Invalid Date';
            }

            // Format the date consistently
            const options = {
                year: 'numeric',
                month: 'short',
                day: 'numeric',
                hour: '2-digit',
                minute: '2-digit',
                hour12: true, // Use 12-hour format with AM/PM
                timeZone: 'Asia/Kolkata' // Set to Indian timezone, change as needed
            };

            return date.toLocaleDateString('en-US', options);
        } catch (error) {
            console.error('Error formatting date:', error, dateString);
            return 'Invalid Date';
        }
    };

    if (loading) {
        return (
            <div className="container-fluid py-4">
                <div className="text-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                    <p className="mt-2">Loading contacts...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="container-fluid py-4">
                <div className="alert alert-danger" role="alert">
                    <h4 className="alert-heading">Error!</h4>
                    <p>{error}</p>
                    <div className="mt-3">
                        <button className="btn btn-danger me-2" onClick={fetchContacts}>
                            Retry
                        </button>
                        <button 
                            className="btn btn-outline-secondary"
                            onClick={() => {
                                console.log('Current token:', getAuthToken());
                                console.log('API Base URL:', API_BASE_URL);
                            }}
                        >
                            Debug Info
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="container-fluid py-4">
            {/* Header */}
            <div className="row mb-4">
                <div className="col-12">
                    <div className="d-flex justify-content-between align-items-center">
                        <h1 className="h3 mb-0">Contact Management</h1>
                        <div className="d-flex gap-3">
                            <span className="badge bg-danger">Unread: {getStatusCount('unread')}</span>
                            <span className="badge bg-warning">Read: {getStatusCount('read')}</span>
                            <span className="badge bg-success">Resolved: {getStatusCount('resolved')}</span>
                            <span className="badge bg-info">Pending: {getStatusCount('pending')}</span>
                            <button className="btn btn-primary btn-sm" onClick={fetchContacts}>
                                <i className="fas fa-sync-alt me-1"></i>
                                Refresh
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            {/* Filters */}
            <div className="row mb-4">
                <div className="col-md-6">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Search by name, email, or subject..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </div>
                <div className="col-md-3">
                    <select
                        className="form-select"
                        value={filter}
                        onChange={(e) => setFilter(e.target.value)}
                    >
                        <option value="all">All Status</option>
                        <option value="unread">Unread</option>
                        <option value="read">Read</option>
                        <option value="resolved">Resolved</option>
                        <option value="pending">Pending</option>
                    </select>
                </div>
                <div className="col-md-3">
                    <div className="text-muted">
                        Total: {filteredContacts.length} contacts
                    </div>
                </div>
            </div>

            {/* Main Table */}
            <div className="row">
                <div className="col-12">
                    <div className="card" style={{ overflow: 'visible' }}>
                        <div className="card-body p-0" style={{ overflow: 'visible' }}>
                            <div className="table-responsive" style={{ overflow: 'visible' }}>
                                <table className="table table-hover mb-0">
                                    <thead className="table-light">
                                        <tr>
                                            <th>Name</th>
                                            <th>Email</th>
                                            <th>Phone</th>
                                            <th>Subject</th>
                                            <th>Date</th>
                                            <th>Status</th>
                                            <th>Actions</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {filteredContacts.map(contact => (
                                            <tr
                                                key={contact._id}
                                                className="cursor-pointer"
                                                onClick={() => handleContactClick(contact)}
                                            >
                                                <td className="fw-semibold">{contact.name || 'N/A'}</td>
                                                <td>{contact.email || 'N/A'}</td>
                                                <td>{contact.phone || 'N/A'}</td>
                                                <td className="text-truncate" style={{ maxWidth: '200px' }}>
                                                    {contact.subject || 'N/A'}
                                                </td>
                                                <td>{formatDate(contact.submittedAt || contact.createdAt)}</td>
                                                <td>
                                                    <span className={`badge ${getStatusBadge(contact.status)}`}>
                                                        {contact.status || 'pending'}
                                                    </span>
                                                </td>
                                                <td>
                                                    <div className="dropdown" onClick={(e) => e.stopPropagation()}>
                                                        <button
                                                            className="btn btn-sm btn-outline-secondary dropdown-toggle"
                                                            type="button"
                                                            data-bs-toggle="dropdown"
                                                        >
                                                            Actions
                                                        </button>
                                                        <ul className="dropdown-menu">
                                                            <li>
                                                                <button
                                                                    className="dropdown-item"
                                                                    onClick={() => handleContactClick(contact)}
                                                                >
                                                                    View Details
                                                                </button>
                                                            </li>
                                                            <li>
                                                                <button
                                                                    className="dropdown-item"
                                                                    onClick={() => updateStatus(contact._id, 'resolved')}
                                                                >
                                                                    Mark as Resolved
                                                                </button>
                                                            </li>
                                                            <li>
                                                                <button
                                                                    className="dropdown-item"
                                                                    onClick={() => window.open(`mailto:${contact.email}?subject=Re: ${contact.subject}`)}
                                                                >
                                                                    Reply via Email
                                                                </button>
                                                            </li>
                                                            <li><hr className="dropdown-divider" /></li>
                                                            <li>
                                                                <button
                                                                    className="dropdown-item text-danger"
                                                                    onClick={() => deleteContact(contact._id)}
                                                                >
                                                                    Delete
                                                                </button>
                                                            </li>
                                                        </ul>
                                                    </div>
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>

                            {filteredContacts.length === 0 && !loading && (
                                <div className="text-center p-4 text-muted">
                                    <h5>No contacts found</h5>
                                    <p>No contacts match your current search criteria.</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>

            {/* Contact Details Modal */}
            {selectedContact && (
                <div
                    className={`modal fade ${showModal ? 'show' : ''}`}
                    style={{ display: showModal ? 'block' : 'none' }}
                    tabIndex="-1"
                >
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Contact Details</h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    onClick={() => setShowModal(false)}
                                ></button>
                            </div>

                            <div className="modal-body">
                                <div className="row mb-4">
                                    <div className="col-md-6">
                                        <h6 className="text-muted mb-3">Contact Information</h6>
                                        <div className="mb-2">
                                            <strong>Name:</strong>
                                            <span className="ms-2">{selectedContact.name || 'N/A'}</span>
                                        </div>
                                        <div className="mb-2">
                                            <strong>Email:</strong>
                                            <a href={`mailto:${selectedContact.email}`} className="ms-2">
                                                {selectedContact.email || 'N/A'}
                                            </a>
                                        </div>
                                        <div className="mb-2">
                                            <strong>Phone:</strong>
                                            <a href={`tel:${selectedContact.phone}`} className="ms-2">
                                                {selectedContact.phone || 'N/A'}
                                            </a>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <h6 className="text-muted mb-3">Message Details</h6>
                                        <div className="mb-2">
                                            <strong>Subject:</strong>
                                            <span className="ms-2">{selectedContact.subject || 'N/A'}</span>
                                        </div>
                                        <div className="mb-2">
                                            <strong>Date:</strong>
                                            <span className="ms-2">{formatDate(selectedContact.submittedAt || selectedContact.createdAt)}</span>
                                        </div>
                                        <div className="mb-2">
                                            <strong>Status:</strong>
                                            <span className={`badge ${getStatusBadge(selectedContact.status)} ms-2`}>
                                                {selectedContact.status || 'pending'}
                                            </span>
                                        </div>
                                    </div>
                                </div>

                                <div className="mb-4">
                                    <h6 className="text-muted mb-3">Full Message</h6>
                                    <div className="border rounded p-3 bg-light">
                                        <p className="mb-0" style={{ whiteSpace: 'pre-wrap' }}>
                                            {selectedContact.message || 'No message content available'}
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <div className="modal-footer">
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={() => setShowModal(false)}
                                >
                                    Close
                                </button>
                                <button
                                    type="button"
                                    className="btn btn-primary"
                                    onClick={() => window.open(`mailto:${selectedContact.email}?subject=Re: ${selectedContact.subject}`)}
                                >
                                    Reply via Email
                                </button>
                                <button
                                    type="button"
                                    className="btn btn-success"
                                    onClick={() => {
                                        updateStatus(selectedContact._id, 'resolved');
                                        setShowModal(false);
                                    }}
                                >
                                    Mark as Resolved
                                </button>
                                <button
                                    type="button"
                                    className="btn btn-danger"
                                    onClick={() => deleteContact(selectedContact._id)}
                                >
                                    Delete Contact
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Modal Backdrop */}
            {showModal && (
                <div
                    className="modal-backdrop fade show"
                    onClick={() => setShowModal(false)}
                ></div>
            )}
        </div>
    );
};

export default ManageContacts;
