import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageCategories = () => {
    const [categories, setCategories] = useState([]);
    const [name, setName] = useState("");
    const [slug, setSlug] = useState("");
    const [parentCategory, setParentCategory] = useState("");
    const [level, setLevel] = useState(1); // 1 = Main, 2 = Sub, 3 = Sub-sub
    const [editingId, setEditingId] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [expandedCategories, setExpandedCategories] = useState({});

    // Fetch categories from backend
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const token = localStorage.getItem("adminToken");
                const res = await axios.get("http://localhost:5000/api/admin/categories", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setCategories(res.data);
            } catch (err) {
                console.error("Error fetching categories", err);
            }
        };

        fetchCategories();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem("adminToken");
        const categoryPayload = {
            name,
            slug: slug || name.toLowerCase().replace(/\s+/g, "-"),
            parentCategory: parentCategory || null,
            level: level,
        };

        try {
            if (editingId) {
                await axios.put(`http://localhost:5000/api/admin/category/${editingId}`, categoryPayload, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                alert("Category updated successfully!");
            } else {
                const res = await axios.post("http://localhost:5000/api/admin/add-category", categoryPayload, {
                    headers: { Authorization: `Bearer ${token}` },
                });

                const newCategory = res.data;
                setCategories([...categories, { ...newCategory, isActive: true }]);
                alert("Category added successfully!");
            }

            resetForm();
            setShowModal(false);
            // Refresh categories after add/edit
            window.location.reload();
        } catch (err) {
            console.error("Error submitting category:", err);
            alert("Failed to submit category");
        }
    };

    const resetForm = () => {
        setName("");
        setSlug("");
        setParentCategory("");
        setLevel(1);
        setEditingId(null);
    };

    const handleEdit = (category) => {
        setName(category.name);
        setSlug(category.slug);
        setParentCategory(category.parentCategory || "");
        setLevel(category.level || 1);
        setEditingId(category._id);
        setShowModal(true);
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Are you sure you want to delete this category? This will also delete all its subcategories.")) return;

        try {
            const token = localStorage.getItem("adminToken");
            await axios.delete(`http://localhost:5000/api/admin/category/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setCategories(categories.filter((cat) => cat._id !== id));
            alert("Category deleted successfully!");
        } catch (err) {
            console.error("Delete error:", err);
            alert("Failed to delete category");
        }
    };

    // Organize categories into hierarchical structure
    const organizeCategories = (cats) => {
        const categoryMap = {};
        const rootCategories = [];

        // First, create a map of all categories
        cats.forEach(cat => {
            categoryMap[cat._id] = { ...cat, children: [] };
        });

        // Then, organize them hierarchically
        cats.forEach(cat => {
            if (cat.parentCategory && categoryMap[cat.parentCategory]) {
                categoryMap[cat.parentCategory].children.push(categoryMap[cat._id]);
            } else {
                rootCategories.push(categoryMap[cat._id]);
            }
        });

        return rootCategories;
    };

    const toggleExpand = (categoryId) => {
        setExpandedCategories(prev => ({
            ...prev,
            [categoryId]: !prev[categoryId]
        }));
    };

    const renderCategoryTree = (cats, depth = 0) => {
        return cats.map((category, index) => (
            <React.Fragment key={category._id}>
                <tr>
                    <td>{index + 1}</td>
                    <td style={{ paddingLeft: `${depth * 20}px` }}>
                        <div className="d-flex align-items-center">
                            {category.children.length > 0 && (
                                <button
                                    className="btn btn-sm btn-link p-0 me-2"
                                    onClick={() => toggleExpand(category._id)}
                                >
                                    <i className={`fas ${expandedCategories[category._id] ? 'fa-chevron-down' : 'fa-chevron-right'}`}></i>
                                </button>
                            )}
                            <span className={`badge me-2 ${
                                category.level === 1 ? 'bg-primary' : 
                                category.level === 2 ? 'bg-success' : 'bg-warning'
                            }`}>
                                {category.level === 1 ? 'Main' : category.level === 2 ? 'Sub' : 'Sub-Sub'}
                            </span>
                            {category.name}
                        </div>
                    </td>
                    <td>{category.slug}</td>
                    <td>
                        <button 
                            className="btn btn-sm btn-outline-success me-2" 
                            onClick={() => {
                                setParentCategory(category._id);
                                setLevel(Math.min(category.level + 1, 3));
                                setShowModal(true);
                            }}
                        >
                            Add Sub
                        </button>
                        <button 
                            className="btn btn-sm btn-outline-primary me-2" 
                            onClick={() => handleEdit(category)}
                        >
                            Edit
                        </button>
                        <button 
                            className="btn btn-sm btn-outline-danger" 
                            onClick={() => handleDelete(category._id)}
                        >
                            Delete
                        </button>
                    </td>
                </tr>
                {expandedCategories[category._id] && category.children.length > 0 && 
                    renderCategoryTree(category.children, depth + 1)
                }
            </React.Fragment>
        ));
    };

    // Get available parent categories based on selected level
    const getAvailableParents = () => {
        if (level === 1) return []; // Main categories have no parent
        if (level === 2) return categories.filter(cat => cat.level === 1); // Sub categories under main
        if (level === 3) return categories.filter(cat => cat.level === 2); // Sub-sub under sub
        return [];
    };

    const organizedCategories = organizeCategories(categories);

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-5">
                <h2 className="mb-0">Manage Hierarchical Categories</h2>
                <button
                    className="btn btn-primary"
                    onClick={() => {
                        resetForm();
                        setShowModal(true);
                    }}
                >
                    Add New Category
                </button>
            </div>

            {/* Category Tree Table */}
            <div className="card">
                <div className="card-header">
                    <h5 className="mb-0">Category Hierarchy</h5>
                </div>
                <div className="card-body p-0">
                    <table className="table table-hover mb-0">
                        <thead className="table-light">
                            <tr>
                                <th>No</th>
                                <th>Name</th>
                                <th>Slug</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {organizedCategories.length > 0 ? (
                                renderCategoryTree(organizedCategories)
                            ) : (
                                <tr>
                                    <td colSpan="4" className="text-center text-muted py-4">
                                        No categories found. Add your first category!
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>

            {/* Modal */}
            {showModal && (
                <div className="modal" style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">
                                    {editingId ? "Edit Category" : "Add New Category"}
                                </h5>
                                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={handleSubmit}>
                                    <div className="mb-3">
                                        <label className="form-label">Category Level</label>
                                        <select 
                                            className="form-select" 
                                            value={level} 
                                            onChange={(e) => {
                                                setLevel(parseInt(e.target.value));
                                                setParentCategory(""); // Reset parent when level changes
                                            }}
                                            disabled={editingId} // Don't allow level change during edit
                                        >
                                            <option value={1}>Main Category</option>
                                            <option value={2}>Subcategory</option>
                                            <option value={3}>Sub-subcategory</option>
                                        </select>
                                    </div>

                                    {level > 1 && (
                                        <div className="mb-3">
                                            <label className="form-label">Parent Category</label>
                                            <select 
                                                className="form-select" 
                                                value={parentCategory} 
                                                onChange={(e) => setParentCategory(e.target.value)}
                                                required
                                            >
                                                <option value="">Select Parent Category</option>
                                                {getAvailableParents().map((cat) => (
                                                    <option key={cat._id} value={cat._id}>
                                                        {cat.name}
                                                    </option>
                                                ))}
                                            </select>
                                        </div>
                                    )}

                                    <div className="mb-3">
                                        <label className="form-label">Category Name</label>
                                        <input 
                                            type="text" 
                                            className="form-control" 
                                            value={name} 
                                            onChange={(e) => setName(e.target.value)} 
                                            placeholder="Enter category name"
                                            required 
                                        />
                                    </div>

                                    <div className="mb-3">
                                        <label className="form-label">Slug</label>
                                        <input 
                                            type="text" 
                                            className="form-control" 
                                            value={slug} 
                                            onChange={(e) => setSlug(e.target.value)} 
                                            placeholder="Auto-generated if empty" 
                                        />
                                    </div>

                                    <div className="modal-footer">
                                        <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>
                                            Cancel
                                        </button>
                                        <button type="submit" className="btn btn-primary">
                                            {editingId ? "Update" : "Save"}
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Legend */}
            <div className="mt-4">
                <div className="card">
                    <div className="card-body">
                        <h6>Category Levels:</h6>
                        <div className="d-flex gap-3">
                            <span><span className="badge bg-primary">Main</span> - Top level categories</span>
                            <span><span className="badge bg-success">Sub</span> - Subcategories</span>
                            <span><span className="badge bg-warning">Sub-Sub</span> - Sub-subcategories</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ManageCategories;
