import { useEffect, useState } from "react";
import axios from "axios";
import { FaTrash, FaEdit } from "react-icons/fa";

const ManageCourses = () => {
  const [courses, setCourses] = useState([]);
  const [message, setMessage] = useState("");

  const fetchCourses = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/courses", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
        },
      });
      console.log("Courses response:", res.data); // Check response shape

      // If backend returns array directly, use res.data
      // If backend returns object with courses key, use res.data.courses
      setCourses(res.data.courses || res.data);
    } catch (error) {
      console.error("Error fetching courses:", error);
    }
  };

  const deleteCourse = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/course/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
        },
      });
      setMessage("✅ Course deleted");
      fetchCourses(); // refresh list
    } catch (error) {
      console.error("Delete error:", error);
      setMessage("❌ Failed to delete");
    }
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  return (
    <div>
      <h2>All Courses</h2>
      {message && <p>{message}</p>}
      {!courses || courses.length === 0 ? (
        <p>No courses found</p>
      ) : (
        <table border="1" cellPadding="10">
          <thead>
            <tr>
              <th>Title</th>
              <th>Price</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {courses.map((course) => (
              <tr key={course._id}>
                <td>{course.title}</td>
                <td>₹{course.price}</td>
                <td>
                  <button onClick={() => alert("Edit logic coming soon")}>
                    <FaEdit />
                  </button>
                  <button onClick={() => deleteCourse(course._id)}>
                    <FaTrash />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ManageCourses;
