import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const CourseDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [course, setCourse] = useState(null);
    const [message, setMessage] = useState("");

    useEffect(() => {
        const fetchCourse = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/admin/course/${id}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
                    },
                });
                setCourse(res.data.course || res.data);
            } catch (error) {
                console.error("Error fetching course details:", error);
                setMessage("Failed to load course details");
            }
        };
        fetchCourse();
    }, [id]);

    if (message) return <p>{message}</p>;
    if (!course) return <p>Loading...</p>;

    return (
        <div>
            <h2>{course.title}</h2>
            {course.thumbnailUrl && (
                <img src={course.thumbnailUrl} alt={course.title} style={{ maxWidth: "300px" }} />
            )}
            <p><strong>Description:</strong> {course.description}</p>
            <p><strong>Instructor:</strong> {course.instructor}</p>
            <p><strong>Price:</strong> â‚¹{course.price}</p>
            <p><strong>Category:</strong> {course.category}</p>
            <p><strong>Level:</strong> {course.level}</p>
            <p><strong>Duration:</strong> {course.duration}</p>
            <p><strong>Video URL:</strong> <a href={course.videoUrl} target="_blank" rel="noreferrer">{course.videoUrl}</a></p>
            <p><strong>Tags:</strong> {course.tags && course.tags.join(", ")}</p>
            <p><strong>Language:</strong> {course.language}</p>
            <p><strong>Published:</strong> {course.isPublished ? "Yes" : "No"}</p>

            <button onClick={() => navigate(-1)}>Back to Manage Courses</button>
        </div>
    );
};

export default CourseDetail;
