import React, { useEffect, useState } from "react";

export default function AdminDashboard() {
    const [adminData, setAdminData] = useState(null);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchAdminData = async () => {
            const token = localStorage.getItem("adminToken");
            if (!token) {
                setError("No token found");
                return;
            }

            try {
                const res = await fetch("http://localhost:5000/api/admin/admin-data", {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${token}`,
                    },
                });

                const data = await res.json();

                if (res.ok) {
                    setAdminData(data);
                } else {
                    setError(data.message || "Unauthorized");
                }
            } catch (err) {
                setError("Failed to fetch admin data");
            }
        };

        fetchAdminData();
    }, []);

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>Admin Dashboard</h2>
                <button
                    className="btn btn-danger"
                    onClick={() => {
                        localStorage.removeItem("adminToken");
                        window.location.href = "/admin-login";
                    }}
                >
                    Logout
                </button>
            </div>

            {error && <div className="alert alert-danger">{error}</div>}

            {adminData && (
                <div className="row">
                    <div className="col-md-4 mb-3">
                        <div className="card text-white bg-primary h-100">
                            <div className="card-body">
                                <h5 className="card-title">Welcome Admin</h5>
                                <p className="card-text">Manage your platform efficiently.</p>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-4 mb-3">
                        <div className="card bg-light h-100">
                            <div className="card-body">
                                <h5 className="card-title">Users</h5>
                                <p className="card-text">View all registered users.</p>
                                <button className="btn btn-outline-primary w-100">Manage Users</button>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-4 mb-3">
                        <div className="card bg-light h-100">
                            <div className="card-body">
                                <h5 className="card-title">Courses</h5>
                                <p className="card-text">Add or update courses.</p>
                                <button className="btn btn-outline-success w-100">Manage Courses</button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
