import React, { useState, useEffect } from 'react';

const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [filterRole, setFilterRole] = useState('all');
    const [filterStatus, setFilterStatus] = useState('all');
    const [showModal, setShowModal] = useState(false);
    const [modalType, setModalType] = useState('add'); // 'add' or 'edit'
    const [selectedUser, setSelectedUser] = useState(null);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        role: 'student',
        status: 'active',
        department: '',
        phoneNumber: '',
        enrollmentDate: ''
    });

    // Mock data - replace with actual API calls
    useEffect(() => {
        const mockUsers = [
            {
                id: 1,
                firstName: 'John',
                lastName: 'Doe',
                email: 'john.doe@edu.com',
                role: 'student',
                status: 'active',
                department: 'Computer Science',
                phoneNumber: '+1234567890',
                enrollmentDate: '2024-01-15',
                lastLogin: '2024-06-09'
            },
            {
                id: 2,
                firstName: 'Jane',
                lastName: 'Smith',
                email: 'jane.smith@edu.com',
                role: 'teacher',
                status: 'active',
                department: 'Mathematics',
                phoneNumber: '+1234567891',
                enrollmentDate: '2023-08-20',
                lastLogin: '2024-06-10'
            },
            {
                id: 3,
                firstName: 'Bob',
                lastName: 'Johnson',
                email: 'bob.johnson@edu.com',
                role: 'admin',
                status: 'active',
                department: 'Administration',
                phoneNumber: '+1234567892',
                enrollmentDate: '2022-05-10',
                lastLogin: '2024-06-08'
            },
            {
                id: 4,
                firstName: 'Alice',
                lastName: 'Brown',
                email: 'alice.brown@edu.com',
                role: 'student',
                status: 'inactive',
                department: 'Physics',
                phoneNumber: '+1234567893',
                enrollmentDate: '2024-02-01',
                lastLogin: '2024-05-15'
            }
        ];
        setUsers(mockUsers);
    }, []);

    const filteredUsers = users.filter(user => {
        const matchesSearch = user.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
            user.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
            user.email.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesRole = filterRole === 'all' || user.role === filterRole;
        const matchesStatus = filterStatus === 'all' || user.status === filterStatus;
        return matchesSearch && matchesRole && matchesStatus;
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleAddUser = () => {
        setModalType('add');
        setFormData({
            firstName: '',
            lastName: '',
            email: '',
            role: 'student',
            status: 'active',
            department: '',
            phoneNumber: '',
            enrollmentDate: ''
        });
        setShowModal(true);
    };

    const handleEditUser = (user) => {
        setModalType('edit');
        setSelectedUser(user);
        setFormData({
            firstName: user.firstName,
            lastName: user.lastName,
            email: user.email,
            role: user.role,
            status: user.status,
            department: user.department,
            phoneNumber: user.phoneNumber,
            enrollmentDate: user.enrollmentDate
        });
        setShowModal(true);
    };

    const handleSaveUser = () => {
        if (modalType === 'add') {
            const newUser = {
                id: users.length + 1,
                ...formData,
                lastLogin: 'Never'
            };
            setUsers(prev => [...prev, newUser]);
        } else {
            setUsers(prev => prev.map(user =>
                user.id === selectedUser.id ? { ...user, ...formData } : user
            ));
        }
        setShowModal(false);
    };

    const handleDeleteUser = (userId) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            setUsers(prev => prev.filter(user => user.id !== userId));
        }
    };

    const toggleUserStatus = (userId) => {
        setUsers(prev => prev.map(user =>
            user.id === userId
                ? { ...user, status: user.status === 'active' ? 'inactive' : 'active' }
                : user
        ));
    };

    const getRoleBadgeClass = (role) => {
        switch (role) {
            case 'admin': return 'bg-danger';
            case 'teacher': return 'bg-success';
            case 'student': return 'bg-primary';
            default: return 'bg-secondary';
        }
    };

    const getStatusBadgeClass = (status) => {
        return status === 'active' ? 'bg-success' : 'bg-secondary';
    };

    return (
        <div className="container-fluid py-4">
            {/* Header */}
            <div className="row mb-4 align-items-center">
                <div className="col-12 col-md-8">
                    <h2 className="mb-1 mb-md-0">Manage Users</h2>
                    <p className="text-muted mb-3 mb-md-0">Manage students, teachers, and administrators</p>
                </div>
                <div className="col-12 col-md-4 text-md-end">
                    <button
                        className="btn btn-primary w-100 w-md-auto"
                        onClick={handleAddUser}
                    >
                        <i className="bi bi-plus-circle me-2"></i>
                        Add New User
                    </button>
                </div>
            </div>

            {/* Filters and Search */}
            <div className="row mb-4 g-3">
                <div className="col-12 col-lg-4">
                    <div className="input-group">
                        <span className="input-group-text">
                            <i className="bi bi-search"></i>
                        </span>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Search users..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                    </div>
                </div>
                <div className="col-6 col-md-4 col-lg-3">
                    <select
                        className="form-select"
                        value={filterRole}
                        onChange={(e) => setFilterRole(e.target.value)}
                    >
                        <option value="all">All Roles</option>
                        <option value="student">Students</option>
                        <option value="teacher">Teachers</option>
                        <option value="admin">Administrators</option>
                    </select>
                </div>
                <div className="col-6 col-md-4 col-lg-3">
                    <select
                        className="form-select"
                        value={filterStatus}
                        onChange={(e) => setFilterStatus(e.target.value)}
                    >
                        <option value="all">All Status</option>
                        <option value="active">Active</option>
                        <option value="inactive">Inactive</option>
                    </select>
                </div>
                <div className="col-12 col-md-4 col-lg-2">
                    <div className="text-muted small text-center text-md-start">
                        <span className="d-block d-md-inline">Showing {filteredUsers.length} of {users.length} users</span>
                    </div>
                </div>
            </div>

            {/* Users Table */}
            <div className="card">
                <div className="card-body p-0">
                    {/* Desktop Table View */}
                    <div className="d-none d-lg-block">
                        <div className="table-responsive">
                            <table className="table table-hover mb-0">
                                <thead className="table-light">
                                    <tr>
                                        <th>User</th>
                                        <th>Role</th>
                                        <th>Department</th>
                                        <th>Status</th>
                                        <th>Last Login</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredUsers.map(user => (
                                        <tr key={user.id}>
                                            <td>
                                                <div className="d-flex align-items-center">
                                                    <div className="avatar-circle bg-primary text-white me-3 d-flex align-items-center justify-content-center" style={{ width: '40px', height: '40px', borderRadius: '50%' }}>
                                                        {user.firstName.charAt(0)}{user.lastName.charAt(0)}
                                                    </div>
                                                    <div>
                                                        <div className="fw-semibold">{user.firstName} {user.lastName}</div>
                                                        <div className="text-muted small">{user.email}</div>
                                                        <div className="text-muted small">{user.phoneNumber}</div>
                                                    </div>
                                                </div>
                                            </td>
                                            <td>
                                                <span className={`badge ${getRoleBadgeClass(user.role)}`}>
                                                    {user.role.charAt(0).toUpperCase() + user.role.slice(1)}
                                                </span>
                                            </td>
                                            <td>{user.department}</td>
                                            <td>
                                                <span className={`badge ${getStatusBadgeClass(user.status)}`}>
                                                    {user.status.charAt(0).toUpperCase() + user.status.slice(1)}
                                                </span>
                                            </td>
                                            <td className="text-muted small">{user.lastLogin}</td>
                                            <td>
                                                <div className="btn-group" role="group">
                                                    <button
                                                        className="btn btn-sm btn-outline-primary"
                                                        onClick={() => handleEditUser(user)}
                                                        title="Edit User"
                                                    >
                                                        <i className="bi bi-pencil"></i>
                                                    </button>
                                                    <button
                                                        className={`btn btn-sm ${user.status === 'active' ? 'btn-outline-warning' : 'btn-outline-success'}`}
                                                        onClick={() => toggleUserStatus(user.id)}
                                                        title={user.status === 'active' ? 'Deactivate' : 'Activate'}
                                                    >
                                                        <i className={`bi ${user.status === 'active' ? 'bi-pause' : 'bi-play'}`}></i>
                                                    </button>
                                                    <button
                                                        className="btn btn-sm btn-outline-danger"
                                                        onClick={() => handleDeleteUser(user.id)}
                                                        title="Delete User"
                                                    >
                                                        <i className="bi bi-trash"></i>
                                                    </button>
                                                </div>
                                            </td>
                                        </tr>
                                    ))}
                                    {filteredUsers.length === 0 && (
                                        <tr>
                                            <td colSpan="6" className="text-center py-4 text-muted">
                                                No users found matching your criteria
                                            </td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                    </div>

                    {/* Mobile/Tablet Card View */}
                    <div className="d-lg-none">
                        {filteredUsers.length === 0 ? (
                            <div className="p-4 text-center text-muted">
                                No users found matching your criteria
                            </div>
                        ) : (
                            <div className="p-3">
                                {filteredUsers.map(user => (
                                    <div key={user.id} className="card mb-3 border">
                                        <div className="card-body">
                                            <div className="row align-items-center">
                                                <div className="col-3 col-sm-2">
                                                    <div className="avatar-circle bg-primary text-white d-flex align-items-center justify-content-center mx-auto" style={{ width: '50px', height: '50px', borderRadius: '50%' }}>
                                                        {user.firstName.charAt(0)}{user.lastName.charAt(0)}
                                                    </div>
                                                </div>
                                                <div className="col-9 col-sm-10">
                                                    <div className="d-flex justify-content-between align-items-start mb-2">
                                                        <div>
                                                            <h6 className="mb-1">{user.firstName} {user.lastName}</h6>
                                                            <div className="text-muted small">{user.email}</div>
                                                            <div className="text-muted small d-sm-none">{user.phoneNumber}</div>
                                                        </div>
                                                        <div className="d-flex flex-column flex-sm-row gap-1">
                                                            <span className={`badge ${getRoleBadgeClass(user.role)} mb-1 mb-sm-0`}>
                                                                {user.role.charAt(0).toUpperCase() + user.role.slice(1)}
                                                            </span>
                                                            <span className={`badge ${getStatusBadgeClass(user.status)}`}>
                                                                {user.status.charAt(0).toUpperCase() + user.status.slice(1)}
                                                            </span>
                                                        </div>
                                                    </div>

                                                    <div className="row small text-muted mb-2 d-none d-sm-flex">
                                                        <div className="col-sm-6">
                                                            <strong>Department:</strong> {user.department}
                                                        </div>
                                                        <div className="col-sm-6">
                                                            <strong>Phone:</strong> {user.phoneNumber}
                                                        </div>
                                                    </div>

                                                    <div className="d-sm-none small text-muted mb-2">
                                                        <div><strong>Department:</strong> {user.department}</div>
                                                    </div>

                                                    <div className="small text-muted mb-3">
                                                        <strong>Last Login:</strong> {user.lastLogin}
                                                    </div>

                                                    <div className="d-flex gap-2 justify-content-end">
                                                        <button
                                                            className="btn btn-sm btn-outline-primary"
                                                            onClick={() => handleEditUser(user)}
                                                        >
                                                            <i className="bi bi-pencil me-1"></i>
                                                            <span className="d-none d-sm-inline">Edit</span>
                                                        </button>
                                                        <button
                                                            className={`btn btn-sm ${user.status === 'active' ? 'btn-outline-warning' : 'btn-outline-success'}`}
                                                            onClick={() => toggleUserStatus(user.id)}
                                                        >
                                                            <i className={`bi ${user.status === 'active' ? 'bi-pause' : 'bi-play'} me-1`}></i>
                                                            <span className="d-none d-sm-inline">
                                                                {user.status === 'active' ? 'Deactivate' : 'Activate'}
                                                            </span>
                                                        </button>
                                                        <button
                                                            className="btn btn-sm btn-outline-danger"
                                                            onClick={() => handleDeleteUser(user.id)}
                                                        >
                                                            <i className="bi bi-trash me-1"></i>
                                                            <span className="d-none d-sm-inline">Delete</span>
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Add/Edit User Modal */}
            {showModal && (
                <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
                    <div className="modal-dialog modal-lg modal-dialog-centered modal-dialog-scrollable">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">
                                    {modalType === 'add' ? 'Add New User' : 'Edit User'}
                                </h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    onClick={() => setShowModal(false)}
                                ></button>
                            </div>
                            <div className="modal-body">
                                <div>
                                    <div className="row g-3">
                                        <div className="col-12 col-md-6">
                                            <label className="form-label">First Name *</label>
                                            <input
                                                type="text"
                                                className="form-control"
                                                name="firstName"
                                                value={formData.firstName}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="col-12 col-md-6">
                                            <label className="form-label">Last Name *</label>
                                            <input
                                                type="text"
                                                className="form-control"
                                                name="lastName"
                                                value={formData.lastName}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="col-12 col-md-6">
                                            <label className="form-label">Email *</label>
                                            <input
                                                type="email"
                                                className="form-control"
                                                name="email"
                                                value={formData.email}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="col-12 col-md-6">
                                            <label className="form-label">Phone Number</label>
                                            <input
                                                type="tel"
                                                className="form-control"
                                                name="phoneNumber"
                                                value={formData.phoneNumber}
                                                onChange={handleInputChange}
                                            />
                                        </div>
                                        <div className="col-12 col-md-4">
                                            <label className="form-label">Role *</label>
                                            <select
                                                className="form-select"
                                                name="role"
                                                value={formData.role}
                                                onChange={handleInputChange}
                                                required
                                            >
                                                <option value="student">Student</option>
                                                <option value="teacher">Teacher</option>
                                                <option value="admin">Administrator</option>
                                            </select>
                                        </div>
                                        <div className="col-12 col-md-4">
                                            <label className="form-label">Status</label>
                                            <select
                                                className="form-select"
                                                name="status"
                                                value={formData.status}
                                                onChange={handleInputChange}
                                            >
                                                <option value="active">Active</option>
                                                <option value="inactive">Inactive</option>
                                            </select>
                                        </div>
                                        <div className="col-12 col-md-4">
                                            <label className="form-label">Enrollment Date</label>
                                            <input
                                                type="date"
                                                className="form-control"
                                                name="enrollmentDate"
                                                value={formData.enrollmentDate}
                                                onChange={handleInputChange}
                                            />
                                        </div>
                                        <div className="col-12">
                                            <label className="form-label">Department</label>
                                            <input
                                                type="text"
                                                className="form-control"
                                                name="department"
                                                value={formData.department}
                                                onChange={handleInputChange}
                                                placeholder="e.g., Computer Science, Mathematics, Administration"
                                            />
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="modal-footer flex-column flex-sm-row">
                                <button
                                    type="button"
                                    className="btn btn-secondary w-100 w-sm-auto mb-2 mb-sm-0 me-sm-2"
                                    onClick={() => setShowModal(false)}
                                >
                                    Cancel
                                </button>
                                <button
                                    type="button"
                                    className="btn btn-primary w-100 w-sm-auto"
                                    onClick={handleSaveUser}
                                >
                                    {modalType === 'add' ? 'Add User' : 'Save Changes'}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Bootstrap Icons CDN - Add this to your HTML head or import in your project */}
            <link
                rel="stylesheet"
                href="https://cdn.jsdelivr.net/npm/bootstrap-icons@1.10.0/font/bootstrap-icons.css"
            />
        </div>
    );
};

export default ManageUsers;
