import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function AddCourse() {
    const navigate = useNavigate();
    const [categories, setCategories] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);
    const [hoveredCategory, setHoveredCategory] = useState(null);
    const [hoveredSubCategory, setHoveredSubCategory] = useState(null);
    const dropdownRef = useRef(null);

    const [courseData, setCourseData] = useState({
        title: "",
        description: "",
        instructor: "You", // Default instructor
        price: 0,
        category: "",
        categoryId: "",
        level: "",
        duration: "",
        thumbnailUrl: "",
        videoUrl: "",
        tags: [""],
        language: "",
        isPublished: false,
    });

    // Fetch categories from backend
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const res = await axios.get("http://localhost:5000/api/admin/categories", {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
                    },
                });
                setCategories(res.data);
            } catch (err) {
                console.error("Error fetching categories", err);
            }
        };

        fetchCategories();
    }, []);

    // Close dropdown when clicking outside
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setShowCategoryDropdown(false);
                setHoveredCategory(null);
                setHoveredSubCategory(null);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    // Organize categories into hierarchical structure
    const organizeCategories = (cats) => {
        const categoryMap = {};
        const rootCategories = [];

        // First, create a map of all categories
        cats.forEach(cat => {
            categoryMap[cat._id] = { ...cat, children: [] };
        });

        // Then, organize them hierarchically
        cats.forEach(cat => {
            if (cat.parentCategory && categoryMap[cat.parentCategory]) {
                categoryMap[cat.parentCategory].children.push(categoryMap[cat._id]);
            } else {
                rootCategories.push(categoryMap[cat._id]);
            }
        });

        return rootCategories;
    };

    const organizedCategories = organizeCategories(categories);

    const [activeTab, setActiveTab] = useState("basic");
    const [dragActive, setDragActive] = useState(false);

    const levels = ["Beginner", "Intermediate", "Advanced"];
    const languages = [
        "English",
        "Hindi",
        "Spanish",
        "French",
        "German",
        "Chinese",
        "Japanese",
    ];

    const handleInputChange = (field, value) => {
        setCourseData((prev) => ({ ...prev, [field]: value }));
    };

    const handleCategorySelect = (category) => {
        setCourseData((prev) => ({
            ...prev,
            category: category.name,
            categoryId: category._id
        }));
        setShowCategoryDropdown(false);
        setHoveredCategory(null);
        setHoveredSubCategory(null);
    };

    const handleArrayChange = (field, index, value) => {
        setCourseData((prev) => ({
            ...prev,
            [field]: prev[field].map((item, i) => (i === index ? value : item)),
        }));
    };

    const addArrayItem = (field) => {
        setCourseData((prev) => ({
            ...prev,
            [field]: [...prev[field], ""],
        }));
    };

    const removeArrayItem = (field, index) => {
        setCourseData((prev) => ({
            ...prev,
            [field]: prev[field].filter((_, i) => i !== index),
        }));
    };

    const handleDrag = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (e.type === "dragenter" || e.type === "dragover") {
            setDragActive(true);
        } else if (e.type === "dragleave") {
            setDragActive(false);
        }
    };

    const handleDrop = (e, fileType) => {
        e.preventDefault();
        e.stopPropagation();
        setDragActive(false);

        const files = e.dataTransfer.files;
        if (files && files[0]) {
            handleFileUpload(files[0], fileType);
        }
    };

    const handleFileUpload = (file, fileType) => {
        if (fileType === "thumbnail") {
            setCourseData((prev) => ({
                ...prev,
                thumbnailFile: file,
                thumbnailUrl: URL.createObjectURL(file),
            }));
        } else if (fileType === "promoVideo") {
            setCourseData((prev) => ({
                ...prev,
                promoVideoFile: file,
                videoUrl: URL.createObjectURL(file),
            }));
        }
    };

    const handleSubmit = async (action) => {
        try {
            setIsLoading(true);
            setError(null);

            const formData = new FormData();
            formData.append("title", courseData.title);
            formData.append("description", courseData.description);
            formData.append("instructor", courseData.instructor);
            formData.append("price", courseData.price);
            formData.append("category", courseData.category);
            formData.append("level", courseData.level);
            formData.append("duration", courseData.duration);
            formData.append("language", courseData.language);
            formData.append("isPublished", action === "published");
            formData.append("thumbnail", courseData.thumbnailFile);

            formData.append("videoUrl", courseData.promoVideoFile || "");
            formData.append("tags", JSON.stringify(courseData.tags));

            const token = localStorage.getItem("adminToken");
            const response = await axios.post(
                "http://localhost:5000/api/admin/add-course",
                formData,
                {
                    headers: {
                        "Content-Type": "multipart/form-data",
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (response.data && response.data.message === "Course added successfully") {
                alert(`Course ${action === "published" ? "published" : "saved as draft"} successfully!`);
                navigate("/courses");
            } else {
                throw new Error("Failed to add course");
            }
        } catch (err) {
            setError(err.response?.data?.message || err.message || "Failed to save course");
            console.error("Course submission error:", err);
        } finally {
            setIsLoading(false);
        }
    };

    const TabButton = ({ id, label, icon }) => (
        <button
            onClick={() => setActiveTab(id)}
            className={`btn me-2 d-flex align-items-center ${activeTab === id ? "btn-primary" : "btn-outline-secondary"}`}
        >
            <span className="me-2">{icon}</span>
            <span>{label}</span>
        </button>
    );

    const FileUploadZone = ({ fileType, accept, label, description, file }) => (
        <div
            className={`border border-2 border-dashed rounded p-4 text-center ${dragActive ? "border-secondary bg-light" : "border-secondary"}`}
            style={{ minHeight: "200px" }}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={(e) => handleDrop(e, fileType)}
        >
            {file ? (
                <div className="d-flex flex-column align-items-center">
                    <div
                        className="bg-success bg-opacity-25 rounded-circle p-3 mb-3"
                        style={{ width: "80px", height: "80px" }}
                    >
                        <span style={{ fontSize: "2rem" }}>
                            {fileType === "thumbnail" ? "🖼️" : "🎥"}
                        </span>
                    </div>
                    <p className="fw-bold mb-2">
                        {typeof file === "string" ? file.split("/").pop() : file.name}
                    </p>
                    <button
                        onClick={() => {
                            if (fileType === "thumbnail") {
                                setCourseData((prev) => ({
                                    ...prev,
                                    thumbnailUrl: "",
                                    thumbnailFile: null
                                }));
                            } else if (fileType === "promoVideo") {
                                setCourseData((prev) => ({
                                    ...prev,
                                    videoUrl: "",
                                    promoVideoFile: null
                                }));
                            }
                        }}
                        className="btn btn-sm btn-outline-danger"
                    >
                        Remove
                    </button>
                </div>
            ) : (
                <div className="d-flex flex-column align-items-center">
                    <div
                        className="bg-light rounded-circle p-3 mb-3"
                        style={{ width: "80px", height: "80px" }}
                    >
                        <span style={{ fontSize: "2rem" }}>📤</span>
                    </div>
                    <div className="mb-3">
                        <p className="fw-bold mb-1">{label}</p>
                        <p className="text-muted small">{description}</p>
                    </div>
                    <input
                        type="file"
                        accept={accept}
                        onChange={(e) =>
                            e.target.files[0] && handleFileUpload(e.target.files[0], fileType)
                        }
                        className="d-none"
                        id={`${fileType}-upload`}
                    />
                    <label htmlFor={`${fileType}-upload`} className="btn btn-primary">
                        Choose File
                    </label>
                </div>
            )}
        </div>
    );

    // Hierarchical Dropdown Component
    const HierarchicalCategoryDropdown = () => (
        <div className="position-relative" ref={dropdownRef}>
            <div
                className="form-control d-flex justify-content-between align-items-center"
                style={{ cursor: 'pointer' }}
                onClick={() => setShowCategoryDropdown(!showCategoryDropdown)}
            >
                <span className={courseData.category ? "text-dark" : "text-muted"}>
                    {courseData.category || "Select Category"}
                </span>
                <i className={`fas fa-chevron-${showCategoryDropdown ? 'up' : 'down'}`}></i>
            </div>

            {showCategoryDropdown && (
                <div className="position-absolute w-100 bg-white border rounded shadow-lg mt-1" style={{ zIndex: 1050 }}>
                    {organizedCategories.map((mainCategory) => (
                        <div
                            key={mainCategory._id}
                            className="position-relative"
                            onMouseEnter={() => setHoveredCategory(mainCategory._id)}
                            onMouseLeave={() => {
                                if (!hoveredSubCategory) {
                                    setHoveredCategory(null);
                                }
                            }}
                        >
                            <div
                                className={`px-3 py-2 border-bottom cursor-pointer d-flex justify-content-between align-items-center ${hoveredCategory === mainCategory._id ? 'bg-light' : ''
                                    }`}
                                onClick={() => handleCategorySelect(mainCategory)}
                                style={{ cursor: 'pointer' }}
                            >
                                <div className="d-flex align-items-center">
                                    <span className="badge bg-primary me-2">Main</span>
                                    <span>{mainCategory.name}</span>
                                </div>
                                {mainCategory.children.length > 0 && (
                                    <i className="fas fa-chevron-right text-muted"></i>
                                )}
                            </div>

                            {/* Subcategories Dropdown */}
                            {hoveredCategory === mainCategory._id && mainCategory.children.length > 0 && (
                                <div
                                    className="position-absolute bg-white border rounded shadow-lg"
                                    style={{
                                        left: '100%',
                                        top: 0,
                                        minWidth: '200px',
                                        zIndex: 1051
                                    }}
                                    onMouseEnter={() => setHoveredCategory(mainCategory._id)}
                                    onMouseLeave={() => {
                                        if (!hoveredSubCategory) {
                                            setHoveredCategory(null);
                                        }
                                    }}
                                >
                                    {mainCategory.children.map((subCategory) => (
                                        <div
                                            key={subCategory._id}
                                            className="position-relative"
                                            onMouseEnter={() => setHoveredSubCategory(subCategory._id)}
                                            onMouseLeave={() => setHoveredSubCategory(null)}
                                        >
                                            <div
                                                className={`px-3 py-2 border-bottom cursor-pointer d-flex justify-content-between align-items-center ${hoveredSubCategory === subCategory._id ? 'bg-light' : ''
                                                    }`}
                                                onClick={() => handleCategorySelect(subCategory)}
                                                style={{ cursor: 'pointer' }}
                                            >
                                                <div className="d-flex align-items-center">
                                                    <span className="badge bg-success me-2">Sub</span>
                                                    <span>{subCategory.name}</span>
                                                </div>
                                                {subCategory.children.length > 0 && (
                                                    <i className="fas fa-chevron-right text-muted"></i>
                                                )}
                                            </div>

                                            {/* Sub-subcategories Dropdown */}
                                            {hoveredSubCategory === subCategory._id && subCategory.children.length > 0 && (
                                                <div
                                                    className="position-absolute bg-white border rounded shadow-lg"
                                                    style={{
                                                        left: '100%',
                                                        top: 0,
                                                        minWidth: '200px',
                                                        zIndex: 1052
                                                    }}
                                                    onMouseEnter={() => setHoveredSubCategory(subCategory._id)}
                                                    onMouseLeave={() => setHoveredSubCategory(null)}
                                                >
                                                    {subCategory.children.map((subSubCategory) => (
                                                        <div
                                                            key={subSubCategory._id}
                                                            className="px-3 py-2 border-bottom cursor-pointer hover:bg-light"
                                                            onClick={() => handleCategorySelect(subSubCategory)}
                                                            style={{ cursor: 'pointer' }}
                                                        >
                                                            <div className="d-flex align-items-center">
                                                                <span className="badge bg-warning me-2">Sub-Sub</span>
                                                                <span>{subSubCategory.name}</span>
                                                            </div>
                                                        </div>
                                                    ))}
                                                </div>
                                            )}
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );

    return (
        <div className="min-vh-100 bg-light">
            {/* Header */}
            <div className="bg-white border-bottom">
                <div className="container-fluid py-4">
                    <div className="d-flex justify-content-between align-items-center">
                        <div className="d-flex align-items-center">
                            <div
                                className="bg-primary rounded p-2 me-3"
                                style={{ width: "48px", height: "48px" }}
                            >
                                <span className="text-white fs-4"></span>
                            </div>
                            <div>
                                <h1 className="h2 mb-1">Add New Course</h1>
                                <p className="text-muted mb-0">
                                    Create and publish your course content
                                </p>
                            </div>
                        </div>
                        <div className="d-flex gap-2">
                            <button
                                onClick={() => handleSubmit("draft")}
                                className="btn btn-outline-secondary"
                                disabled={isLoading}
                            >
                                {isLoading ? "Saving..." : "Save Draft"}
                            </button>
                            <button
                                onClick={() => handleSubmit("published")}
                                className="btn btn-primary"
                                disabled={isLoading}
                            >
                                {isLoading ? "Publishing..." : "Publish Course"}
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            {error && (
                <div className="container-fluid mt-3">
                    <div className="alert alert-danger">{error}</div>
                </div>
            )}

            <div className="container-fluid py-4">
                {/* Navigation Tabs */}
                <div className="bg-white rounded shadow-sm p-3 mb-4">
                    <div className="d-flex flex-wrap gap-2">
                        <TabButton id="basic" label="Basic Info" icon="📝" />
                        <TabButton id="media" label="Media" icon="🖼️" />
                        <TabButton id="content" label="Content" icon="📚" />
                        <TabButton id="pricing" label="Pricing" icon="💰" />
                    </div>
                </div>

                <div className="row">
                    {/* Main Content */}
                    <div className="col-lg-8">
                        {/* Basic Information Tab */}
                        {activeTab === "basic" && (
                            <div className="card shadow-sm">
                                <div className="card-body">
                                    <h2 className="card-title h4 mb-4">Basic Information</h2>

                                    <div className="row g-3">
                                        <div className="col-12">
                                            <label className="form-label fw-bold">
                                                Course Title *
                                            </label>
                                            <input
                                                type="text"
                                                value={courseData.title}
                                                onChange={(e) =>
                                                    handleInputChange("title", e.target.value)
                                                }
                                                placeholder="Enter a compelling course title"
                                                className="form-control"
                                                required
                                            />
                                        </div>

                                        <div className="col-12">
                                            <label className="form-label fw-bold">
                                                Course Description *
                                            </label>
                                            <textarea
                                                value={courseData.description}
                                                onChange={(e) =>
                                                    handleInputChange("description", e.target.value)
                                                }
                                                placeholder="Provide a detailed description of your course content, objectives, and target audience..."
                                                rows={6}
                                                className="form-control"
                                                style={{ resize: "none" }}
                                                required
                                            />
                                        </div>

                                        <div className="col-md-4">
                                            <label className="form-label fw-bold">Category *</label>
                                            <HierarchicalCategoryDropdown />
                                        </div>

                                        <div className="col-md-4">
                                            <label className="form-label fw-bold">Level *</label>
                                            <select
                                                value={courseData.level}
                                                onChange={(e) =>
                                                    handleInputChange("level", e.target.value)
                                                }
                                                className="form-select"
                                                required
                                            >
                                                <option value="">Select Level</option>
                                                {levels.map((level) => (
                                                    <option key={level} value={level}>
                                                        {level}
                                                    </option>
                                                ))}
                                            </select>
                                        </div>

                                        <div className="col-md-4">
                                            <label className="form-label fw-bold">Language *</label>
                                            <select
                                                value={courseData.language}
                                                onChange={(e) =>
                                                    handleInputChange("language", e.target.value)
                                                }
                                                className="form-select"
                                                required
                                            >
                                                <option value="">Select Language</option>
                                                {languages.map((lang) => (
                                                    <option key={lang} value={lang}>
                                                        {lang}
                                                    </option>
                                                ))}
                                            </select>
                                        </div>

                                        <div className="col-12">
                                            <label className="form-label fw-bold">
                                                Instructor Name
                                            </label>
                                            <input
                                                type="text"
                                                value={courseData.instructor}
                                                onChange={(e) =>
                                                    handleInputChange("instructor", e.target.value)
                                                }
                                                placeholder="Course instructor name"
                                                className="form-control"
                                            />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Media Tab */}
                        {activeTab === "media" && (
                            <div className="card shadow-sm">
                                <div className="card-body">
                                    <h2 className="card-title h4 mb-4">Media Assets</h2>

                                    <div className="row g-4">
                                        <div className="col-12">
                                            <h3 className="h5 mb-3">Course Thumbnail</h3>
                                            <FileUploadZone
                                                fileType="thumbnail"
                                                accept="image/*"
                                                label="Upload Course Thumbnail"
                                                description="Recommended: 1280x720px, JPG or PNG"
                                                file={courseData.thumbnailUrl ? { name: "Thumbnail uploaded" } : null}
                                            />
                                        </div>

                                        <div className="col-12">
                                            <h3 className="h5 mb-3">Promotional Video</h3>
                                            <FileUploadZone
                                                fileType="promoVideo"
                                                accept="video/*"
                                                label="Upload Promo Video"
                                                description="Optional: MP4 format, max 100MB"
                                                file={courseData.videoUrl ? { name: "Video uploaded" } : null}
                                            />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Content Tab */}
                        {activeTab === "content" && (
                            <div className="card shadow-sm">
                                <div className="card-body">
                                    <h2 className="card-title h4 mb-4">Course Content</h2>

                                    <div className="row g-4">
                                        <div className="col-12">
                                            <label className="form-label fw-bold">Course Tags</label>
                                            <p className="text-muted small mb-3">
                                                Add relevant tags to help students find your course
                                            </p>
                                            {courseData.tags.map((tag, index) => (
                                                <div
                                                    key={index}
                                                    className="d-flex align-items-center mb-3"
                                                >
                                                    <input
                                                        type="text"
                                                        value={tag}
                                                        onChange={(e) =>
                                                            handleArrayChange("tags", index, e.target.value)
                                                        }
                                                        placeholder={`Tag ${index + 1}`}
                                                        className="form-control me-2"
                                                    />
                                                    {courseData.tags.length > 1 && (
                                                        <button
                                                            onClick={() => removeArrayItem("tags", index)}
                                                            className="btn btn-outline-danger btn-sm"
                                                        >
                                                            ❌
                                                        </button>
                                                    )}
                                                </div>
                                            ))}
                                            <button
                                                onClick={() => addArrayItem("tags")}
                                                className="btn btn-outline-primary btn-sm"
                                            >
                                                ➕ Add Tag
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Pricing Tab */}
                        {activeTab === "pricing" && (
                            <div className="card shadow-sm">
                                <div className="card-body">
                                    <h2 className="card-title h4 mb-4">Pricing & Details</h2>

                                    <div className="row g-3">
                                        <div className="col-md-6">
                                            <label className="form-label fw-bold">
                                                Course Price (₹)
                                            </label>
                                            <input
                                                type="number"
                                                value={courseData.price}
                                                onChange={(e) =>
                                                    handleInputChange("price", e.target.value)
                                                }
                                                placeholder="0.00"
                                                min="0"
                                                step="0.01"
                                                className="form-control"
                                            />
                                            <div className="form-text">Set to 0 for free courses</div>
                                        </div>

                                        <div className="col-md-6">
                                            <label className="form-label fw-bold">
                                                Course Duration
                                            </label>
                                            <input
                                                type="text"
                                                value={courseData.duration}
                                                onChange={(e) =>
                                                    handleInputChange("duration", e.target.value)
                                                }
                                                placeholder="e.g., 4 weeks, 20 hours"
                                                className="form-control"
                                                required
                                            />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>

                    {/* Sidebar */}
                    <div className="col-lg-4">
                        <div className="row g-3">
                            {/* Course Preview */}
                            <div className="col-12">
                                <div className="card shadow-sm">
                                    <div className="card-body">
                                        <h3 className="card-title h5 mb-3">Course Preview</h3>
                                        <div className="mb-3">
                                            <div
                                                className="bg-light rounded d-flex align-items-center justify-content-center"
                                                style={{ height: "180px" }}
                                            >
                                                {courseData.thumbnailUrl ? (
                                                    <div className="text-center">
                                                        <span style={{ fontSize: "3rem" }}>🖼️</span>
                                                        <p className="text-muted small mb-0 mt-2">
                                                            Thumbnail uploaded
                                                        </p>
                                                    </div>
                                                ) : (
                                                    <div className="text-center">
                                                        <span style={{ fontSize: "3rem" }}>📷</span>
                                                        <p className="text-muted small mb-0 mt-2">
                                                            No thumbnail
                                                        </p>
                                                    </div>
                                                )}
                                            </div>
                                        </div>

                                        <div className="mb-3">
                                            <h4 className="h6 mb-1 text-truncate">
                                                {courseData.title || "Course Title"}
                                            </h4>
                                            <p className="text-muted small mb-0 text-truncate">
                                                {courseData.description?.substring(0, 50) + "..." ||
                                                    "Course description will appear here"}
                                            </p>
                                        </div>

                                        <div className="d-flex justify-content-between align-items-center">
                                            <span className="text-muted small">
                                                {courseData.level || "Level"}
                                            </span>
                                            <span className="fw-bold text-primary">
                                                {courseData.price ? `₹${courseData.price}` : "Free"}
                                            </span>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            {/* Quick Stats */}
                            <div className="col-12">
                                <div className="card shadow-sm">
                                    <div className="card-body">
                                        <h3 className="card-title h5 mb-3">Quick Stats</h3>
                                        <div className="row g-2">
                                            <div className="col-12">
                                                <div className="d-flex justify-content-between align-items-center">
                                                    <div className="d-flex align-items-center">
                                                        <span className="me-2">⏰</span>
                                                        <span className="small text-muted">Duration</span>
                                                    </div>
                                                    <span className="small fw-bold">
                                                        {courseData.duration || "Not set"}
                                                    </span>
                                                </div>
                                            </div>

                                            <div className="col-12">
                                                <div className="d-flex justify-content-between align-items-center">
                                                    <div className="d-flex align-items-center">
                                                        <span className="me-2">👨‍🏫</span>
                                                        <span className="small text-muted">
                                                            Instructor
                                                        </span>
                                                    </div>
                                                    <span className="small fw-bold">
                                                        {courseData.instructor || "Not set"}
                                                    </span>
                                                </div>
                                            </div>

                                            <div className="col-12">
                                                <div className="d-flex justify-content-between align-items-center">
                                                    <div className="d-flex align-items-center">
                                                        <span className="me-2">📚</span>
                                                        <span className="small text-muted">Category</span>
                                                    </div>
                                                    <span className="small fw-bold text-truncate ms-2">
                                                        {courseData.category || "None"}
                                                    </span>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            {/* Tips */}
                            <div className="col-12">
                                <div className="card bg-primary bg-opacity-10 border-primary">
                                    <div className="card-body">
                                        <h3 className="card-title h5 text-primary mb-3">
                                            💡 Tips for Success
                                        </h3>
                                        <ul className="list-unstyled small text-primary mb-0">
                                            <li className="mb-2">
                                                • Use a compelling title that clearly describes what
                                                students will learn
                                            </li>
                                            <li className="mb-2">
                                                • Add a high-quality thumbnail to attract more students
                                            </li>
                                            <li className="mb-2">
                                                • Write detailed description to set clear
                                                expectations
                                            </li>
                                            <li className="mb-0">
                                                • Choose relevant tags to improve discoverability
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
