import React, { useState } from "react";

const ManageCategories = () => {
    const [categories, setCategories] = useState([
        { id: 1, name: "Web Development", isActive: true, parentId: null, level: 0 },
        { id: 2, name: "Frontend", isActive: true, parentId: 1, level: 1 },
        { id: 3, name: "React", isActive: true, parentId: 2, level: 2 },
        { id: 4, name: "Vue.js", isActive: true, parentId: 2, level: 2 },
        { id: 5, name: "Backend", isActive: true, parentId: 1, level: 1 },
        { id: 6, name: "Node.js", isActive: true, parentId: 5, level: 2 },
        { id: 7, name: "Mobile Development", isActive: true, parentId: null, level: 0 },
        { id: 8, name: "iOS", isActive: true, parentId: 7, level: 1 },
        { id: 9, name: "Swift", isActive: true, parentId: 8, level: 2 },
    ]);

    // Form states
    const [formData, setFormData] = useState({
        name: "",
        level: 0,
        parentId: null,
        selectedMain: null,
        selectedSub: null
    });

    const [editingId, setEditingId] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [expandedItems, setExpandedItems] = useState(new Set());

    // Filter states
    const [filters, setFilters] = useState({
        mainCategory: "",
        sortOrder: "asc"
    });

    // Helper functions
    const getCategoriesByLevel = (level, parentId = null) => {
        return categories.filter(cat =>
            cat.level === level &&
            cat.isActive &&
            (parentId ? cat.parentId === parentId : cat.parentId === null)
        );
    };

    const getChildrenCount = (categoryId) => {
        return categories.filter(cat => cat.parentId === categoryId).length;
    };

    const sortCategories = (categoryList) => {
        return [...categoryList].sort((a, b) => {
            const comparison = a.name.toLowerCase().localeCompare(b.name.toLowerCase());
            return filters.sortOrder === "asc" ? comparison : -comparison;
        });
    };

    const toggleExpand = (categoryId) => {
        const newExpanded = new Set(expandedItems);
        if (newExpanded.has(categoryId)) {
            newExpanded.delete(categoryId);
        } else {
            newExpanded.add(categoryId);
        }
        setExpandedItems(newExpanded);
    };

    // Form handlers
    const resetForm = () => {
        setFormData({
            name: "",
            level: 0,
            parentId: null,
            selectedMain: null,
            selectedSub: null
        });
        setEditingId(null);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (editingId) {
            setCategories(categories.map(cat =>
                cat.id === editingId
                    ? { ...cat, name: formData.name, level: formData.level, parentId: formData.parentId }
                    : cat
            ));
        } else {
            const newCategory = {
                id: Math.max(...categories.map(c => c.id), 0) + 1,
                name: formData.name,
                isActive: true,
                parentId: formData.parentId,
                level: formData.level,
            };
            setCategories([...categories, newCategory]);
        }

        resetForm();
        setShowModal(false);
    };

    const handleEdit = (category) => {
        const parentSub = category.level === 2 ? categories.find(cat => cat.id === category.parentId) : null;
        const parentMain = parentSub ? parentSub.parentId : category.parentId;

        setFormData({
            name: category.name,
            level: category.level,
            parentId: category.parentId,
            selectedMain: parentMain,
            selectedSub: parentSub?.id || null
        });
        setEditingId(category.id);
        setShowModal(true);
    };

    const handleDelete = (id) => {
        if (getChildrenCount(id) > 0) {
            alert("Cannot delete category with subcategories. Delete subcategories first.");
            return;
        }

        if (window.confirm("Are you sure you want to delete this category?")) {
            setCategories(categories.filter(cat => cat.id !== id));
        }
    };

    const handleLevelChange = (level) => {
        let parentId = null;
        if (level === 1 && formData.selectedMain) parentId = formData.selectedMain;
        if (level === 2 && formData.selectedSub) parentId = formData.selectedSub;

        setFormData({ ...formData, level, parentId });
    };

    // Get filtered categories
    const getFilteredMainCategories = () => {
        let mainCats = getCategoriesByLevel(0);
        if (filters.mainCategory) {
            mainCats = mainCats.filter(cat => cat.id === parseInt(filters.mainCategory));
        }
        return sortCategories(mainCats);
    };

    const CategoryItem = ({ category, level, index }) => {
        const children = sortCategories(getCategoriesByLevel(level + 1, category.id));
        const isExpanded = expandedItems.has(category.id);
        const badgeColors = ["primary", "info", "success"];
        const labels = ["Main", "Sub", "Sub-Sub"];
        const indentClass = level > 0 ? `ps-${3 + level * 2}` : "";

        return (
            <div key={category.id}>
                <div className={`p-3 ${level === 0 ? 'bg-light' : 'bg-white'} border-bottom d-flex justify-content-between align-items-center ${indentClass}`}>
                    <div className="d-flex align-items-center" style={{ cursor: children.length > 0 ? "pointer" : "default" }}
                        onClick={() => children.length > 0 && toggleExpand(category.id)}>
                        {children.length > 0 && (
                            <i className={`fas ${isExpanded ? "fa-chevron-down" : "fa-chevron-right"} me-2`}></i>
                        )}
                        <span className={`badge bg-${badgeColors[level]} me-2`}>{labels[level]}</span>
                        <strong>{level === 0 ? `${index + 1}. ` : ""}{category.name}</strong>
                        {children.length > 0 && (
                            <small className="text-muted ms-2">({children.length} sub-categories)</small>
                        )}
                    </div>
                    <div>
                        <button className="btn btn-sm btn-outline-primary me-2" onClick={() => handleEdit(category)}>
                            Edit
                        </button>
                        <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(category.id)}>
                            Delete
                        </button>
                    </div>
                </div>
                {isExpanded && children.map((child, childIndex) => (
                    <CategoryItem key={child.id} category={child} level={level + 1} index={childIndex} />
                ))}
            </div>
        );
    };

    return (
        <div className="container mt-4">
            {/* Header */}
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2 className="mb-0">Manage Categories</h2>
                <button className="btn btn-primary" onClick={() => { resetForm(); setShowModal(true); }}>
                    Add New Category
                </button>
            </div>

            {/* Statistics */}
            <div className="row mb-4">
                {[0, 1, 2].map(level => (
                    <div key={level} className="col-md-4">
                        <div className="card text-center">
                            <div className="card-body">
                                <h5 className="card-title">{["Main", "Sub", "Sub-Sub"][level]} Categories</h5>
                                <h3 className={`text-${["primary", "info", "success"][level]}`}>
                                    {categories.filter(cat => cat.level === level).length}
                                </h3>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Filters */}
            <div className="card mb-4">
                <div className="card-header">
                    <h5 className="mb-0 text-center">Filter & Sort</h5>
                </div>
                <div className="card-body">
                    <div className="row">
                        <div className="col-md-6">
                            <label className="form-label">Filter by Main Category</label>
                            <select className="form-select" value={filters.mainCategory}
                                onChange={(e) => setFilters({ ...filters, mainCategory: e.target.value })}>
                                <option value="">All Categories</option>
                                {getCategoriesByLevel(0).map(cat => (
                                    <option key={cat.id} value={cat.id}>{cat.name}</option>
                                ))}
                            </select>
                        </div>
                        <div className="col-md-6">
                            <label className="form-label">Sort Order</label>
                            <select className="form-select" value={filters.sortOrder}
                                onChange={(e) => setFilters({ ...filters, sortOrder: e.target.value })}>
                                <option value="asc">A to Z</option>
                                <option value="desc">Z to A</option>
                            </select>
                        </div>
                    </div>
                    <div className="mt-3">
                        <button className="btn btn-outline-secondary me-2"
                            onClick={() => setFilters({ mainCategory: "", sortOrder: "asc" })}>
                            Clear Filters
                        </button>
                        <small className="text-muted">
                            Showing {getFilteredMainCategories().length} main categories
                        </small>
                    </div>
                </div>
            </div>

            {/* Categories List */}
            <div className="card">
                <div className="card-header">
                    <h5 className="mb-0 text-center">Categories</h5>
                </div>
                <div className="card-body p-0">
                    {getFilteredMainCategories().length === 0 ? (
                        <div className="text-center text-muted py-4">No categories found.</div>
                    ) : (
                        getFilteredMainCategories().map((category, index) => (
                            <CategoryItem key={category.id} category={category} level={0} index={index} />
                        ))
                    )}
                </div>
            </div>

            {/* Modal */}
            {showModal && (
                <div className="modal" style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">{editingId ? "Edit Category" : "Add New Category"}</h5>
                                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                            </div>
                            <div className="modal-body">
                                <div className="mb-3">
                                    <label className="form-label">Category Level</label>
                                    <select className="form-select" value={formData.level}
                                        onChange={(e) => handleLevelChange(parseInt(e.target.value))}>
                                        <option value={0}>Main Category</option>
                                        <option value={1}>Sub Category</option>
                                        <option value={2}>Sub-Sub Category</option>
                                    </select>
                                </div>

                                {formData.level > 0 && (
                                    <div className="mb-3">
                                        <label className="form-label">Main Category</label>
                                        <select className="form-select" value={formData.selectedMain || ""}
                                            onChange={(e) => {
                                                const mainId = e.target.value ? parseInt(e.target.value) : null;
                                                setFormData({
                                                    ...formData,
                                                    selectedMain: mainId,
                                                    selectedSub: null,
                                                    parentId: formData.level === 1 ? mainId : null
                                                });
                                            }} required>
                                            <option value="">Choose Main Category</option>
                                            {getCategoriesByLevel(0).map(cat => (
                                                <option key={cat.id} value={cat.id}>{cat.name}</option>
                                            ))}
                                        </select>
                                    </div>
                                )}

                                {formData.level === 2 && formData.selectedMain && (
                                    <div className="mb-3">
                                        <label className="form-label">Sub Category</label>
                                        <select className="form-select" value={formData.selectedSub || ""}
                                            onChange={(e) => {
                                                const subId = e.target.value ? parseInt(e.target.value) : null;
                                                setFormData({ ...formData, selectedSub: subId, parentId: subId });
                                            }} required>
                                            <option value="">Choose Sub Category</option>
                                            {getCategoriesByLevel(1, formData.selectedMain).map(cat => (
                                                <option key={cat.id} value={cat.id}>{cat.name}</option>
                                            ))}
                                        </select>
                                    </div>
                                )}

                                <div className="mb-3">
                                    <label className="form-label">Category Name</label>
                                    <input type="text" className="form-control" value={formData.name}
                                        onChange={(e) => setFormData({ ...formData, name: e.target.value })} required />
                                </div>

                                {formData.name && (
                                    <div className="alert alert-info">
                                        <strong>Preview:</strong> {
                                            formData.level === 0 ? formData.name :
                                                formData.level === 1 && formData.selectedMain ?
                                                    `${categories.find(c => c.id === formData.selectedMain)?.name} > ${formData.name}` :
                                                    formData.level === 2 && formData.selectedMain && formData.selectedSub ?
                                                        `${categories.find(c => c.id === formData.selectedMain)?.name} > ${categories.find(c => c.id === formData.selectedSub)?.name} > ${formData.name}` :
                                                        "Please select parent categories"
                                        }
                                    </div>
                                )}
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>
                                        Cancel
                                    </button>
                                    <button type="button" className="btn btn-primary" onClick={handleSubmit}>
                                        {editingId ? "Update" : "Save"}
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ManageCategories;
