import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const AddCourse = () => {
    const navigate = useNavigate();
    const thumbnailRef = useRef(null);
    const videoRef = useRef(null);

    // State Management
    const [categories, setCategories] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState("");
    const [previewData, setPreviewData] = useState({
        thumbnail: null,
        video: null
    });

    const [courseData, setCourseData] = useState({
        title: "",
        description: "",
        instructor: "You",
        price: 0,
        category: "",
        level: "",
        duration: "",
        tags: [""],
        language: "",
        isPublished: false,
    });

    const [files, setFiles] = useState({
        thumbnail: null,
        promoVideo: null
    });

    // Form validation state
    const [validationErrors, setValidationErrors] = useState({});

    // Fetch categories from backend
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const token = localStorage.getItem("adminToken");
                const res = await axios.get("http://localhost:5000/api/admin/categories", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setCategories(res.data);
            } catch (err) {
                console.error("Error fetching categories", err);
                setError("Failed to load categories");
            }
        };

        fetchCategories();
    }, []);

    // Handle input changes
    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setCourseData(prev => ({
            ...prev,
            [name]: type === "checkbox" ? checked : value
        }));

        // Clear validation error when user starts typing
        if (validationErrors[name]) {
            setValidationErrors(prev => ({
                ...prev,
                [name]: ""
            }));
        }
    };

    // Handle file uploads
    const handleFileChange = (e, fileType) => {
        const file = e.target.files[0];
        if (file) {
            setFiles(prev => ({
                ...prev,
                [fileType]: file
            }));

            // Create preview for images
            if (fileType === 'thumbnail' && file.type.startsWith('image/')) {
                const reader = new FileReader();
                reader.onload = (e) => {
                    setPreviewData(prev => ({
                        ...prev,
                        thumbnail: e.target.result
                    }));
                };
                reader.readAsDataURL(file);
            }

            // Create preview for videos
            if (fileType === 'promoVideo' && file.type.startsWith('video/')) {
                const url = URL.createObjectURL(file);
                setPreviewData(prev => ({
                    ...prev,
                    video: url
                }));
            }
        }
    };

    // Handle tags
    const handleTagChange = (index, value) => {
        const newTags = [...courseData.tags];
        newTags[index] = value;
        setCourseData(prev => ({
            ...prev,
            tags: newTags
        }));
    };

    const addTag = () => {
        setCourseData(prev => ({
            ...prev,
            tags: [...prev.tags, ""]
        }));
    };

    const removeTag = (index) => {
        if (courseData.tags.length > 1) {
            const newTags = courseData.tags.filter((_, i) => i !== index);
            setCourseData(prev => ({
                ...prev,
                tags: newTags
            }));
        }
    };

    // Form validation
    const validateForm = () => {
        const errors = {};

        if (!courseData.title.trim()) errors.title = "Course title is required";
        if (!courseData.description.trim()) errors.description = "Course description is required";
        if (!courseData.category) errors.category = "Please select a category";
        if (!courseData.level) errors.level = "Please select a level";
        if (!courseData.duration.trim()) errors.duration = "Course duration is required";
        if (!courseData.language.trim()) errors.language = "Course language is required";
        if (courseData.price < 0) errors.price = "Price cannot be negative";

        const validTags = courseData.tags.filter(tag => tag.trim() !== "");
        if (validTags.length === 0) errors.tags = "At least one tag is required";

        setValidationErrors(errors);
        return Object.keys(errors).length === 0;
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validateForm()) {
            setError("Please fix the validation errors");
            return;
        }

        setIsLoading(true);
        setError(null);
        setSuccessMessage("");

        try {
            const token = localStorage.getItem("adminToken");
            const formData = new FormData();

            // Add course data to FormData
            Object.keys(courseData).forEach(key => {
                if (key === 'tags') {
                    formData.append(key, JSON.stringify(courseData.tags.filter(tag => tag.trim() !== "")));
                } else {
                    formData.append(key, courseData[key]);
                }
            });

            // Add files if they exist
            if (files.thumbnail) {
                formData.append('thumbnail', files.thumbnail);
            }
            if (files.promoVideo) {
                formData.append('promoVideo', files.promoVideo);
            }

            const response = await axios.post(
                "http://localhost:5000/api/admin/add-course",
                formData,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'multipart/form-data'
                    },
                }
            );

            setSuccessMessage("Course added successfully!");
            
            // Reset form after successful submission
            setTimeout(() => {
                resetForm();
                navigate("/admin/courses"); // Navigate to courses list
            }, 2000);

        } catch (err) {
            console.error("Error adding course:", err);
            setError(err.response?.data?.message || "Failed to add course");
        } finally {
            setIsLoading(false);
        }
    };

    // Reset form
    const resetForm = () => {
        setCourseData({
            title: "",
            description: "",
            instructor: "You",
            price: 0,
            category: "",
            level: "",
            duration: "",
            tags: [""],
            language: "",
            isPublished: false,
        });
        setFiles({
            thumbnail: null,
            promoVideo: null
        });
        setPreviewData({
            thumbnail: null,
            video: null
        });
        setValidationErrors({});
        setError(null);
        setSuccessMessage("");
        
        // Reset file inputs
        if (thumbnailRef.current) thumbnailRef.current.value = "";
        if (videoRef.current) videoRef.current.value = "";
    };

    // Organize categories hierarchically (similar to ManageCategories)
    const organizeCategories = (cats) => {
        const categoryMap = {};
        const rootCategories = [];

        cats.forEach(cat => {
            categoryMap[cat._id] = { ...cat, children: [] };
        });

        cats.forEach(cat => {
            if (cat.parentCategory && categoryMap[cat.parentCategory]) {
                categoryMap[cat.parentCategory].children.push(categoryMap[cat._id]);
            } else {
                rootCategories.push(categoryMap[cat._id]);
            }
        });

        return rootCategories;
    };

    // Render category options recursively
    const renderCategoryOptions = (cats, depth = 0) => {
        return cats.map(category => (
            <React.Fragment key={category._id}>
                <option value={category._id}>
                    {'â€”'.repeat(depth)} {category.name}
                </option>
                {category.children && category.children.length > 0 &&
                    renderCategoryOptions(category.children, depth + 1)
                }
            </React.Fragment>
        ));
    };

    const organizedCategories = organizeCategories(categories);

    return (
        <div className="container mt-4">
            <div className="row justify-content-center">
                <div className="col-lg-8">
                    <div className="d-flex justify-content-between align-items-center mb-4">
                        <h2 className="mb-0">Add New Course</h2>
                        <button
                            type="button"
                            className="btn btn-outline-secondary"
                            onClick={() => navigate("/admin/courses")}
                        >
                            <i className="fas fa-arrow-left me-2"></i>
                            Back to Courses
                        </button>
                    </div>

                    {/* Alert Messages */}
                    {error && (
                        <div className="alert alert-danger alert-dismissible fade show" role="alert">
                            <i className="fas fa-exclamation-circle me-2"></i>
                            {error}
                            <button type="button" className="btn-close" onClick={() => setError(null)}></button>
                        </div>
                    )}

                    {successMessage && (
                        <div className="alert alert-success alert-dismissible fade show" role="alert">
                            <i className="fas fa-check-circle me-2"></i>
                            {successMessage}
                            <button type="button" className="btn-close" onClick={() => setSuccessMessage("")}></button>
                        </div>
                    )}

                    <div className="card shadow">
                        <div className="card-header">
                            <h5 className="mb-0">Course Information</h5>
                        </div>
                        <div className="card-body">
                            <form onSubmit={handleSubmit}>
                                <div className="row">
                                    {/* Course Title */}
                                    <div className="col-md-12 mb-3">
                                        <label className="form-label">
                                            Course Title <span className="text-danger">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            className={`form-control ${validationErrors.title ? 'is-invalid' : ''}`}
                                            name="title"
                                            value={courseData.title}
                                            onChange={handleInputChange}
                                            placeholder="Enter course title"
                                        />
                                        {validationErrors.title && (
                                            <div className="invalid-feedback">{validationErrors.title}</div>
                                        )}
                                    </div>

                                    {/* Course Description */}
                                    <div className="col-md-12 mb-3">
                                        <label className="form-label">
                                            Course Description <span className="text-danger">*</span>
                                        </label>
                                        <textarea
                                            className={`form-control ${validationErrors.description ? 'is-invalid' : ''}`}
                                            name="description"
                                            value={courseData.description}
                                            onChange={handleInputChange}
                                            rows="4"
                                            placeholder="Enter detailed course description"
                                        />
                                        {validationErrors.description && (
                                            <div className="invalid-feedback">{validationErrors.description}</div>
                                        )}
                                    </div>

                                    {/* Instructor */}
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">Instructor</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="instructor"
                                            value={courseData.instructor}
                                            onChange={handleInputChange}
                                            placeholder="Enter instructor name"
                                        />
                                    </div>

                                    {/* Price */}
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">Price ($)</label>
                                        <input
                                            type="number"
                                            className={`form-control ${validationErrors.price ? 'is-invalid' : ''}`}
                                            name="price"
                                            value={courseData.price}
                                            onChange={handleInputChange}
                                            min="0"
                                            step="0.01"
                                            placeholder="0.00"
                                        />
                                        {validationErrors.price && (
                                            <div className="invalid-feedback">{validationErrors.price}</div>
                                        )}
                                    </div>

                                    {/* Category */}
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">
                                            Category <span className="text-danger">*</span>
                                        </label>
                                        <select
                                            className={`form-select ${validationErrors.category ? 'is-invalid' : ''}`}
                                            name="category"
                                            value={courseData.category}
                                            onChange={handleInputChange}
                                        >
                                            <option value="">Select Category</option>
                                            {renderCategoryOptions(organizedCategories)}
                                        </select>
                                        {validationErrors.category && (
                                            <div className="invalid-feedback">{validationErrors.category}</div>
                                        )}
                                    </div>

                                    {/* Level */}
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">
                                            Level <span className="text-danger">*</span>
                                        </label>
                                        <select
                                            className={`form-select ${validationErrors.level ? 'is-invalid' : ''}`}
                                            name="level"
                                            value={courseData.level}
                                            onChange={handleInputChange}
                                        >
                                            <option value="">Select Level</option>
                                            <option value="Beginner">Beginner</option>
                                            <option value="Intermediate">Intermediate</option>
                                            <option value="Advanced">Advanced</option>
                                            <option value="Expert">Expert</option>
                                        </select>
                                        {validationErrors.level && (
                                            <div className="invalid-feedback">{validationErrors.level}</div>
                                        )}
                                    </div>

                                    {/* Duration */}
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">
                                            Duration <span className="text-danger">*</span>
                                        </label>
                                        <input
                                            type="text"
                                            className={`form-control ${validationErrors.duration ? 'is-invalid' : ''}`}
                                            name="duration"
                                            value={courseData.duration}
                                            onChange={handleInputChange}
                                            placeholder="e.g., 4 weeks, 20 hours"
                                        />
                                        {validationErrors.duration && (
                                            <div className="invalid-feedback">{validationErrors.duration}</div>
                                        )}
                                    </div>

                                    {/* Language */}
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">
                                            Language <span className="text-danger">*</span>
                                        </label>
                                        <select
                                            className={`form-select ${validationErrors.language ? 'is-invalid' : ''}`}
                                            name="language"
                                            value={courseData.language}
                                            onChange={handleInputChange}
                                        >
                                            <option value="">Select Language</option>
                                            <option value="English">English</option>
                                            <option value="Spanish">Spanish</option>
                                            <option value="French">French</option>
                                            <option value="German">German</option>
                                            <option value="Chinese">Chinese</option>
                                            <option value="Japanese">Japanese</option>
                                            <option value="Hindi">Hindi</option>
                                            <option value="Arabic">Arabic</option>
                                        </select>
                                        {validationErrors.language && (
                                            <div className="invalid-feedback">{validationErrors.language}</div>
                                        )}
                                    </div>

                                    {/* Tags */}
                                    <div className="col-md-12 mb-3">
                                        <label className="form-label">
                                            Tags <span className="text-danger">*</span>
                                        </label>
                                        {courseData.tags.map((tag, index) => (
                                            <div key={index} className="input-group mb-2">
                                                <input
                                                    type="text"
                                                    className={`form-control ${validationErrors.tags ? 'is-invalid' : ''}`}
                                                    value={tag}
                                                    onChange={(e) => handleTagChange(index, e.target.value)}
                                                    placeholder="Enter tag"
                                                />
                                                <button
                                                    type="button"
                                                    className="btn btn-outline-danger"
                                                    onClick={() => removeTag(index)}
                                                    disabled={courseData.tags.length === 1}
                                                >
                                                    <i className="fas fa-times"></i>
                                                </button>
                                            </div>
                                        ))}
                                        <button
                                            type="button"
                                            className="btn btn-outline-primary btn-sm"
                                            onClick={addTag}
                                        >
                                            <i className="fas fa-plus me-1"></i>
                                            Add Tag
                                        </button>
                                        {validationErrors.tags && (
                                            <div className="text-danger small mt-1">{validationErrors.tags}</div>
                                        )}
                                    </div>

                                    {/* File Uploads */}
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">Course Thumbnail</label>
                                        <input
                                            type="file"
                                            className="form-control"
                                            ref={thumbnailRef}
                                            accept="image/*"
                                            onChange={(e) => handleFileChange(e, 'thumbnail')}
                                        />
                                        {previewData.thumbnail && (
                                            <div className="mt-2">
                                                <img
                                                    src={previewData.thumbnail}
                                                    alt="Thumbnail preview"
                                                    className="img-thumbnail"
                                                    style={{ maxHeight: "150px" }}
                                                />
                                            </div>
                                        )}
                                    </div>

                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">Promo Video</label>
                                        <input
                                            type="file"
                                            className="form-control"
                                            ref={videoRef}
                                            accept="video/*"
                                            onChange={(e) => handleFileChange(e, 'promoVideo')}
                                        />
                                        {previewData.video && (
                                            <div className="mt-2">
                                                <video
                                                    controls
                                                    className="img-thumbnail"
                                                    style={{ maxHeight: "150px", maxWidth: "100%" }}
                                                >
                                                    <source src={previewData.video} />
                                                    Your browser does not support the video tag.
                                                </video>
                                            </div>
                                        )}
                                    </div>

                                    {/* Publish Status */}
                                    <div className="col-md-12 mb-4">
                                        <div className="form-check">
                                            <input
                                                className="form-check-input"
                                                type="checkbox"
                                                name="isPublished"
                                                checked={courseData.isPublished}
                                                onChange={handleInputChange}
                                            />
                                            <label className="form-check-label">
                                                Publish this course immediately
                                            </label>
                                        </div>
                                    </div>
                                </div>

                                {/* Form Actions */}
                                <div className="d-flex gap-2 justify-content-end">
                                    <button
                                        type="button"
                                        className="btn btn-outline-secondary"
                                        onClick={resetForm}
                                        disabled={isLoading}
                                    >
                                        <i className="fas fa-undo me-2"></i>
                                        Reset
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn btn-primary"
                                        disabled={isLoading}
                                    >
                                        {isLoading ? (
                                            <>
                                                <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                                                Adding Course...
                                            </>
                                        ) : (
                                            <>
                                                <i className="fas fa-save me-2"></i>
                                                Add Course
                                            </>
                                        )}
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddCourse;
