import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageCategories = () => {
    // State for categories
    const [categories, setCategories] = useState([]);

    // Form states
    const [name, setName] = useState("");
    const [slug, setSlug] = useState("");
    const [isActive, setIsActive] = useState(true);
    const [categoryType, setCategoryType] = useState("main");
    const [selectedMainCategory, setSelectedMainCategory] = useState(null);
    const [selectedSubCategory, setSelectedSubCategory] = useState(null);
    const [parentId, setParentId] = useState(null);
    const [editingId, setEditingId] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [showBulkModal, setShowBulkModal] = useState(false);

    // Expanded states for dropdown functionality
    const [expandedMainCategories, setExpandedMainCategories] = useState(new Set());
    const [expandedSubCategories, setExpandedSubCategories] = useState(new Set());

    // Filter dropdowns
    const [filterMainCategory, setFilterMainCategory] = useState("");
    const [filterSubCategory, setFilterSubCategory] = useState("");

    // Loading and error states
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    // Sorting states
    const [sortBy, setSortBy] = useState("name");
    const [sortOrder, setSortOrder] = useState("asc");

    // Bulk category creation state
    const [bulkCategory, setBulkCategory] = useState({
        mainCategory: { name: "", slug: "" },
        subcategories: [{ name: "", slug: "", subSubcategories: [{ name: "", slug: "" }] }]
    });

    // Auto-generate slug when name changes
    useEffect(() => {
        if (name && !editingId) {
            const generatedSlug = name.toLowerCase()
                .replace(/\s+/g, "-")
                .replace(/[^a-z0-9-]/g, "");
            setSlug(generatedSlug);
        }
    }, [name, editingId]);

    // Generate slug helper function
    const generateSlug = (text) => {
        return text.toLowerCase()
            .replace(/\s+/g, "-")
            .replace(/[^a-z0-9-]/g, "");
    };

    // Helper functions for category levels
    const getCategoryLevel = (type) => {
        switch (type) {
            case "main": return 1;
            case "sub": return 2;
            case "sub-sub": return 3;
            default: return 1;
        }
    };

    const getCategoryTypeFromLevel = (level) => {
        switch (level) {
            case 1: return "main";
            case 2: return "sub";
            case 3: return "sub-sub";
            default: return "main";
        }
    };

    // Category filtering functions
    const getMainCategories = () => {
        return categories.filter((cat) => cat.level === 1);
    };

    const getSubCategories = (mainCategoryId = null) => {
        if (mainCategoryId) {
            return categories.filter(
                (cat) => cat.level === 2 && cat.parentCategory === mainCategoryId
            );
        }
        return categories.filter((cat) => cat.level === 2);
    };

    const getSubSubCategories = (subCategoryId = null) => {
        if (subCategoryId) {
            return categories.filter(
                (cat) => cat.level === 3 && cat.parentCategory === subCategoryId
            );
        }
        return categories.filter((cat) => cat.level === 3);
    };

    const getCategoryPath = (category) => {
        const path = [];
        let current = category;

        while (current) {
            path.unshift(current.name);
            current = categories.find((cat) => cat._id === current.parentCategory);
        }

        return path.join(" > ");
    };

    const getChildrenCount = (categoryId) => {
        return categories.filter((cat) => cat.parentCategory === categoryId).length;
    };

    // Sort categories function
    const sortCategories = (categoryList) => {
        return [...categoryList].sort((a, b) => {
            let aValue, bValue;

            switch (sortBy) {
                case "name":
                    aValue = a.name.toLowerCase();
                    bValue = b.name.toLowerCase();
                    break;
                case "id":
                    aValue = a._id;
                    bValue = b._id;
                    break;
                case "children":
                    aValue = getChildrenCount(a._id);
                    bValue = getChildrenCount(b._id);
                    break;
                default:
                    aValue = a.name.toLowerCase();
                    bValue = b.name.toLowerCase();
            }

            if (sortBy === "name") {
                const comparison = aValue.localeCompare(bValue);
                return sortOrder === "asc" ? comparison : -comparison;
            } else {
                if (aValue < bValue) return sortOrder === "asc" ? -1 : 1;
                if (aValue > bValue) return sortOrder === "asc" ? 1 : -1;
                return 0;
            }
        });
    };

    // Get sorted categories
    const getSortedSubCategories = (parentId) => {
        const subCats = getSubCategories(parentId);
        return sortCategories(subCats);
    };

    const getSortedSubSubCategories = (parentId) => {
        const subSubCats = getSubSubCategories(parentId);
        return sortCategories(subSubCats);
    };

    // Toggle functions for expand/collapse
    const toggleMainCategory = (categoryId) => {
        const newExpanded = new Set(expandedMainCategories);
        if (newExpanded.has(categoryId)) {
            newExpanded.delete(categoryId);
            // Also collapse all sub-categories under this main category
            const subCats = getSubCategories(categoryId);
            subCats.forEach((subCat) => {
                const newExpandedSub = new Set(expandedSubCategories);
                newExpandedSub.delete(subCat._id);
                setExpandedSubCategories(newExpandedSub);
            });
        } else {
            newExpanded.add(categoryId);
        }
        setExpandedMainCategories(newExpanded);
    };

    const toggleSubCategory = (categoryId) => {
        const newExpanded = new Set(expandedSubCategories);
        if (newExpanded.has(categoryId)) {
            newExpanded.delete(categoryId);
        } else {
            newExpanded.add(categoryId);
        }
        setExpandedSubCategories(newExpanded);
    };

    // Handle cascading dropdown changes
    const handleMainCategoryChange = (mainCatId) => {
        setSelectedMainCategory(mainCatId);
        setSelectedSubCategory(null);

        if (categoryType === "sub") {
            setParentId(mainCatId);
        } else if (categoryType === "sub-sub") {
            setParentId(null);
        }
    };

    const handleSubCategoryChange = (subCatId) => {
        setSelectedSubCategory(subCatId);

        if (categoryType === "sub-sub") {
            setParentId(subCatId);
        }
    };

    // Fetch categories from backend
    const fetchCategories = useCallback(async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem("adminToken");
            if (!token) {
                setError("No admin token found. Please login again.");
                return;
            }

            const res = await axios.get("http://localhost:5000/api/admin/categories", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setCategories(res.data);
            setError("");
        } catch (err) {
            console.error("Error fetching categories", err);
            setError(err.response?.data?.message || "Failed to fetch categories");
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchCategories();
    }, [fetchCategories]);

    // Form submission handler
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        const token = localStorage.getItem("adminToken");
        if (!token) {
            setError("No admin token found. Please login again.");
            setLoading(false);
            return;
        }

        const level = getCategoryLevel(categoryType);
        let finalParentId = null;

        if (categoryType === "sub" && selectedMainCategory) {
            finalParentId = selectedMainCategory;
        } else if (categoryType === "sub-sub" && selectedSubCategory) {
            finalParentId = selectedSubCategory;
        }

        const categoryPayload = {
            name: name.trim(),
            slug: slug.trim() || generateSlug(name),
            parentCategory: finalParentId,
            level: level,
        };

        // Validation
        if (!categoryPayload.name) {
            setError("Category name is required");
            setLoading(false);
            return;
        }

        if (level > 1 && !finalParentId) {
            setError("Parent category is required for subcategories");
            setLoading(false);
            return;
        }

        try {
            if (editingId) {
                const res = await axios.put(
                    `http://localhost:5000/api/admin/category/${editingId}`,
                    categoryPayload,
                    {
                        headers: { Authorization: `Bearer ${token}` }
                    }
                );

                setCategories(prev => prev.map(cat =>
                    cat._id === editingId ? res.data.category : cat
                ));
                alert("Category updated successfully!");
            } else {
                const res = await axios.post(
                    "http://localhost:5000/api/admin/add-category",
                    categoryPayload,
                    {
                        headers: { Authorization: `Bearer ${token}` },
                    }
                );

                setCategories(prev => [...prev, res.data.category]);
                alert("Category added successfully!");
            }

            resetForm();
            setShowModal(false);
        } catch (err) {
            console.error("Error submitting category:", err);
            const errorMessage = err.response?.data?.message || "Failed to submit category";
            setError(errorMessage);
            alert(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    // Handle bulk category creation
    const handleBulkSubmit = async () => {
        setLoading(true);
        setError("");

        const token = localStorage.getItem("adminToken");
        if (!token) {
            setError("No admin token found. Please login again.");
            setLoading(false);
            return;
        }

        // Validation
        if (!bulkCategory.mainCategory.name.trim()) {
            setError("Main category name is required");
            setLoading(false);
            return;
        }

        try {
            // Create main category first
            const mainCategoryData = {
                name: bulkCategory.mainCategory.name.trim(),
                slug: bulkCategory.mainCategory.slug || generateSlug(bulkCategory.mainCategory.name),
                level: 1,
                parentCategory: null
            };

            const mainCategoryRes = await axios.post(
                "http://localhost:5000/api/admin/add-category",
                mainCategoryData,
                { headers: { Authorization: `Bearer ${token}` } }
            );

            const mainCategoryId = mainCategoryRes.data.category._id;
            let allNewCategories = [mainCategoryRes.data.category];

            // Create subcategories
            for (const subcategory of bulkCategory.subcategories) {
                if (subcategory.name.trim()) {
                    const subcategoryData = {
                        name: subcategory.name.trim(),
                        slug: subcategory.slug || generateSlug(subcategory.name),
                        level: 2,
                        parentCategory: mainCategoryId
                    };

                    const subcategoryRes = await axios.post(
                        "http://localhost:5000/api/admin/add-category",
                        subcategoryData,
                        { headers: { Authorization: `Bearer ${token}` } }
                    );

                    const subcategoryId = subcategoryRes.data.category._id;
                    allNewCategories.push(subcategoryRes.data.category);

                    // Create sub-subcategories
                    for (const subSubcategory of subcategory.subSubcategories) {
                        if (subSubcategory.name.trim()) {
                            const subSubcategoryData = {
                                name: subSubcategory.name.trim(),
                                slug: subSubcategory.slug || generateSlug(subSubcategory.name),
                                level: 3,
                                parentCategory: subcategoryId
                            };

                            const subSubcategoryRes = await axios.post(
                                "http://localhost:5000/api/admin/add-category",
                                subSubcategoryData,
                                { headers: { Authorization: `Bearer ${token}` } }
                            );

                            allNewCategories.push(subSubcategoryRes.data.category);
                        }
                    }
                }
            }

            // Update categories state
            setCategories(prev => [...prev, ...allNewCategories]);
            alert("Category hierarchy created successfully!");
            resetBulkForm();
            setShowBulkModal(false);

        } catch (err) {
            console.error("Error creating bulk categories:", err);
            const errorMessage = err.response?.data?.message || "Failed to create category hierarchy";
            setError(errorMessage);
            alert(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    const resetForm = () => {
        setName("");
        setSlug("");
        setIsActive(true);
        setCategoryType("main");
        setSelectedMainCategory(null);
        setSelectedSubCategory(null);
        setParentId(null);
        setEditingId(null);
        setError("");
    };

    const resetBulkForm = () => {
        setBulkCategory({
            mainCategory: { name: "", slug: "" },
            subcategories: [{ name: "", slug: "", subSubcategories: [{ name: "", slug: "" }] }]
        });
        setError("");
    };

    // Bulk form handlers
    const updateMainCategory = (field, value) => {
        setBulkCategory(prev => ({
            ...prev,
            mainCategory: {
                ...prev.mainCategory,
                [field]: value,
                ...(field === 'name' && { slug: generateSlug(value) })
            }
        }));
    };

    const addSubcategory = () => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: [...prev.subcategories, { name: "", slug: "", subSubcategories: [{ name: "", slug: "" }] }]
        }));
    };

    const updateSubcategory = (index, field, value) => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: prev.subcategories.map((sub, i) =>
                i === index ? {
                    ...sub,
                    [field]: value,
                    ...(field === 'name' && { slug: generateSlug(value) })
                } : sub
            )
        }));
    };

    const removeSubcategory = (index) => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: prev.subcategories.filter((_, i) => i !== index)
        }));
    };

    const addSubSubcategory = (subcategoryIndex) => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: prev.subcategories.map((sub, i) =>
                i === subcategoryIndex ? {
                    ...sub,
                    subSubcategories: [...sub.subSubcategories, { name: "", slug: "" }]
                } : sub
            )
        }));
    };

    const updateSubSubcategory = (subcategoryIndex, subSubIndex, field, value) => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: prev.subcategories.map((sub, i) =>
                i === subcategoryIndex ? {
                    ...sub,
                    subSubcategories: sub.subSubcategories.map((subSub, j) =>
                        j === subSubIndex ? {
                            ...subSub,
                            [field]: value,
                            ...(field === 'name' && { slug: generateSlug(value) })
                        } : subSub
                    )
                } : sub
            )
        }));
    };

    const removeSubSubcategory = (subcategoryIndex, subSubIndex) => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: prev.subcategories.map((sub, i) =>
                i === subcategoryIndex ? {
                    ...sub,
                    subSubcategories: sub.subSubcategories.filter((_, j) => j !== subSubIndex)
                } : sub
            )
        }));
    };

    const handleEdit = (category) => {
        setName(category.name);
        setSlug(category.slug);
        setIsActive(true); // Assuming all categories are active
        setCategoryType(getCategoryTypeFromLevel(category.level));

        if (category.level === 2) {
            setSelectedMainCategory(category.parentCategory);
            setSelectedSubCategory(null);
        } else if (category.level === 3) {
            const parentSub = categories.find((cat) => cat._id === category.parentCategory);
            if (parentSub) {
                setSelectedMainCategory(parentSub.parentCategory);
                setSelectedSubCategory(parentSub._id);
            }
        } else {
            setSelectedMainCategory(null);
            setSelectedSubCategory(null);
        }

        setParentId(category.parentCategory);
        setEditingId(category._id);
        setShowModal(true);
        setError("");
    };

    const handleDelete = async (id) => {
        const hasChildren = getChildrenCount(id) > 0;

        if (hasChildren) {
            alert("Cannot delete category that has subcategories. Please delete subcategories first.");
            return;
        }

        if (!window.confirm("Are you sure you want to delete this category?")) return;

        setLoading(true);
        try {
            const token = localStorage.getItem("adminToken");
            await axios.delete(`http://localhost:5000/api/admin/category/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setCategories(prev => prev.filter(cat => cat._id !== id));
            alert("Category deleted successfully!");
        } catch (err) {
            console.error("Delete error:", err);
            const errorMessage = err.response?.data?.message || "Failed to delete category";
            alert(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    // Get filtered main categories
    const getFilteredMainCategories = () => {
        let mainCats = getMainCategories();

        if (filterMainCategory) {
            mainCats = mainCats.filter((cat) => cat._id === filterMainCategory);
        }

        return sortCategories(mainCats);
    };

    // Get filtered sub categories
    const getFilteredSubCategories = (mainCategoryId) => {
        let subCats = getSortedSubCategories(mainCategoryId);

        if (filterSubCategory) {
            subCats = subCats.filter((cat) => cat._id === filterSubCategory);
        }

        return subCats;
    };

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-5">
                <h2 className="mb-0">Manage Categories</h2>
                <div className="d-flex gap-2">
                    <button
                        className="btn btn-success"
                        onClick={() => {
                            resetBulkForm();
                            setShowBulkModal(true);
                        }}
                    >
                        <i className="fas fa-plus-circle me-1"></i>
                        Bulk Create
                    </button>
                    <button
                        className="btn btn-primary"
                        onClick={() => {
                            resetForm();
                            setShowModal(true);
                        }}
                    >
                        <i className="fas fa-plus me-1"></i>
                        Add New Category
                    </button>
                </div>
            </div>

            {/* Error Display */}
            {error && (
                <div className="alert alert-danger alert-dismissible fade show" role="alert">
                    {error}
                    <button
                        type="button"
                        className="btn-close"
                        onClick={() => setError("")}
                    ></button>
                </div>
            )}

            {/* Loading Overlay */}
            {loading && (
                <div className="position-fixed top-0 start-0 w-100 h-100 d-flex justify-content-center align-items-center"
                    style={{ backgroundColor: "rgba(0,0,0,0.5)", zIndex: 9999 }}>
                    <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            )}

            {/* Category Statistics */}
            <div className="row mb-4">
                <div className="col-md-4">
                    <div className="card text-center">
                        <div className="card-body">
                            <h5 className="card-title">Main Categories</h5>
                            <h3 className="text-primary">
                                {categories.filter((cat) => cat.level === 1).length}
                            </h3>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card text-center">
                        <div className="card-body">
                            <h5 className="card-title">Sub Categories</h5>
                            <h3 className="text-info">
                                {categories.filter((cat) => cat.level === 2).length}
                            </h3>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card text-center">
                        <div className="card-body">
                            <h5 className="card-title">Sub-Sub Categories</h5>
                            <h3 className="text-success">
                                {categories.filter((cat) => cat.level === 3).length}
                            </h3>
                        </div>
                    </div>
                </div>
            </div>

            {/* Filter and Sort Section */}
            <div className="card mb-4">
                <div className="card-header">
                    <h5 className="mb-0 text-center">Filter & Sort Categories</h5>
                </div>
                <div className="card-body">
                    <div className="row">
                        {/* Filter Section */}
                        <div className="col-md-8 text-center">
                            <h6 className="text-muted mb-3">Filter Options</h6>
                            <div className="row">
                                <div className="col-md-6">
                                    <label className="form-label">Main Category</label>
                                    <select
                                        className="form-select"
                                        value={filterMainCategory}
                                        onChange={(e) => {
                                            setFilterMainCategory(e.target.value);
                                            setFilterSubCategory("");
                                        }}
                                    >
                                        <option value="">All Main Categories</option>
                                        {getMainCategories().map((cat) => (
                                            <option key={cat._id} value={cat._id}>
                                                {cat.name}
                                            </option>
                                        ))}
                                    </select>
                                </div>
                                <div className="col-md-6">
                                    <label className="form-label">Sub Category</label>
                                    <select
                                        className="form-select"
                                        value={filterSubCategory}
                                        onChange={(e) => setFilterSubCategory(e.target.value)}
                                        disabled={!filterMainCategory}
                                    >
                                        <option value="">All Sub Categories</option>
                                        {filterMainCategory &&
                                            getSubCategories(filterMainCategory).map((cat) => (
                                                <option key={cat._id} value={cat._id}>
                                                    {cat.name}
                                                </option>
                                            ))}
                                    </select>
                                </div>
                            </div>
                        </div>

                        {/* Sort Section */}
                        <div className="col-md-4 text-center">
                            <h6 className="text-muted mb-3">Sort Options</h6>
                            <div className="row">
                                <div className="col-md-6">
                                    <label className="form-label">Sort By</label>
                                    <select
                                        className="form-select"
                                        value={sortBy}
                                        onChange={(e) => setSortBy(e.target.value)}
                                    >
                                        <option value="name">Name</option>
                                        <option value="id">ID</option>
                                        <option value="children">Children Count</option>
                                    </select>
                                </div>
                                <div className="col-md-6">
                                    <label className="form-label">Sort Order</label>
                                    <select
                                        className="form-select"
                                        value={sortOrder}
                                        onChange={(e) => setSortOrder(e.target.value)}
                                    >
                                        <option value="asc">Ascending</option>
                                        <option value="desc">Descending</option>
                                    </select>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="row mt-3">
                        <div className="col-md-12">
                            <div className="d-flex gap-2">
                                <button
                                    className="btn btn-outline-secondary"
                                    onClick={() => {
                                        setFilterMainCategory("");
                                        setFilterSubCategory("");
                                    }}
                                >
                                    <i className="fas fa-filter me-1"></i>
                                    Clear Filters
                                </button>
                                <button
                                    className="btn btn-outline-info"
                                    onClick={() => {
                                        setSortBy("name");
                                        setSortOrder("asc");
                                    }}
                                >
                                    <i className="fas fa-sort me-1"></i>
                                    Reset Sorting
                                </button>
                                <div className="ms-auto">
                                    <small className="text-muted">
                                        Showing {getFilteredMainCategories().length} main categories
                                        {sortBy !== "name" || sortOrder !== "asc"
                                            ? ` (sorted by ${sortBy} ${sortOrder === "asc" ? "↑" : "↓"})`
                                            : ""}
                                    </small>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Dropdown/Accordion Style Categories */}
            <div className="card">
                <div className="card-header">
                    <h5 className="mb-0 text-center">Categories</h5>
                </div>
                <div className="card-body">
                    {getFilteredMainCategories().length === 0 ? (
                        <div className="text-center text-muted py-4">
                            <i className="fas fa-folder-open fa-3x mb-3"></i>
                            <p>No categories found matching the current filters.</p>
                        </div>
                    ) : (
                        getFilteredMainCategories().map((mainCategory, index) => {
                            const subCategories = getSortedSubCategories(mainCategory._id);
                            const isMainExpanded = expandedMainCategories.has(mainCategory._id);

                            // Filter sub categories if filter is applied
                            const filteredSubCategories = filterSubCategory
                                ? subCategories.filter((sub) => sub._id === filterSubCategory)
                                : subCategories;

                            return (
                                <div key={mainCategory._id} className="mb-3">
                                    {/* Main Category */}
                                    <div className="border rounded">
                                        <div
                                            className="p-3 bg-light d-flex justify-content-between align-items-center"
                                            style={{ cursor: "pointer" }}
                                            onClick={() => toggleMainCategory(mainCategory._id)}
                                        >
                                            <div className="d-flex align-items-center">
                                                <i
                                                    className={`fas ${isMainExpanded ? "fa-chevron-down" : "fa-chevron-right"
                                                        } me-2`}
                                                ></i>
                                                <span className="badge bg-primary me-2">Main</span>
                                                <strong>
                                                    {index + 1}. {mainCategory.name}
                                                </strong>
                                                <small className="text-muted ms-2">
                                                    ({getChildrenCount(mainCategory._id)} sub-categories)
                                                </small>
                                                {mainCategory.slug && (
                                                    <small className="text-muted ms-2 fst-italic">
                                                        /{mainCategory.slug}
                                                    </small>
                                                )}
                                            </div>
                                            <div onClick={(e) => e.stopPropagation()}>
                                                <button
                                                    className="btn btn-sm btn-outline-primary me-2"
                                                    onClick={() => handleEdit(mainCategory)}
                                                >
                                                    <i className="fas fa-edit me-1"></i>
                                                    Edit
                                                </button>
                                                <button
                                                    className="btn btn-sm btn-outline-danger"
                                                    onClick={() => handleDelete(mainCategory._id)}
                                                >
                                                    <i className="fas fa-trash me-1"></i>
                                                    Delete
                                                </button>
                                            </div>
                                        </div>

                                        {/* Sub Categories */}
                                        {isMainExpanded && filteredSubCategories.length > 0 && (
                                            <div className="border-top">
                                                {filteredSubCategories.map((subCategory) => {
                                                    const subSubCategories = getSortedSubSubCategories(subCategory._id);
                                                    const isSubExpanded = expandedSubCategories.has(subCategory._id);

                                                    return (
                                                        <div key={subCategory._id}>
                                                            <div
                                                                className="p-3 ps-5 bg-white d-flex justify-content-between align-items-center border-bottom"
                                                                style={{ cursor: "pointer" }}
                                                                onClick={() => toggleSubCategory(subCategory._id)}
                                                            >
                                                                <div className="d-flex align-items-center">
                                                                    {subSubCategories.length > 0 && (
                                                                        <i
                                                                            className={`fas ${isSubExpanded ? "fa-chevron-down" : "fa-chevron-right"
                                                                                } me-2`}
                                                                        ></i>
                                                                    )}
                                                                    <span className="badge bg-info me-2">Sub</span>
                                                                    <strong>{subCategory.name}</strong>
                                                                    <small className="text-muted ms-2">
                                                                        ({getChildrenCount(subCategory._id)} sub-sub categories)
                                                                    </small>
                                                                    {subCategory.slug && (
                                                                        <small className="text-muted ms-2 fst-italic">
                                                                            /{subCategory.slug}
                                                                        </small>
                                                                    )}
                                                                </div>
                                                                <div onClick={(e) => e.stopPropagation()}>
                                                                    <button
                                                                        className="btn btn-sm btn-outline-primary me-2"
                                                                        onClick={() => handleEdit(subCategory)}
                                                                    >
                                                                        <i className="fas fa-edit me-1"></i>
                                                                        Edit
                                                                    </button>
                                                                    <button
                                                                        className="btn btn-sm btn-outline-danger"
                                                                        onClick={() => handleDelete(subCategory._id)}
                                                                    >
                                                                        <i className="fas fa-trash me-1"></i>
                                                                        Delete
                                                                    </button>
                                                                </div>
                                                            </div>

                                                            {/* Sub-Sub Categories */}
                                                            {isSubExpanded && subSubCategories.length > 0 && (
                                                                <div className="bg-light">
                                                                    {subSubCategories.map((subSubCategory) => (
                                                                        <div
                                                                            key={subSubCategory._id}
                                                                            className="p-3 ps-5 ms-4 d-flex justify-content-between align-items-center border-bottom"
                                                                        >
                                                                            <div className="d-flex align-items-center">
                                                                                <span className="badge bg-success me-2">Sub-Sub</span>
                                                                                <strong>{subSubCategory.name}</strong>
                                                                                {subSubCategory.slug && (
                                                                                    <small className="text-muted ms-2 fst-italic">
                                                                                        /{subSubCategory.slug}
                                                                                    </small>
                                                                                )}
                                                                            </div>
                                                                            <div>
                                                                                <button
                                                                                    className="btn btn-sm btn-outline-primary me-2"
                                                                                    onClick={() => handleEdit(subSubCategory)}
                                                                                >
                                                                                    <i className="fas fa-edit me-1"></i>
                                                                                    Edit
                                                                                </button>
                                                                                <button
                                                                                    className="btn btn-sm btn-outline-danger"
                                                                                    onClick={() => handleDelete(subSubCategory._id)}
                                                                                >
                                                                                    <i className="fas fa-trash me-1"></i>
                                                                                    Delete
                                                                                </button>
                                                                            </div>
                                                                        </div>
                                                                    ))}
                                                                </div>
                                                            )}
                                                        </div>
                                                    );
                                                })}
                                            </div>
                                        )}

                                        {/* No Sub Categories Message */}
                                        {isMainExpanded && filteredSubCategories.length === 0 && (
                                            <div className="p-3 text-center text-muted">
                                                <i className="fas fa-info-circle me-1"></i>
                                                No sub-categories found
                                            </div>
                                        )}
                                    </div>
                                </div>
                            );
                        })
                    )}
                </div>
            </div>

            {/* Add/Edit Category Modal */}
            {showModal && (
                <div
                    className="modal"
                    style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}
                >
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">
                                    <i className={`fas ${editingId ? 'fa-edit' : 'fa-plus'} me-2`}></i>
                                    {editingId ? "Edit Category" : "Add New Category"}
                                </h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    onClick={() => setShowModal(false)}
                                ></button>
                            </div>
                            <form onSubmit={handleSubmit}>
                                <div className="modal-body">
                                    <div className="row">
                                        <div className="col-md-12">
                                            <div className="mb-3">
                                                <label className="form-label">Category Type</label>
                                                <select
                                                    className="form-select"
                                                    value={categoryType}
                                                    onChange={(e) => {
                                                        setCategoryType(e.target.value);
                                                        setSelectedMainCategory(null);
                                                        setSelectedSubCategory(null);
                                                        setParentId(null);
                                                    }}
                                                >
                                                    <option value="main">Main Category</option>
                                                    <option value="sub">Sub Category</option>
                                                    <option value="sub-sub">Sub-Sub Category</option>
                                                </select>
                                            </div>
                                        </div>
                                    </div>

                                    {/* Cascading Dropdowns */}
                                    {categoryType !== "main" && (
                                        <div className="row">
                                            <div className="col-md-6">
                                                <div className="mb-3">
                                                    <label className="form-label">
                                                        Select Main Category <span className="text-danger">*</span>
                                                    </label>
                                                    <select
                                                        className="form-select"
                                                        value={selectedMainCategory || ""}
                                                        onChange={(e) =>
                                                            handleMainCategoryChange(e.target.value || null)
                                                        }
                                                        required
                                                    >
                                                        <option value="">Choose Main Category</option>
                                                        {getMainCategories().map((cat) => (
                                                            <option key={cat._id} value={cat._id}>
                                                                {cat.name}
                                                            </option>
                                                        ))}
                                                    </select>
                                                </div>
                                            </div>

                                            {categoryType === "sub-sub" && (
                                                <div className="col-md-6">
                                                    <div className="mb-3">
                                                        <label className="form-label">
                                                            Select Sub Category <span className="text-danger">*</span>
                                                        </label>
                                                        <select
                                                            className="form-select"
                                                            value={selectedSubCategory || ""}
                                                            onChange={(e) =>
                                                                handleSubCategoryChange(e.target.value || null)
                                                            }
                                                            disabled={!selectedMainCategory}
                                                            required
                                                        >
                                                            <option value="">Choose Sub Category</option>
                                                            {selectedMainCategory &&
                                                                getSubCategories(selectedMainCategory).map((cat) => (
                                                                    <option key={cat._id} value={cat._id}>
                                                                        {cat.name}
                                                                    </option>
                                                                ))}
                                                        </select>
                                                    </div>
                                                </div>
                                            )}
                                        </div>
                                    )}

                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="mb-3">
                                                <label className="form-label">
                                                    Category Name <span className="text-danger">*</span>
                                                </label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={name}
                                                    onChange={(e) => setName(e.target.value)}
                                                    required
                                                    placeholder="Enter category name"
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="mb-3">
                                                <label className="form-label">Category Slug</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={slug}
                                                    onChange={(e) => setSlug(e.target.value)}
                                                    placeholder="Auto-generated from name"
                                                />
                                                <small className="text-muted">
                                                    Leave empty to auto-generate from name
                                                </small>
                                            </div>
                                        </div>
                                    </div>

                                    {/* Preview Section */}
                                    {name && (
                                        <div className="alert alert-info">
                                            <h6><i className="fas fa-eye me-1"></i>Preview:</h6>
                                            <strong>Category Path:</strong>{" "}
                                            {getCategoryPath({
                                                name,
                                                parentCategory:
                                                    categoryType === "main" ? null :
                                                        categoryType === "sub" ? selectedMainCategory :
                                                            selectedSubCategory
                                            }) || name}
                                            <br />
                                            <strong>Slug:</strong> /{slug || generateSlug(name)}
                                        </div>
                                    )}
                                </div>
                                <div className="modal-footer">
                                    <button
                                        type="button"
                                        className="btn btn-secondary"
                                        onClick={() => setShowModal(false)}
                                    >
                                        <i className="fas fa-times me-1"></i>
                                        Cancel
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn btn-primary"
                                        disabled={loading}
                                    >
                                        {loading ? (
                                            <>
                                                <span className="spinner-border spinner-border-sm me-1" role="status"></span>
                                                {editingId ? "Updating..." : "Saving..."}
                                            </>
                                        ) : (
                                            <>
                                                <i className={`fas ${editingId ? 'fa-save' : 'fa-plus'} me-1`}></i>
                                                {editingId ? "Update" : "Save"}
                                            </>
                                        )}
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}

            {/* Bulk Create Modal */}
            {showBulkModal && (
                <div
                    className="modal"
                    style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}
                >
                    <div className="modal-dialog modal-xl">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">
                                    <i className="fas fa-plus-circle me-2"></i>
                                    Bulk Create Category Hierarchy
                                </h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    onClick={() => setShowBulkModal(false)}
                                ></button>
                            </div>
                            <div className="modal-body">
                                {/* Main Category */}
                                <div className="card mb-3">
                                    <div className="card-header bg-primary text-white">
                                        <h6 className="mb-0">Main Category</h6>
                                    </div>
                                    <div className="card-body">
                                        <div className="row">
                                            <div className="col-md-6">
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    placeholder="Main category name"
                                                    value={bulkCategory.mainCategory.name}
                                                    onChange={(e) => updateMainCategory('name', e.target.value)}
                                                />
                                            </div>
                                            <div className="col-md-6">
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    placeholder="Slug (auto-generated)"
                                                    value={bulkCategory.mainCategory.slug}
                                                    onChange={(e) => updateMainCategory('slug', e.target.value)}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                {/* Subcategories */}
                                <div className="card">
                                    <div className="card-header bg-info text-white d-flex justify-content-between align-items-center">
                                        <h6 className="mb-0">Subcategories</h6>
                                        <button
                                            type="button"
                                            className="btn btn-sm btn-light"
                                            onClick={addSubcategory}
                                        >
                                            <i className="fas fa-plus me-1"></i>
                                            Add Subcategory
                                        </button>
                                    </div>
                                    <div className="card-body">
                                        {bulkCategory.subcategories.map((subcategory, subIndex) => (
                                            <div key={subIndex} className="border rounded p-3 mb-3">
                                                <div className="d-flex justify-content-between align-items-center mb-2">
                                                    <h6 className="text-info mb-0">Subcategory {subIndex + 1}</h6>
                                                    {bulkCategory.subcategories.length > 1 && (
                                                        <button
                                                            type="button"
                                                            className="btn btn-sm btn-outline-danger"
                                                            onClick={() => removeSubcategory(subIndex)}
                                                        >
                                                            <i className="fas fa-trash"></i>
                                                        </button>
                                                    )}
                                                </div>
                                                <div className="row mb-3">
                                                    <div className="col-md-6">
                                                        <input
                                                            type="text"
                                                            className="form-control"
                                                            placeholder="Subcategory name"
                                                            value={subcategory.name}
                                                            onChange={(e) => updateSubcategory(subIndex, 'name', e.target.value)}
                                                        />
                                                    </div>
                                                    <div className="col-md-6">
                                                        <input
                                                            type="text"
                                                            className="form-control"
                                                            placeholder="Slug (auto-generated)"
                                                            value={subcategory.slug}
                                                            onChange={(e) => updateSubcategory(subIndex, 'slug', e.target.value)}
                                                        />
                                                    </div>
                                                </div>

                                                {/* Sub-subcategories */}
                                                <div className="ms-3">
                                                    <div className="d-flex justify-content-between align-items-center mb-2">
                                                        <small className="text-muted">Sub-subcategories</small>
                                                        <button
                                                            type="button"
                                                            className="btn btn-sm btn-outline-success"
                                                            onClick={() => addSubSubcategory(subIndex)}
                                                        >
                                                            <i className="fas fa-plus me-1"></i>
                                                            Add Sub-subcategory
                                                        </button>
                                                    </div>
                                                    {subcategory.subSubcategories.map((subSub, subSubIndex) => (
                                                        <div key={subSubIndex} className="row mb-2">
                                                            <div className="col-md-5">
                                                                <input
                                                                    type="text"
                                                                    className="form-control form-control-sm"
                                                                    placeholder="Sub-subcategory name"
                                                                    value={subSub.name}
                                                                    onChange={(e) => updateSubSubcategory(subIndex, subSubIndex, 'name', e.target.value)}
                                                                />
                                                            </div>
                                                            <div className="col-md-5">
                                                                <input
                                                                    type="text"
                                                                    className="form-control form-control-sm"
                                                                    placeholder="Slug (auto-generated)"
                                                                    value={subSub.slug}
                                                                    onChange={(e) => updateSubSubcategory(subIndex, subSubIndex, 'slug', e.target.value)}
                                                                />
                                                            </div>
                                                            <div className="col-md-2">
                                                                {subcategory.subSubcategories.length > 1 && (
                                                                    <button
                                                                        type="button"
                                                                        className="btn btn-sm btn-outline-danger"
                                                                        onClick={() => removeSubSubcategory(subIndex, subSubIndex)}
                                                                    >
                                                                        <i className="fas fa-trash"></i>
                                                                    </button>
                                                                )}
                                                            </div>
                                                        </div>
                                                    ))}
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={() => setShowBulkModal(false)}
                                >
                                    <i className="fas fa-times me-1"></i>
                                    Cancel
                                </button>
                                <button
                                    type="button"
                                    className="btn btn-success"
                                    onClick={handleBulkSubmit}
                                    disabled={loading || !bulkCategory.mainCategory.name.trim()}
                                >
                                    {loading ? (
                                        <>
                                            <span className="spinner-border spinner-border-sm me-1" role="status"></span>
                                            Creating...
                                        </>
                                    ) : (
                                        <>
                                            <i className="fas fa-save me-1"></i>
                                            Create Hierarchy
                                        </>
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ManageCategories;
