import React, { useState, useEffect } from "react";

const ManageCategories = () => {
  // State for categories from backend
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Form states
  const [name, setName] = useState("");
  const [slug, setSlug] = useState("");
  const [isActive, setIsActive] = useState(true);
  const [categoryType, setCategoryType] = useState("main");
  const [selectedMainCategory, setSelectedMainCategory] = useState(null);
  const [selectedSubCategory, setSelectedSubCategory] = useState(null);
  const [parentId, setParentId] = useState(null);
  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isAddingSubCategory, setIsAddingSubCategory] = useState(false); // New state

  // UI states
  const [expandedMainCategories, setExpandedMainCategories] = useState(new Set());
  const [expandedSubCategories, setExpandedSubCategories] = useState(new Set());
  const [filterMainCategory, setFilterMainCategory] = useState("");
  const [filterSubCategory, setFilterSubCategory] = useState("");
  const [sortBy, setSortBy] = useState("name");
  const [sortOrder, setSortOrder] = useState("asc");

  // API base URL - adjust this to your backend URL
  const API_BASE_URL = "http://localhost:5000/api/admin";

  // Get auth token from localStorage (adjust based on your auth implementation)
  const getAuthToken = () => {
    return localStorage.getItem('adminToken') || localStorage.getItem('token');
  };

  // API Headers
  const getHeaders = () => ({
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${getAuthToken()}`
  });

  // Fetch categories from backend
  const fetchCategories = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/categories`, {
        headers: getHeaders()
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch categories: ${response.status}`);
      }

      const data = await response.json();
      setCategories(data);
      setError(null);
    } catch (err) {
      console.error("Error fetching categories:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Load categories on component mount
  useEffect(() => {
    fetchCategories();
  }, []);

  // Helper functions to work with backend data structure
  const getCategoryLevel = (type) => {
    switch (type) {
      case "main": return 1;
      case "sub": return 2;
      case "sub-sub": return 3;
      default: return 1;
    }
  };

  const getCategoryTypeFromLevel = (level) => {
    switch (level) {
      case 1: return "main";
      case 2: return "sub";
      case 3: return "sub-sub";
      default: return "main";
    }
  };

  const getMainCategories = () => {
    return categories.filter((cat) => cat.level === 1 && cat.isActive);
  };

  const getSubCategories = (mainCategoryId = null) => {
    if (mainCategoryId) {
      return categories.filter(
        (cat) => cat.level === 2 && cat.parentCategory === mainCategoryId && cat.isActive
      );
    }
    return categories.filter((cat) => cat.level === 2 && cat.isActive);
  };

  const getSubSubCategories = (subCategoryId = null) => {
    if (subCategoryId) {
      return categories.filter(
        (cat) => cat.level === 3 && cat.parentCategory === subCategoryId && cat.isActive
      );
    }
    return categories.filter((cat) => cat.level === 3 && cat.isActive);
  };

  const getCategoryPath = (category) => {
    const path = [];
    let current = category;

    while (current) {
      path.unshift(current.name);
      current = categories.find((cat) => cat._id === current.parentCategory);
    }

    return path.join(" > ");
  };

  const getChildrenCount = (categoryId) => {
    return categories.filter((cat) => cat.parentCategory === categoryId).length;
  };

  // Sort categories function
  const sortCategories = (categoryList) => {
    return [...categoryList].sort((a, b) => {
      let aValue, bValue;

      switch (sortBy) {
        case "name":
          aValue = a.name.toLowerCase();
          bValue = b.name.toLowerCase();
          break;
        case "id":
          aValue = a._id;
          bValue = b._id;
          break;
        case "children":
          aValue = getChildrenCount(a._id);
          bValue = getChildrenCount(b._id);
          break;
        default:
          aValue = a.name.toLowerCase();
          bValue = b.name.toLowerCase();
      }

      if (sortBy === "name") {
        const comparison = aValue.localeCompare(bValue);
        return sortOrder === "asc" ? comparison : -comparison;
      } else {
        if (aValue < bValue) return sortOrder === "asc" ? -1 : 1;
        if (aValue > bValue) return sortOrder === "asc" ? 1 : -1;
        return 0;
      }
    });
  };

  const getSortedSubCategories = (parentId) => {
    const subCats = getSubCategories(parentId);
    return sortCategories(subCats);
  };

  const getSortedSubSubCategories = (parentId) => {
    const subSubCats = getSubSubCategories(parentId);
    return sortCategories(subSubCats);
  };

  // Toggle functions for expand/collapse
  const toggleMainCategory = (categoryId) => {
    const newExpanded = new Set(expandedMainCategories);
    if (newExpanded.has(categoryId)) {
      newExpanded.delete(categoryId);
      const subCats = getSubCategories(categoryId);
      subCats.forEach((subCat) => {
        const newExpandedSub = new Set(expandedSubCategories);
        newExpandedSub.delete(subCat._id);
        setExpandedSubCategories(newExpandedSub);
      });
    } else {
      newExpanded.add(categoryId);
    }
    setExpandedMainCategories(newExpanded);
  };

  const toggleSubCategory = (categoryId) => {
    const newExpanded = new Set(expandedSubCategories);
    if (newExpanded.has(categoryId)) {
      newExpanded.delete(categoryId);
    } else {
      newExpanded.add(categoryId);
    }
    setExpandedSubCategories(newExpanded);
  };

  // Handle cascading dropdown changes
  const handleMainCategoryChange = (mainCatId) => {
    setSelectedMainCategory(mainCatId);
    setSelectedSubCategory(null);

    if (categoryType === "sub") {
      setParentId(mainCatId);
    } else if (categoryType === "sub-sub") {
      setParentId(null);
    }
  };

  const handleSubCategoryChange = (subCatId) => {
    setSelectedSubCategory(subCatId);

    if (categoryType === "sub-sub") {
      setParentId(subCatId);
    }
  };

  // Generate slug from name
  const generateSlug = (name) => {
    return name
      .toLowerCase()
      .replace(/\s+/g, '-')
      .replace(/[^a-z0-9-]/g, '');
  };

  // Handle name change and auto-generate slug
  const handleNameChange = (e) => {
    const newName = e.target.value;
    setName(newName);
    if (!editingId || isAddingSubCategory) { // Auto-generate slug for new categories or when adding sub-categories
      setSlug(generateSlug(newName));
    }
  };

  // Form handlers
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name.trim()) {
      alert("Category name is required");
      return;
    }

    const level = getCategoryLevel(categoryType);
    let finalParentId = null;

    if (categoryType === "sub" && selectedMainCategory) {
      finalParentId = selectedMainCategory;
    } else if (categoryType === "sub-sub" && selectedSubCategory) {
      finalParentId = selectedSubCategory;
    }

    const categoryData = {
      name: name.trim(),
      slug: slug.trim() || generateSlug(name.trim()),
      parentCategory: finalParentId,
      level,
      isActive
    };

    try {
      let response;

      // If we're editing but adding sub-category, OR if no editingId, create new category
      if (!editingId || isAddingSubCategory) {
        // Add new category
        response = await fetch(`${API_BASE_URL}/add-category`, {
          method: 'POST',
          headers: getHeaders(),
          body: JSON.stringify(categoryData)
        });
      } else {
        // Update existing category (only when actually editing the same category)
        response = await fetch(`${API_BASE_URL}/category/${editingId}`, {
          method: 'PUT',
          headers: getHeaders(),
          body: JSON.stringify(categoryData)
        });
      }

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || `Failed to ${editingId && !isAddingSubCategory ? 'update' : 'add'} category`);
      }

      const result = await response.json();

      // Show success message
      alert(result.message || `Category ${editingId && !isAddingSubCategory ? 'updated' : 'added'} successfully`);

      // Reset form and close modal
      resetForm();
      setShowModal(false);

      // Refresh categories
      await fetchCategories();

    } catch (err) {
      console.error("Error saving category:", err);
      alert(err.message || "Failed to save category");
    }
  };

  const resetForm = () => {
    setName("");
    setSlug("");
    setIsActive(true);
    setCategoryType("main");
    setSelectedMainCategory(null);
    setSelectedSubCategory(null);
    setParentId(null);
    setEditingId(null);
    setIsAddingSubCategory(false);
  };

  // Modified handleEdit function
  const handleEdit = (category) => {
    setName(category.name);
    setSlug(category.slug);
    setIsActive(category.isActive);
    setCategoryType(getCategoryTypeFromLevel(category.level));

    if (category.level === 2) {
      setSelectedMainCategory(category.parentCategory);
      setSelectedSubCategory(null);
      setParentId(category.parentCategory);
    } else if (category.level === 3) {
      const parentSub = categories.find((cat) => cat._id === category.parentCategory);
      if (parentSub) {
        setSelectedMainCategory(parentSub.parentCategory);
        setSelectedSubCategory(parentSub._id);
        setParentId(parentSub._id);
      }
    } else {
      setSelectedMainCategory(null);
      setSelectedSubCategory(null);
      setParentId(null);
    }

    setEditingId(category._id);
    setIsAddingSubCategory(false); // This is a regular edit
    setShowModal(true);
  };

  // New function to handle adding sub-categories
  const handleAddSubCategory = (parentCategory, subType = "sub") => {
    resetForm();
    
    if (subType === "sub") {
      // Adding sub-category under main category
      setCategoryType("sub");
      setSelectedMainCategory(parentCategory._id);
      setParentId(parentCategory._id);
    } else if (subType === "sub-sub") {
      // Adding sub-sub category under sub category
      setCategoryType("sub-sub");
      const parentMain = categories.find(cat => cat._id === parentCategory.parentCategory);
      if (parentMain) {
        setSelectedMainCategory(parentMain._id);
        setSelectedSubCategory(parentCategory._id);
        setParentId(parentCategory._id);
      }
    }
    
    setIsAddingSubCategory(true);
    setEditingId(null); // No editing, this is new
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    const hasChildren = getChildrenCount(id) > 0;

    if (hasChildren) {
      if (!window.confirm("This category has subcategories. Deleting it will also delete all subcategories. Are you sure?")) {
        return;
      }
    } else {
      if (!window.confirm("Are you sure you want to delete this category?")) {
        return;
      }
    }

    try {
      const response = await fetch(`${API_BASE_URL}/category/${id}`, {
        method: 'DELETE',
        headers: getHeaders()
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to delete category');
      }

      const result = await response.json();
      alert(result.message || 'Category deleted successfully');

      // Refresh categories
      await fetchCategories();

    } catch (err) {
      console.error("Error deleting category:", err);
      alert(err.message || "Failed to delete category");
    }
  };

  // Get filtered main categories
  const getFilteredMainCategories = () => {
    let mainCats = getMainCategories();

    if (filterMainCategory) {
      mainCats = mainCats.filter((cat) => cat._id === filterMainCategory);
    }

    return sortCategories(mainCats);
  };

  // Loading state
  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Loading categories...</p>
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger" role="alert">
          <h4 className="alert-heading">Error!</h4>
          <p>{error}</p>
          <button
            className="btn btn-outline-danger"
            onClick={fetchCategories}
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-5">
        <h2 className="mb-0">Manage Categories</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
        >
          Add New Category
        </button>
      </div>

      {/* Category Statistics */}
      <div className="row mb-4">
        <div className="col-md-4">
          <div className="card text-center">
            <div className="card-body">
              <h5 className="card-title">Main Categories</h5>
              <h3 className="text-primary">
                {categories.filter((cat) => cat.level === 1).length}
              </h3>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-center">
            <div className="card-body">
              <h5 className="card-title">Sub Categories</h5>
              <h3 className="text-info">
                {categories.filter((cat) => cat.level === 2).length}
              </h3>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-center">
            <div className="card-body">
              <h5 className="card-title">Sub-Sub Categories</h5>
              <h3 className="text-success">
                {categories.filter((cat) => cat.level === 3).length}
              </h3>
            </div>
          </div>
        </div>
      </div>

      {/* Filter and Sort Section */}
      <div className="card mb-4">
        <div className="card-header">
          <h5 className="mb-0 text-center">Filter & Sort Categories</h5>
        </div>
        <div className="card-body">
          <div className="row">
            {/* Filter Section */}
            <div className="col-md-8 text-center">
              <h6 className="text-muted mb-3">Filter Options</h6>
              <div className="row">
                <div className="col-md-6">
                  <label className="form-label">Main Category</label>
                  <select
                    className="form-select"
                    value={filterMainCategory}
                    onChange={(e) => {
                      setFilterMainCategory(e.target.value);
                      setFilterSubCategory("");
                    }}
                  >
                    <option value="">All Main Categories</option>
                    {getMainCategories().map((cat) => (
                      <option key={cat._id} value={cat._id}>
                        {cat.name}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="col-md-6">
                  <label className="form-label">Sub Category</label>
                  <select
                    className="form-select"
                    value={filterSubCategory}
                    onChange={(e) => setFilterSubCategory(e.target.value)}
                    disabled={!filterMainCategory}
                  >
                    <option value="">All Sub Categories</option>
                    {filterMainCategory &&
                      getSubCategories(filterMainCategory).map((cat) => (
                        <option key={cat._id} value={cat._id}>
                          {cat.name}
                        </option>
                      ))}
                  </select>
                </div>
              </div>
            </div>

            {/* Sort Section */}
            <div className="col-md-4 text-center">
              <h6 className="text-muted mb-3">Sort Options</h6>
              <div className="row">
                <div className="col-md-12">
                  <label className="form-label">Sort Order</label>
                  <select
                    className="form-select"
                    value={sortOrder}
                    onChange={(e) => setSortOrder(e.target.value)}
                  >
                    <option value="asc">A to Z</option>
                    <option value="desc">Z to A</option>
                  </select>
                </div>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="row mt-3">
            <div className="col-md-12">
              <div className="d-flex gap-2">
                <button
                  className="btn btn-outline-secondary"
                  onClick={() => {
                    setFilterMainCategory("");
                    setFilterSubCategory("");
                  }}
                >
                  <i className="fas fa-filter me-1"></i>
                  Clear Filters
                </button>
                <button
                  className="btn btn-outline-info"
                  onClick={() => {
                    setSortBy("name");
                    setSortOrder("asc");
                  }}
                >
                  <i className="fas fa-sort me-1"></i>
                  Reset Sorting
                </button>
                <button
                  className="btn btn-outline-success"
                  onClick={fetchCategories}
                >
                  <i className="fas fa-sync me-1"></i>
                  Refresh
                </button>
                <div className="ms-auto">
                  <small className="text-muted">
                    Showing {getFilteredMainCategories().length} main categories
                    {sortBy !== "name" || sortOrder !== "asc"
                      ? ` (sorted by ${sortBy} ${sortOrder === "asc" ? "↑" : "↓"})`
                      : ""}
                  </small>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Categories Display */}
      <div className="card">
        <div className="card-header">
          <h5 className="mb-0 text-center">Categories</h5>
        </div>
        <div className="card-body">
          {getFilteredMainCategories().length === 0 ? (
            <div className="text-center text-muted py-4">
              No categories found matching the current filters.
            </div>
          ) : (
            getFilteredMainCategories().map((mainCategory, index) => {
              const subCategories = getSortedSubCategories(mainCategory._id);
              const isMainExpanded = expandedMainCategories.has(mainCategory._id);

              const filteredSubCategories = filterSubCategory
                ? subCategories.filter((sub) => sub._id === filterSubCategory)
                : subCategories;

              return (
                <div key={mainCategory._id} className="mb-3">
                  {/* Main Category */}
                  <div className="border rounded">
                    <div
                      className="p-3 bg-light d-flex justify-content-between align-items-center"
                      style={{ cursor: "pointer" }}
                      onClick={() => toggleMainCategory(mainCategory._id)}
                    >
                      <div className="d-flex align-items-center">
                        <i
                          className={`fas ${isMainExpanded ? "fa-chevron-down" : "fa-chevron-right"
                            } me-2`}
                        ></i>
                        <span className="badge bg-primary me-2">Main</span>
                        <strong>
                          {index + 1}. {mainCategory.name}
                        </strong>
                        <small className="text-muted ms-2">
                          ({getChildrenCount(mainCategory._id)} sub-categories)
                        </small>
                        {mainCategory.slug && (
                          <small className="text-muted ms-2">
                            [/{mainCategory.slug}]
                          </small>
                        )}
                      </div>
                      <div>
                        <span className={`badge ${mainCategory.isActive ? 'bg-success' : 'bg-secondary'} me-2`}>
                          {mainCategory.isActive ? 'Active' : 'Inactive'}
                        </span>
                        <button
                          className="btn btn-sm btn-outline-success me-2"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleAddSubCategory(mainCategory, "sub");
                          }}
                          title="Add Sub Category"
                        >
                          Add Sub
                        </button>
                        <button
                          className="btn btn-sm btn-outline-primary me-2"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleEdit(mainCategory);
                          }}
                        >
                          Edit
                        </button>
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDelete(mainCategory._id);
                          }}
                        >
                          Delete
                        </button>
                      </div>
                    </div>

                    {/* Sub Categories */}
                    {isMainExpanded && filteredSubCategories.length > 0 && (
                      <div className="border-top">
                        {filteredSubCategories.map((subCategory) => {
                          const subSubCategories = getSortedSubSubCategories(subCategory._id);
                          const isSubExpanded = expandedSubCategories.has(subCategory._id);

                          return (
                            <div key={subCategory._id}>
                              <div
                                className="p-3 ps-5 bg-white d-flex justify-content-between align-items-center border-bottom"
                                style={{ cursor: "pointer" }}
                                onClick={() => toggleSubCategory(subCategory._id)}
                              >
                                <div className="d-flex align-items-center">
                                  <span className="badge bg-info me-2">Sub</span>
                                  <strong>{subCategory.name}</strong>
                                  <small className="text-muted ms-2">
                                    ({getChildrenCount(subCategory._id)} sub-sub categories)
                                  </small>
                                  {subCategory.slug && (
                                    <small className="text-muted ms-2">
                                      [/{subCategory.slug}]
                                    </small>
                                  )}
                                </div>
                                <div>
                                  <span className={`badge ${subCategory.isActive ? 'bg-success' : 'bg-secondary'} me-2`}>
                                    {subCategory.isActive ? 'Active' : 'Inactive'}
                                  </span>
                                  <button
                                    className="btn btn-sm btn-outline-success me-2"
                                    onClick={(e) => {
                                      e.stopPropagation();
                                      handleAddSubCategory(subCategory, "sub-sub");
                                    }}
                                    title="Add Sub-Sub Category"
                                  >
                                    Add Sub-Sub
                                  </button>
                                  <button
                                    className="btn btn-sm btn-outline-primary me-2"
                                    onClick={(e) => {
                                      e.stopPropagation();
                                      handleEdit(subCategory);
                                    }}
                                  >
                                    Edit
                                  </button>
                                  <button
                                    className="btn btn-sm btn-outline-danger"
                                    onClick={(e) => {
                                      e.stopPropagation();
                                      handleDelete(subCategory._id);
                                    }}
                                  >
                                    Delete
                                  </button>
                                </div>
                              </div>

                              {/* Sub-Sub Categories */}
                              {isSubExpanded && subSubCategories.length > 0 && (
                                <div className="bg-light">
                                  {subSubCategories.map((subSubCategory) => (
                                    <div
                                      key={subSubCategory._id}
                                      className="p-3 ps-5 ms-4 d-flex justify-content-between align-items-center border-bottom"
                                    >
                                      <div className="d-flex align-items-center">
                                        <span className="badge bg-success me-2">Sub-Sub</span>
                                        <strong>{subSubCategory.name}</strong>
                                        {subSubCategory.slug && (
                                          <small className="text-muted ms-2">
                                            [/{subSubCategory.slug}]
                                          </small>
                                        )}
                                      </div>
                                      <div>
                                        <span className={`badge ${subSubCategory.isActive ? 'bg-success' : 'bg-secondary'} me-2`}>
                                          {subSubCategory.isActive ? 'Active' : 'Inactive'}
                                        </span>
                                        <button
                                          className="btn btn-sm btn-outline-primary me-2"
                                          onClick={() => handleEdit(subSubCategory)}
                                        >
                                          Edit
                                        </button>
                                        <button
                                          className="btn btn-sm btn-outline-danger"
                                          onClick={() => handleDelete(subSubCategory._id)}
                                        >
                                          Delete
                                        </button>
                                      </div>
                                    </div>
                                  ))}
                                </div>
                              )}
                            </div>
                          );
                        })}
                      </div>
                    )}
                  </div>
                </div>
              );
            })
          )}
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div
          className="modal"
          style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}
        >
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {editingId && !isAddingSubCategory 
                    ? "Edit Category" 
                    : isAddingSubCategory 
                      ? `Add ${categoryType === 'sub' ? 'Sub' : 'Sub-Sub'} Category`
                      : "Add New Category"}
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <form onSubmit={handleSubmit}>
                  <div className="row">
                    <div className="col-md-12">
                      <div className="mb-3">
                        <label className="form-label">Category Type</label>
                        <select
                          className="form-select"
                          value={categoryType}
                          onChange={(e) => {
                            setCategoryType(e.target.value);
                            setSelectedMainCategory(null);
                            setSelectedSubCategory(null);
                            setParentId(null);
                          }}
                          disabled={isAddingSubCategory} // Disable when adding sub-category
                        >
                          <option value="main">Main Category</option>
                          <option value="sub">Sub Category</option>
                          <option value="sub-sub">Sub-Sub Category</option>
                        </select>
                        {isAddingSubCategory && (
                          <small className="text-muted">
                            Category type is auto-selected when adding sub-categories
                          </small>
                        )}
                      </div>
                    </div>
                  </div>

                  {/* Cascading Dropdowns */}
                  {categoryType !== "main" && (
                    <div className="row">
                      <div className="col-md-6">
                        <div className="mb-3">
                          <label className="form-label">Select Main Category</label>
                          <select
                            className="form-select"
                            value={selectedMainCategory || ""}
                            onChange={(e) =>
                              handleMainCategoryChange(e.target.value || null)
                            }
                            required
                          >
                            <option value="">Choose Main Category</option>
                            {getMainCategories().map((cat) => (
                              <option key={cat._id} value={cat._id}>
                                {cat.name}
                              </option>
                            ))}
                          </select>
                        </div>
                      </div>

                      {categoryType === "sub-sub" && (
                        <div className="col-md-6">
                          <div className="mb-3">
                            <label className="form-label">Select Sub Category</label>
                            <select
                              className="form-select"
                              value={selectedSubCategory || ""}
                              onChange={(e) =>
                                handleSubCategoryChange(e.target.value || null)
                              }
                              disabled={!selectedMainCategory}
                              required
                            >
                              <option value="">Choose Sub Category</option>
                              {selectedMainCategory &&
                                getSubCategories(selectedMainCategory).map((cat) => (
                                  <option key={cat._id} value={cat._id}>
                                    {cat.name}
                                  </option>
                                ))}
                            </select>
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  <div className="row">
                    <div className="col-md-6">
                      <div className="mb-3">
                        <label className="form-label">Category Name *</label>
                        <input
                          type="text"
                          className="form-control"
                          value={name}
                          onChange={handleNameChange}
                          required
                        />
                      </div>
                    </div>
                    <div className="col-md-6">
                      <div className="mb-3">
                        <label className="form-label">Slug</label>
                        <input
                          type="text"
                          className="form-control"
                          value={slug}
                          onChange={(e) => setSlug(e.target.value)}
                          placeholder="Auto-generated from name"
                        />
                        <small className="text-muted">
                          Leave empty to auto-generate from name
                        </small>
                      </div>
                    </div>
                  </div>

                  <div className="mb-3">
                    <div className="form-check">
                      <input
                        className="form-check-input"
                        type="checkbox"
                        id="isActive"
                        checked={isActive}
                        onChange={(e) => setIsActive(e.target.checked)}
                      />
                      <label className="form-check-label" htmlFor="isActive">
                        Active Category
                      </label>
                    </div>
                  </div>

                  {/* Preview Section */}
                  {name && (
                    <div className="alert alert-info">
                      <h6>Preview:</h6>
                      <strong>Category Path:</strong>{" "}
                      {categoryType === "main"
                        ? name
                        : categoryType === "sub" && selectedMainCategory
                          ? categories.find(
                            (cat) => cat._id === selectedMainCategory
                          )?.name +
                          " > " +
                          name
                          : categoryType === "sub-sub" &&
                            selectedMainCategory &&
                            selectedSubCategory
                            ? categories.find(
                              (cat) => cat._id === selectedMainCategory
                            )?.name +
                            " > " +
                            categories.find(
                              (cat) => cat._id === selectedSubCategory
                            )?.name +
                            " > " +
                            name
                            : "Please select parent categories"}
                    </div>
                  )}
                </form>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowModal(false)}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="btn btn-primary"
                  onClick={handleSubmit}
                >
                  {editingId ? "Update" : "Save"}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageCategories;
