import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const UpdateCourse = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    const [course, setCourse] = useState({
        title: "",
        description: "",
        price: "",
        thumbnailUrl: "",
    });
    const [message, setMessage] = useState("");

    // Fetch course data by ID to prefill form
    useEffect(() => {
        const fetchCourse = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/admin/course/${id}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
                    },
                });
                setCourse(res.data.course || res.data);
            } catch (error) {
                console.error("Error fetching course:", error);
                setMessage("Failed to load course details");
            }
        };
        fetchCourse();
    }, [id]);

    // Handle form change
    const handleChange = (e) => {
        setCourse({ ...course, [e.target.name]: e.target.value });
    };

    // Handle form submit to update
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.put(`http://localhost:5000/api/admin/course/${id}`, course, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
                },
            });
            if (res.data.message) {
                setMessage("✅ Course updated successfully");
                // Optionally redirect after short delay
                setTimeout(() => {
                    navigate("/admin/manage-courses");
                }, 1500);
            }
        } catch (error) {
            console.error("Update error:", error);
            setMessage("❌ Failed to update course");
        }
    };

    return (
        <div>
            <h2>Update Course</h2>
            {message && <p>{message}</p>}
            <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px", maxWidth: "400px" }}>
                <input
                    type="text"
                    name="title"
                    placeholder="Course Title"
                    value={course.title}
                    onChange={handleChange}
                    required
                />
                <textarea
                    name="description"
                    placeholder="Course Description"
                    value={course.description}
                    onChange={handleChange}
                    required
                />
                <input
                    type="number"
                    name="price"
                    placeholder="Course Price"
                    value={course.price}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="thumbnailUrl"
                    placeholder="Thumbnail URL"
                    value={course.thumbnailUrl}
                    onChange={handleChange}
                />
                <button type="submit">Update Course</button>
            </form>
        </div>
    );
};

export default UpdateCourse;
