import React, { useState } from "react";

function AdminLogin() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState(""); // Use message for both success/error

    const handleLogin = (e) => {
        e.preventDefault();
        setMessage(""); // Clear previous messages

        // Simulate login logic without API call
        if (email === "admin@example.com" && password === "password") {
            setMessage("Login successful!");
            // In a real app, you would redirect here or set a global auth state
            // For this simple example, we just show a success message.
            console.log("Admin Login Success!");
        } else {
            setMessage("Invalid email or password.");
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100 font-inter">
            <div className="bg-white shadow-lg p-8 rounded-lg w-full max-w-sm">
                <h2 className="text-2xl font-semibold text-center mb-6 text-gray-800">Admin Login</h2>

                {message && (
                    <div className={`p-3 mb-4 rounded-md text-sm ${
                        message.includes("successful") ? "bg-green-100 text-green-700" : "bg-red-100 text-red-700"
                    }`}>
                        {message}
                    </div>
                )}

                <form onSubmit={handleLogin}>
                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-medium mb-2" htmlFor="email">
                            Email address
                        </label>
                        <input
                            type="email"
                            id="email"
                            className="shadow-sm appearance-none border rounded-md w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            placeholder="Enter email"
                        />
                    </div>
                    <div className="mb-6">
                        <label className="block text-gray-700 text-sm font-medium mb-2" htmlFor="password">
                            Password
                        </label>
                        <input
                            type="password"
                            id="password"
                            className="shadow-sm appearance-none border rounded-md w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            placeholder="Enter password"
                        />
                    </div>
                    <button
                        type="submit"
                        className="bg-gray-800 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded-md w-full focus:outline-none focus:shadow-outline transition duration-200 ease-in-out"
                    >
                        Login
                    </button>
                </form>
            </div>
        </div>
    );
}

export default AdminLogin;
