import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageCategories = () => {
    const [categories, setCategories] = useState([]);
    const [name, setName] = useState("");
    const [slug, setSlug] = useState("");
    const [isActive, setIsActive] = useState(true);
    const [editingId, setEditingId] = useState(null);
    const [showModal, setShowModal] = useState(false);

    // Fetch categories from backend
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const token = localStorage.getItem("adminToken");
                const res = await axios.get("http://localhost:5000/api/admin/categories", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setCategories(res.data);
            } catch (err) {
                console.error("Error fetching categories", err);
            }
        };

        fetchCategories();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem("adminToken");
        const categoryPayload = {
            name,
            slug: slug || name.toLowerCase().replace(/\s+/g, "-"),
        };

        try {
            if (editingId) {
                // (Optional) Handle update logic here
                await axios.put(`http://localhost:5000/api/admin/category/${editingId}`, categoryPayload, {
                    headers: { Authorization: `Bearer ${token}` }
                });


                // Add to frontend state
                const newCategory = res.data;
                setCategories([...categories, { ...newCategory, isActive: true }]);
                alert("Category added successfully!");
            }

            resetForm();
            setShowModal(false);
        } catch (err) {
            console.error("Error submitting category:", err);
            alert("Failed to submit category");
        }
    };

    const resetForm = () => {
        setName("");
        setSlug("");
        setIsActive(true);
        setEditingId(null);
    };

    const handleEdit = (category) => {
        setName(category.name);
        setSlug(category.slug);
        setIsActive(category.isActive);
        setEditingId(category._id); // for future update
        setShowModal(true);
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Are you sure you want to delete this category?")) return;

        try {
            const token = localStorage.getItem("adminToken");
            await axios.delete(`http://localhost:5000/api/admin/category/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setCategories(categories.filter((cat) => cat._id !== id));
        } catch (err) {
            console.error("Delete error:", err);
            alert("Failed to delete category");
        }
    };

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-5">
                <h2 className="mb-0">Manage Categories</h2>
                <button
                    className="btn btn-primary"
                    onClick={() => {
                        resetForm();
                        setShowModal(true);
                    }}
                >
                    Add New Category
                </button>
            </div>

            <table className="table table-striped table-bordered">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Slug</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {categories.map((category, index) => (
                        <tr key={category._id}>
                            <td>{index + 1}</td>
                            <td>{category.name}</td>
                            <td>{category.slug}</td>
                            <td>
                                <button className="btn btn-sm btn-outline-primary me-2" onClick={() => handleEdit(category)}>
                                    Edit
                                </button>
                                <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(category._id)}>
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Modal */}
            {showModal && (
                <div className="modal" style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">{editingId ? "Edit Category" : "Add New Category"}</h5>
                                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={handleSubmit}>
                                    <div className="mb-3">
                                        <label className="form-label">Category Name</label>
                                        <input type="text" className="form-control" value={name} onChange={(e) => setName(e.target.value)} required />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Slug</label>
                                        <input type="text" className="form-control" value={slug} onChange={(e) => setSlug(e.target.value)} placeholder="Auto-generated if empty" />
                                    </div>
                                    <div className="modal-footer">
                                        <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>Cancel</button>
                                        <button type="submit" className="btn btn-primary">{editingId ? "Update" : "Save"}</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ManageCategories;
