import { useEffect, useState } from "react";
import axios from "axios";
import { FaTrash, FaEdit, Fa } from "react-icons/fa";
import { useNavigate } from "react-router-dom";  // <-- import here

const ManageCourses = () => {
    const navigate = useNavigate();  // <-- inside component

    const [courses, setCourses] = useState([]);
    const [message, setMessage] = useState("");

    const fetchCourses = async () => {
        try {
            const res = await axios.get("http://localhost:5000/api/admin/courses", {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
                },
            });
            console.log("Courses response:", res.data);

            setCourses(res.data.courses || res.data);
        } catch (error) {
            console.error("Error fetching courses:", error);
        }
    };

    const deleteCourse = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/admin/course/${id}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
                },
            });
            setMessage("✅ Course deleted");
            fetchCourses();
        } catch (error) {
            console.error("Delete error:", error);
            setMessage("❌ Failed to delete");
        }
    };

    useEffect(() => {
        fetchCourses();
    }, []);

    return (
        <div>
            <h2>All Courses</h2>
            {message && <p>{message}</p>}
            {!courses || courses.length === 0 ? (
                <p>No courses found</p>
            ) : (
                <table border="1" cellPadding="10">
                    <thead>
                        <tr>
                            <th>Thumbnail</th>
                            <th>Title</th>
                            <th>Price</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {courses.map((course) => (
                            <tr key={course._id}>
                                <td>
                                    {course.thumbnailUrl ? (
                                        <img
                                            src={course.thumbnailUrl}
                                            alt={course.title}
                                            style={{ width: "80px", height: "auto", objectFit: "cover" }}
                                        />
                                    ) : (
                                        "No Image"
                                    )}
                                </td>
                                <td>{course.title}</td>
                                <td>₹{course.price}</td>
                                <td>
                                    <button onClick={() => navigate(`/admin/update-course/${course._id}`)}>
                                        <FaEdit />
                                    </button>
                                    <button onClick={() => deleteCourse(course._id)}>
                                        <FaTrash />
                                    </button>
                                    <button onClick={() => navigate(`/admin/course-detail/${course._id}`)} title="View Details">
                                        <FaEye />
                                    </button>

                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default ManageCourses;
