import React, { useState, useEffect, useRef } from 'react';

const ManageOrders = () => {
    const [orders, setOrders] = useState([]);
    const [filteredOrders, setFilteredOrders] = useState([]);
    const [statusFilter, setStatusFilter] = useState('all');
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [showRefundModal, setShowRefundModal] = useState(false);
    const [refundReason, setRefundReason] = useState('');
    const modalRef = useRef(null);
    const refundModalRef = useRef(null);

    // Mock data for orders
    useEffect(() => {
        const mockOrders = [
            {
                id: 'ORD-001',
                studentName: 'John Smith',
                email: 'john.smith@email.com',
                course: 'Advanced Mathematics',
                orderDate: '2024-06-01',
                amount: 299.99,
                status: 'completed',
                paymentMethod: 'Credit Card',
                phone: '+1-555-0123',
                lastUpdated: '2024-06-01T10:30:00Z'
            },
            {
                id: 'ORD-002',
                studentName: 'Sarah Johnson',
                email: 'sarah.j@email.com',
                course: 'Digital Marketing Fundamentals',
                orderDate: '2024-06-05',
                amount: 199.99,
                status: 'pending',
                paymentMethod: 'PayPal',
                phone: '+1-555-0124',
                lastUpdated: '2024-06-05T14:20:00Z'
            },
            {
                id: 'ORD-003',
                studentName: 'Michael Brown',
                email: 'mike.brown@email.com',
                course: 'Web Development Bootcamp',
                orderDate: '2024-06-08',
                amount: 499.99,
                status: 'processing',
                paymentMethod: 'Bank Transfer',
                phone: '+1-555-0125',
                lastUpdated: '2024-06-08T16:45:00Z'
            },
            {
                id: 'ORD-004',
                studentName: 'Emily Davis',
                email: 'emily.davis@email.com',
                course: 'Data Science Essentials',
                orderDate: '2024-06-10',
                amount: 349.99,
                status: 'cancelled',
                paymentMethod: 'Credit Card',
                phone: '+1-555-0126',
                lastUpdated: '2024-06-10T12:15:00Z'
            },
            {
                id: 'ORD-005',
                studentName: 'David Wilson',
                email: 'david.w@email.com',
                course: 'Graphic Design Masterclass',
                orderDate: '2024-06-09',
                amount: 279.99,
                status: 'completed',
                paymentMethod: 'PayPal',
                phone: '+1-555-0127',
                lastUpdated: '2024-06-09T09:20:00Z'
            }
        ];
        setOrders(mockOrders);
        setFilteredOrders(mockOrders);
    }, []);

    // Filter orders based on status and search term
    useEffect(() => {
        let filtered = orders;

        if (statusFilter !== 'all') {
            filtered = filtered.filter(order => order.status === statusFilter);
        }

        if (searchTerm) {
            filtered = filtered.filter(order =>
                order.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
                order.studentName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                order.course.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        setFilteredOrders(filtered);
    }, [statusFilter, searchTerm, orders]);

    const getStatusBadge = (status) => {
        const statusClasses = {
            completed: 'bg-success',
            pending: 'bg-warning',
            processing: 'bg-info',
            cancelled: 'bg-danger',
            refunded: 'bg-secondary'
        };
        return statusClasses[status] || 'bg-secondary';
    };

    const getAvailableActions = (status) => {
        switch (status) {
            case 'pending':
                return [
                    { action: 'processing', label: 'Mark Processing', className: 'dropdown-item' },
                    { action: 'completed', label: 'Mark Completed', className: 'dropdown-item' },
                    { action: 'cancelled', label: 'Cancel Order', className: 'dropdown-item text-danger' }
                ];
            case 'processing':
                return [
                    { action: 'completed', label: 'Mark Completed', className: 'dropdown-item' },
                    { action: 'cancelled', label: 'Cancel Order', className: 'dropdown-item text-danger' }
                ];
            case 'completed':
                return [
                    { action: 'refund', label: 'Issue Refund', className: 'dropdown-item text-warning' },
                    { action: 'download', label: 'Download Receipt', className: 'dropdown-item' }
                ];
            case 'cancelled':
                return [
                    { action: 'reactivate', label: 'Reactivate Order', className: 'dropdown-item text-success' }
                ];
            case 'refunded':
                return [
                    { action: 'download', label: 'Download Receipt', className: 'dropdown-item' }
                ];
            default:
                return [];
        }
    };

    const updateOrderStatus = (orderId, newStatus) => {
        setOrders(orders.map(order =>
            order.id === orderId 
                ? { 
                    ...order, 
                    status: newStatus,
                    lastUpdated: new Date().toISOString()
                } 
                : order
        ));
        setShowModal(false);
    };

    const handleActionClick = (orderId, action) => {
        switch (action) {
            case 'refund':
                setSelectedOrder(orders.find(o => o.id === orderId));
                setShowRefundModal(true);
                break;
            case 'download':
                alert(`Downloading receipt for order ${orderId}`);
                break;
            case 'reactivate':
                updateOrderStatus(orderId, 'pending');
                break;
            default:
                updateOrderStatus(orderId, action);
        }
    };

    const handleRefund = () => {
        if (selectedOrder && refundReason.trim()) {
            updateOrderStatus(selectedOrder.id, 'refunded');
            setShowRefundModal(false);
            setRefundReason('');
            alert(`Refund processed for ${selectedOrder.id}. Reason: ${refundReason}`);
        }
    };

    const handleViewOrder = (order) => {
        setSelectedOrder(order);
        setShowModal(true);
    };

    const closeModal = () => {
        setShowModal(false);
        setSelectedOrder(null);
    };

    const closeRefundModal = () => {
        setShowRefundModal(false);
        setRefundReason('');
        setSelectedOrder(null);
    };

    const handleModalBackdropClick = (e) => {
        if (e.target === e.currentTarget) {
            closeModal();
        }
    };

    const handleRefundModalBackdropClick = (e) => {
        if (e.target === e.currentTarget) {
            closeRefundModal();
        }
    };

    const getOrderStats = () => {
        const stats = {
            total: orders.length,
            completed: orders.filter(o => o.status === 'completed').length,
            pending: orders.filter(o => o.status === 'pending').length,
            processing: orders.filter(o => o.status === 'processing').length,
            cancelled: orders.filter(o => o.status === 'cancelled').length,
            refunded: orders.filter(o => o.status === 'refunded').length
        };
        return stats;
    };

    const stats = getOrderStats();

    return (
        <>
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/bootstrap-icons/1.10.0/font/bootstrap-icons.min.css" />
            <link href="https://cdnjs.cloudflare.com/ajax/libs/bootstrap/5.3.0/css/bootstrap.min.css" rel="stylesheet" />
            
            <div className="container-fluid py-4">
                {/* Header */}
                <div className="row mb-4">
                    <div className="col-12">
                        <div className="d-flex flex-column flex-md-row justify-content-between align-items-start align-items-md-center">
                            <div>
                                <h1 className="h3 mb-1">Order Management</h1>
                                <p className="text-muted mb-3 mb-md-0">Manage and track all course orders</p>
                            </div>
                            <div className="d-flex gap-2">
                                <button className="btn btn-outline-primary">
                                    <i className="bi bi-download me-2"></i>
                                    Export Orders
                                </button>
                                <button className="btn btn-primary">
                                    <i className="bi bi-plus-circle me-2"></i>
                                    Manual Order
                                </button>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Statistics Cards */}
                <div className="row mb-4">
                    <div className="col-6 col-md-2 mb-3 mb-md-0">
                        <div className="card bg-primary text-white">
                            <div className="card-body">
                                <div className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <h6 className="card-title mb-0">Total Orders</h6>
                                        <h3 className="mb-0">{stats.total}</h3>
                                    </div>
                                    <i className="bi bi-cart3 fs-2 opacity-75"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="col-6 col-md-2 mb-3 mb-md-0">
                        <div className="card bg-success text-white">
                            <div className="card-body">
                                <div className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <h6 className="card-title mb-0">Completed</h6>
                                        <h3 className="mb-0">{stats.completed}</h3>
                                    </div>
                                    <i className="bi bi-check-circle fs-2 opacity-75"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="col-6 col-md-2 mb-3 mb-md-0">
                        <div className="card bg-warning text-white">
                            <div className="card-body">
                                <div className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <h6 className="card-title mb-0">Pending</h6>
                                        <h3 className="mb-0">{stats.pending}</h3>
                                    </div>
                                    <i className="bi bi-clock fs-2 opacity-75"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="col-6 col-md-2 mb-3 mb-md-0">
                        <div className="card bg-info text-white">
                            <div className="card-body">
                                <div className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <h6 className="card-title mb-0">Processing</h6>
                                        <h3 className="mb-0">{stats.processing}</h3>
                                    </div>
                                    <i className="bi bi-arrow-repeat fs-2 opacity-75"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="col-6 col-md-2 mb-3 mb-md-0">
                        <div className="card bg-danger text-white">
                            <div className="card-body">
                                <div className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <h6 className="card-title mb-0">Cancelled</h6>
                                        <h3 className="mb-0">{stats.cancelled}</h3>
                                    </div>
                                    <i className="bi bi-x-circle fs-2 opacity-75"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="col-6 col-md-2">
                        <div className="card bg-secondary text-white">
                            <div className="card-body">
                                <div className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <h6 className="card-title mb-0">Refunded</h6>
                                        <h3 className="mb-0">{stats.refunded}</h3>
                                    </div>
                                    <i className="bi bi-arrow-left-circle fs-2 opacity-75"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Filters and Search */}
                <div className="row mb-4">
                    <div className="col-12">
                        <div className="card">
                            <div className="card-body">
                                <div className="row g-3">
                                    <div className="col-12 col-md-6 col-lg-4">
                                        <label className="form-label">Search Orders</label>
                                        <div className="input-group">
                                            <span className="input-group-text">
                                                <i className="bi bi-search"></i>
                                            </span>
                                            <input
                                                type="text"
                                                className="form-control"
                                                placeholder="Search by Order ID, Student, or Course"
                                                value={searchTerm}
                                                onChange={(e) => setSearchTerm(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-12 col-md-6 col-lg-4">
                                        <label className="form-label">Filter by Status</label>
                                        <select
                                            className="form-select"
                                            value={statusFilter}
                                            onChange={(e) => setStatusFilter(e.target.value)}
                                        >
                                            <option value="all">All Status</option>
                                            <option value="completed">Completed</option>
                                            <option value="pending">Pending</option>
                                            <option value="processing">Processing</option>
                                            <option value="cancelled">Cancelled</option>
                                            <option value="refunded">Refunded</option>
                                        </select>
                                    </div>
                                    <div className="col-12 col-lg-4 d-flex align-items-end">
                                        <button
                                            className="btn btn-outline-secondary w-100"
                                            onClick={() => {
                                                setSearchTerm('');
                                                setStatusFilter('all');
                                            }}
                                        >
                                            <i className="bi bi-arrow-clockwise me-2"></i>
                                            Reset Filters
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Orders Table */}
                <div className="row">
                    <div className="col-12">
                        <div className="card">
                            <div className="card-header">
                                <h5 className="card-title mb-0">Orders ({filteredOrders.length})</h5>
                            </div>
                            <div className="card-body p-0">
                                <div className="table-responsive">
                                    <table className="table table-hover mb-0">
                                        <thead className="table-light">
                                            <tr>
                                                <th>Order ID</th>
                                                <th>Student</th>
                                                <th className="d-none d-md-table-cell">Course</th>
                                                <th className="d-none d-lg-table-cell">Date</th>
                                                <th>Amount</th>
                                                <th>Status</th>
                                                <th>Actions</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {filteredOrders.map(order => (
                                                <tr key={order.id}>
                                                    <td>
                                                        <strong>{order.id}</strong>
                                                    </td>
                                                    <td>
                                                        <div>
                                                            <div className="fw-semibold">{order.studentName}</div>
                                                            <small className="text-muted d-block d-md-none">{order.course}</small>
                                                        </div>
                                                    </td>
                                                    <td className="d-none d-md-table-cell">
                                                        <span className="text-truncate" style={{ maxWidth: '200px', display: 'inline-block' }}>
                                                            {order.course}
                                                        </span>
                                                    </td>
                                                    <td className="d-none d-lg-table-cell">
                                                        {new Date(order.orderDate).toLocaleDateString()}
                                                    </td>
                                                    <td>
                                                        <strong>${order.amount}</strong>
                                                    </td>
                                                    <td>
                                                        <span className={`badge ${getStatusBadge(order.status)}`}>
                                                            {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                                                        </span>
                                                    </td>
                                                    <td>
                                                        <div className="btn-group" role="group">
                                                            <button
                                                                className="btn btn-sm btn-outline-primary"
                                                                onClick={() => handleViewOrder(order)}
                                                            >
                                                                <i className="bi bi-eye"></i>
                                                                <span className="d-none d-sm-inline ms-1">View</span>
                                                            </button>
                                                            {getAvailableActions(order.status).length > 0 && (
                                                                <div className="dropdown">
                                                                    <button
                                                                        className="btn btn-sm btn-outline-secondary dropdown-toggle"
                                                                        type="button"
                                                                        data-bs-toggle="dropdown"
                                                                        aria-expanded="false"
                                                                    >
                                                                        <i className="bi bi-three-dots"></i>
                                                                    </button>
                                                                    <ul className="dropdown-menu">
                                                                        {getAvailableActions(order.status).map((actionItem, index) => (
                                                                            <li key={index}>
                                                                                <button 
                                                                                    className={actionItem.className}
                                                                                    onClick={() => handleActionClick(order.id, actionItem.action)}
                                                                                >
                                                                                    {actionItem.label}
                                                                                </button>
                                                                            </li>
                                                                        ))}
                                                                    </ul>
                                                                </div>
                                                            )}
                                                        </div>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>

                                {filteredOrders.length === 0 && (
                                    <div className="text-center py-5">
                                        <i className="bi bi-inbox fs-1 text-muted"></i>
                                        <p className="text-muted mt-2">No orders found matching your criteria</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>

                {/* Order Details Modal */}
                {showModal && selectedOrder && (
                    <div 
                        className="modal d-block" 
                        tabIndex="-1" 
                        style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}
                        onClick={handleModalBackdropClick}
                        ref={modalRef}
                    >
                        <div className="modal-dialog modal-lg">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h5 className="modal-title">Order Details - {selectedOrder.id}</h5>
                                    <button type="button" className="btn-close" onClick={closeModal}></button>
                                </div>
                                <div className="modal-body">
                                    <div className="row">
                                        <div className="col-md-6">
                                            <h6>Student Information</h6>
                                            <div className="mb-3">
                                                <strong>Name:</strong> {selectedOrder.studentName}<br />
                                                <strong>Email:</strong> {selectedOrder.email}<br />
                                                <strong>Phone:</strong> {selectedOrder.phone}
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <h6>Order Information</h6>
                                            <div className="mb-3">
                                                <strong>Order ID:</strong> {selectedOrder.id}<br />
                                                <strong>Date:</strong> {new Date(selectedOrder.orderDate).toLocaleDateString()}<br />
                                                <strong>Last Updated:</strong> {new Date(selectedOrder.lastUpdated).toLocaleString()}<br />
                                                <strong>Status:</strong> <span className={`badge ${getStatusBadge(selectedOrder.status)}`}>
                                                    {selectedOrder.status.charAt(0).toUpperCase() + selectedOrder.status.slice(1)}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-12">
                                            <h6>Course Details</h6>
                                            <div className="card bg-light">
                                                <div className="card-body">
                                                    <h6 className="card-title">{selectedOrder.course}</h6>
                                                    <div className="d-flex justify-content-between">
                                                        <span><strong>Amount:</strong> ${selectedOrder.amount}</span>
                                                        <span><strong>Payment Method:</strong> {selectedOrder.paymentMethod}</span>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-secondary" onClick={closeModal}>Close</button>
                                    <div className="btn-group">
                                        {getAvailableActions(selectedOrder.status).map((actionItem, index) => (
                                            <button 
                                                key={index}
                                                className={`btn ${
                                                    actionItem.action === 'refund' ? 'btn-warning' : 
                                                    actionItem.action === 'reactivate' ? 'btn-success' : 
                                                    actionItem.action === 'completed' ? 'btn-success' : 'btn-info'
                                                }`}
                                                onClick={() => handleActionClick(selectedOrder.id, actionItem.action)}
                                            >
                                                {actionItem.label}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                {/* Refund Modal */}
                {showRefundModal && selectedOrder && (
                    <div 
                        className="modal d-block" 
                        tabIndex="-1" 
                        style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}
                        onClick={handleRefundModalBackdropClick}
                        ref={refundModalRef}
                    >
                        <div className="modal-dialog">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h5 className="modal-title">Issue Refund</h5>
                                    <button type="button" className="btn-close" onClick={closeRefundModal}></button>
                                </div>
                                <div className="modal-body">
                                    <div className="alert alert-warning">
                                        <i className="bi bi-exclamation-triangle me-2"></i>
                                        You are about to issue a refund for order {selectedOrder.id} of ${selectedOrder.amount}
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Refund Reason</label>
                                        <textarea 
                                            className="form-control" 
                                            rows="3" 
                                            placeholder="Enter reason for refund..."
                                            value={refundReason}
                                            onChange={(e) => setRefundReason(e.target.value)}
                                        ></textarea>
                                    </div>
                                </div>
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-secondary" onClick={closeRefundModal}>Cancel</button>
                                    <button 
                                        type="button" 
                                        className="btn btn-warning"
                                        onClick={handleRefund}
                                        disabled={!refundReason.trim()}
                                    >
                                        Issue Refund
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </>
    );
};

export default ManageOrders;
