import React, { useEffect, useState } from "react";

function ManageCourses() {
    const [courses, setCourses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [message, setMessage] = useState(""); // Unified for success/error messages
    const [showModal, setShowModal] = useState(false);
    const [editingCourse, setEditingCourse] = useState(null);
    const [formData, setFormData] = useState({
        title: "",
        instructor: "",
        price: "",
        description: "",
        duration: "",
        category: "",
        level: "Beginner",
        isPublished: true,
        language: "English",
        tags: ""
    });

    // API base URL - adjust according to your backend setup
    const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

    /**
     * Get auth token from localStorage
     */
    const getAuthToken = () => {
        return localStorage.getItem('adminToken') || localStorage.getItem('token');
    };

    /**
     * Formats a number as Indian Rupees (INR) currency.
     */
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('en-IN', {
            style: 'currency',
            currency: 'INR',
            minimumFractionDigits: 0,
        }).format(amount);
    };

    /**
     * Fetch courses from backend API
     */
    const fetchCourses = async () => {
        setLoading(true);
        try {
            const token = getAuthToken();
            const response = await fetch(`${API_BASE_URL}/admin/courses`, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            setCourses(data);
            setMessage("");
        } catch (error) {
            console.error("Error fetching courses:", error);
            setMessage("Failed to fetch courses. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    // Fetch courses on component mount
    useEffect(() => {
        fetchCourses();
    }, []);

    /**
     * Handles changes to form input fields.
     */
    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    /**
     * Opens the modal for editing a course.
     */
    const openModal = (course) => {
        setEditingCourse(course);
        setFormData({
            title: course.title || "",
            instructor: course.instructor || "",
            price: course.price || "",
            description: course.description || "",
            duration: course.duration || "",
            category: course.category || "",
            level: course.level || "Beginner",
            isPublished: course.isPublished !== undefined ? course.isPublished : true,
            language: course.language || "English",
            tags: Array.isArray(course.tags) ? course.tags.join(", ") : (course.tags || "")
        });
        setShowModal(true);
    };

    /**
     * Closes the modal and resets the form data.
     */
    const closeModal = () => {
        setShowModal(false);
        setEditingCourse(null);
        setFormData({
            title: "",
            instructor: "",
            price: "",
            description: "",
            duration: "",
            category: "",
            level: "Beginner",
            isPublished: true,
            language: "English",
            tags: ""
        });
        setMessage("");
    };

    /**
     * Handles the submission of the course form (edit only).
     */
    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage("");

        if (!editingCourse) {
            setMessage("No course selected for editing.");
            return;
        }

        try {
            const token = getAuthToken();

            // Prepare data for API
            const updateData = {
                ...formData,
                tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
            };

            const response = await fetch(`${API_BASE_URL}/admin/courses/${editingCourse._id}`, {
                method: 'PUT',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(updateData)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();

            // Update local state
            setCourses(prevCourses =>
                prevCourses.map(course =>
                    course._id === editingCourse._id ? data.course : course
                )
            );

            setMessage("Course updated successfully!");
            closeModal();
        } catch (error) {
            console.error("Error updating course:", error);
            setMessage("Failed to update course. Please try again.");
        }
    };

    /**
     * Handles deleting a course.
     */
    const handleDelete = async (courseId) => {
        if (!window.confirm("Are you sure you want to delete this course? This action cannot be undone.")) {
            return;
        }

        setMessage("Deleting course...");

        try {
            const token = getAuthToken();
            const response = await fetch(`${API_BASE_URL}/admin/courses/${courseId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            // Remove course from local state
            setCourses(prevCourses => prevCourses.filter(course => course._id !== courseId));
            setMessage("Course deleted successfully!");
        } catch (error) {
            console.error("Error deleting course:", error);
            setMessage("Failed to delete course. Please try again.");
        }
    };

    /**
     * Handles toggling the published status of a course.
     */
    const toggleCourseStatus = async (courseId, currentStatus) => {
        setMessage("Updating status...");

        try {
            const token = getAuthToken();
            const response = await fetch(`${API_BASE_URL}/admin/courses/${courseId}`, {
                method: 'PUT',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ isPublished: !currentStatus })
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();

            // Update local state
            setCourses(prevCourses =>
                prevCourses.map(course =>
                    course._id === courseId ? data.course : course
                )
            );

            setMessage("Course status updated successfully!");
        } catch (error) {
            console.error("Error updating course status:", error);
            setMessage("Failed to update course status. Please try again.");
        }
    };

    // Display loading spinner while data is being fetched
    if (loading) {
        return (
            <div className="flex justify-center items-center min-h-screen bg-gray-100 font-inter">
                <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-500 border-t-transparent"></div>
                <span className="ml-3 text-gray-700">Loading courses...</span>
            </div>
        );
    }

    return (
        <div className="p-4 mx-auto max-w-7xl bg-gray-50 min-h-screen font-inter">
            <div className="flex justify-between items-center mb-6">
                <h2 className="text-3xl font-bold text-gray-800">Manage Courses</h2>
            </div>

            {message && (
                <div className={`p-4 mb-6 rounded-md text-sm ${message.includes("success") ? "bg-green-100 text-green-700" : "bg-red-100 text-red-700"
                    } shadow-md`}>
                    {message}
                    <button
                        type="button"
                        className="float-right text-gray-500 hover:text-gray-700 ml-4 -mt-1"
                        onClick={() => setMessage("")}
                    >
                        &times;
                    </button>
                </div>
            )}

            <div className="bg-white shadow-lg rounded-lg p-6">
                {courses.length === 0 ? (
                    <div className="text-center py-8 text-gray-500">
                        <svg className="mx-auto h-16 w-16 text-gray-400 mb-3" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6.75a.75.75 0 01.75.75v3.75a.75.75 0 01-.75.75H8.25a.75.75 0 01-.75-.75V7.5c0-.414.336-.75.75-.75h3.75zm-3 7.5c-.414 0-.75.336-.75.75v3.75a.75.75 0 00.75.75h3.75a.75.75 0 00.75-.75v-3.75a.75.75 0 00-.75-.75H9zm9-7.5a.75.75 0 01.75.75v3.75a.75.75 0 01-.75.75h-3.75a.75.75 0 01-.75-.75V7.5c0-.414.336-.75.75-.75h3.75zm-3 7.5a.75.75 0 01.75.75v3.75a.75.75 0 01-.75.75h-3.75a.75.75 0 01-.75-.75v-3.75a.75.75 0 01.75-.75h3.75z" />
                        </svg>
                        <h5 className="text-xl font-semibold mb-2">No courses found</h5>
                        <p className="text-base">No courses are currently available in the system.</p>
                    </div>
                ) : (
                    <div className="overflow-x-auto rounded-lg border border-gray-200 shadow-sm">
                        <table className="min-w-full divide-y divide-gray-200">
                            <thead className="bg-gray-800 text-white">
                                <tr>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Title</th>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Instructor</th>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Category</th>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Level</th>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Duration</th>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Price</th>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Status</th>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Actions</th>
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {courses.map((course) => (
                                    <tr key={course._id} className="hover:bg-gray-50">
                                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                                            <div>
                                                <strong>{course.title}</strong>
                                                {course.description && (
                                                    <div className="text-gray-500 text-xs">
                                                        {course.description.length > 50
                                                            ? `${course.description.substring(0, 50)}...`
                                                            : course.description}
                                                    </div>
                                                )}
                                            </div>
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{course.instructor}</td>
                                        <td className="px-6 py-4 whitespace-nowrap">
                                            {course.category && (
                                                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-200 text-gray-800">
                                                    {course.category}
                                                </span>
                                            )}
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap">
                                            <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${course.level === 'Beginner' ? 'bg-green-100 text-green-800' :
                                                    course.level === 'Intermediate' ? 'bg-yellow-100 text-yellow-800' : 'bg-red-100 text-red-800'
                                                }`}>
                                                {course.level || 'Beginner'}
                                            </span>
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{course.duration || 'N/A'}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                            <strong className="text-green-600">
                                                {formatCurrency(course.price)}
                                            </strong>
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap">
                                            <button
                                                className={`px-3 py-1 text-xs rounded-full font-semibold transition-colors duration-200 ${course.isPublished ? 'bg-green-500 text-white hover:bg-green-600' : 'bg-gray-400 text-white hover:bg-gray-500'
                                                    }`}
                                                onClick={() => toggleCourseStatus(course._id, course.isPublished)}
                                            >
                                                {course.isPublished ? 'Published' : 'Unpublished'}
                                            </button>
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                                            <div className="flex space-x-2">
                                                <button
                                                    className="inline-flex items-center justify-center p-2 border border-transparent rounded-md shadow-sm text-blue-600 hover:text-white hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition duration-200"
                                                    onClick={() => openModal(course)}
                                                    title="Edit Course"
                                                >
                                                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                                                        <path d="M13.586 3.586a2 2 0 112.828 2.828l-.793.793-2.828-2.828.793-.793zm-3.109 7.071l4.145-4.146a.5.5 0 01.707 0l2.828 2.828a.5.5 0 010 .707l-4.146 4.145a.5.5 0 01-.707 0l-2.828-2.828a.5.5 0 010-.707zM6.5 14a.5.5 0 01.5-.5h4a.5.5 0 010 1h-4a.5.5 0 01-.5-.5z" />
                                                        <path fillRule="evenodd" d="M3 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H5a2 2 0 01-2-2V6zm0 8a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H5a2 2 0 01-2-2v-2z" clipRule="evenodd" />
                                                    </svg>
                                                </button>
                                                <button
                                                    className="inline-flex items-center justify-center p-2 border border-transparent rounded-md shadow-sm text-red-600 hover:text-white hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 transition duration-200"
                                                    onClick={() => handleDelete(course._id)}
                                                    title="Delete Course"
                                                >
                                                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                                                        <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm6 0a1 1 0 01-2 0v6a1 1 0 112 0V8z" clipRule="evenodd" />
                                                    </svg>
                                                </button>
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>

            {/* Edit Course Modal */}
            {showModal && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                    <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
                        <div className="flex justify-between items-center px-6 py-4 border-b border-gray-200">
                            <h5 className="text-xl font-semibold text-gray-800">Edit Course</h5>
                            <button
                                type="button"
                                className="text-gray-400 hover:text-gray-600 text-2xl leading-none"
                                onClick={closeModal}
                                aria-label="Close"
                            >
                                &times;
                            </button>
                        </div>
                        <form onSubmit={handleSubmit}>
                            <div className="p-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                                    <div>
                                        <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-1">Course Title *</label>
                                        <input
                                            type="text"
                                            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                                            id="title"
                                            name="title"
                                            value={formData.title}
                                            onChange={handleInputChange}
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="instructor" className="block text-sm font-medium text-gray-700 mb-1">Instructor *</label>
                                        <input
                                            type="text"
                                            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                                            id="instructor"
                                            name="instructor"
                                            value={formData.instructor}
                                            onChange={handleInputChange}
                                            required
                                        />
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                                    <div>
                                        <label htmlFor="price" className="block text-sm font-medium text-gray-700 mb-1">Price () *</label>
                                        <input
                                            type="number"
                                            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                                            id="price"
                                            name="price"
                                            value={formData.price}
                                            onChange={handleInputChange}
                                            min="0"
                                            step="1"
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="duration" className="block text-sm font-medium text-gray-700 mb-1">Duration</label>
                                        <input
                                            type="text"
                                            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                                            id="duration"
                                            name="duration"
                                            value={formData.duration}
                                            onChange={handleInputChange}
                                            placeholder="e.g., 4 weeks, 20 hours"
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="level" className="block text-sm font-medium text-gray-700 mb-1">Level</label>
                                        <select
                                            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 bg-white"
                                            id="level"
                                            name="level"
                                            value={formData.level}
                                            onChange={handleInputChange}
                                        >
                                            <option value="Beginner">Beginner</option>
                                            <option value="Intermediate">Intermediate</option>
                                            <option value="Advanced">Advanced</option>
                                        </select>
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                                    <div>
                                        <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">Category</label>
                                        <input
                                            type="text"
                                            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                                            id="category"
                                            name="category"
                                            value={formData.category}
                                            onChange={handleInputChange}
                                            placeholder="e.g., Programming, Design, Business"
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="language" className="block text-sm font-medium text-gray-700 mb-1">Language</label>
                                        <input
                                            type="text"
                                            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                                            id="language"
                                            name="language"
                                            value={formData.language}
                                            onChange={handleInputChange}
                                            placeholder="e.g., English, Hindi"
                                        />
                                    </div>
                                </div>

                                <div className="mb-4">
                                    <label htmlFor="tags" className="block text-sm font-medium text-gray-700 mb-1">Tags</label>
                                    <input
                                        type="text"
                                        className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                                        id="tags"
                                        name="tags"
                                        value={formData.tags}
                                        onChange={handleInputChange}
                                        placeholder="e.g., React, JavaScript, Frontend (separate with commas)"
                                    />
                                </div>

                                <div className="mb-4">
                                    <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                                    <textarea
                                        className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                                        id="description"
                                        name="description"
                                        value={formData.description}
                                        onChange={handleInputChange}
                                        rows="3"
                                        placeholder="Brief description of the course..."
                                    ></textarea>
                                </div>

                                <div className="flex items-center">
                                    <input
                                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                                        type="checkbox"
                                        id="isPublished"
                                        name="isPublished"
                                        checked={formData.isPublished}
                                        onChange={handleInputChange}
                                    />
                                    <label className="ml-2 block text-sm text-gray-900" htmlFor="isPublished">
                                        Course is published and available for enrollment
                                    </label>
                                </div>
                            </div>
                            <div className="flex justify-end p-6 border-t border-gray-200 space-x-3">
                                <button
                                    type="button"
                                    className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 transition duration-200"
                                    onClick={closeModal}
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition duration-200"
                                >
                                    Update Course
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ManageCourses;
