import React, { useState, useEffect, useCallback, useMemo } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageCategories = () => {
    const [categories, setCategories] = useState([]);
    const [name, setName] = useState("");
    const [slug, setSlug] = useState("");
    const [parentCategory, setParentCategory] = useState("");
    const [level, setLevel] = useState(1);
    const [isActive, setIsActive] = useState(true);
    const [editingId, setEditingId] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [expandedCategories, setExpandedCategories] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [searchTerm, setSearchTerm] = useState("");
    const [selectedLevel, setSelectedLevel] = useState("all");

    // Auto-generate slug when name changes
    useEffect(() => {
        if (name && !editingId) {
            const generatedSlug = name.toLowerCase()
                .replace(/\s+/g, "-")
                .replace(/[^a-z0-9-]/g, "");
            setSlug(generatedSlug);
        }
    }, [name, editingId]);

    // Memoized API base URL and headers
    const apiConfig = useMemo(() => {
        const token = localStorage.getItem("adminToken");
        return {
            baseURL: "http://localhost:5000/api/admin",
            headers: { Authorization: `Bearer ${token}` }
        };
    }, []);

    // Enhanced error handling
    const handleApiError = useCallback((error, defaultMessage) => {
        console.error(defaultMessage, error);
        const errorMessage = error.response?.data?.message || 
                           error.response?.data?.errors?.[0] || 
                           defaultMessage;
        setError(errorMessage);
        return errorMessage;
    }, []);

    // Fetch categories from backend with enhanced error handling
    const fetchCategories = useCallback(async () => {
        try {
            setLoading(true);
            if (!apiConfig.headers.Authorization.includes('Bearer ')) {
                setError("No admin token found. Please login again.");
                return;
            }

            const res = await axios.get(`${apiConfig.baseURL}/categories`, {
                headers: apiConfig.headers,
            });
            setCategories(res.data);
            setError("");
        } catch (err) {
            handleApiError(err, "Failed to fetch categories");
        } finally {
            setLoading(false);
        }
    }, [apiConfig, handleApiError]);

    useEffect(() => {
        fetchCategories();
    }, [fetchCategories]);

    // Enhanced form validation
    const validateForm = useCallback(() => {
        if (!name.trim()) {
            setError("Category name is required");
            return false;
        }

        if (level > 1 && !parentCategory) {
            setError("Parent category is required for subcategories");
            return false;
        }

        if (level === 1 && parentCategory) {
            setError("Main categories cannot have a parent");
            return false;
        }

        // Check for duplicate names at the same level
        const isDuplicate = categories.some(cat => 
            cat._id !== editingId &&
            cat.name.toLowerCase() === name.trim().toLowerCase() &&
            cat.level === level &&
            cat.parentCategory === (parentCategory || null)
        );

        if (isDuplicate) {
            setError("A category with this name already exists at this level");
            return false;
        }

        return true;
    }, [name, level, parentCategory, categories, editingId]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        if (!validateForm()) {
            setLoading(false);
            return;
        }

        const categoryPayload = {
            name: name.trim(),
            slug: slug.trim() || name.toLowerCase().replace(/\s+/g, "-"),
            parentCategory: parentCategory || null,
            level: level,
            isActive: isActive,
        };

        try {
            let res;
            if (editingId) {
                res = await axios.put(
                    `${apiConfig.baseURL}/category/${editingId}`,
                    categoryPayload,
                    { headers: apiConfig.headers }
                );

                setCategories(prev => prev.map(cat =>
                    cat._id === editingId ? res.data.category : cat
                ));
                setError("");
                showNotification("Category updated successfully!", "success");
            } else {
                res = await axios.post(
                    `${apiConfig.baseURL}/add-category`,
                    categoryPayload,
                    { headers: apiConfig.headers }
                );

                setCategories(prev => [...prev, res.data.category]);
                setError("");
                showNotification("Category added successfully!", "success");
            }

            resetForm();
            setShowModal(false);
        } catch (err) {
            const errorMessage = handleApiError(err, "Failed to submit category");
            showNotification(errorMessage, "danger");
        } finally {
            setLoading(false);
        }
    };

    // Enhanced notification system
    const showNotification = (message, type = "info") => {
        // Create toast notification element
        const toast = document.createElement('div');
        toast.className = `alert alert-${type} alert-dismissible fade show position-fixed`;
        toast.style.cssText = 'top: 20px; right: 20px; z-index: 9999; min-width: 300px;';
        toast.innerHTML = `
            <i class="fas fa-${type === 'success' ? 'check-circle' : type === 'danger' ? 'exclamation-triangle' : 'info-circle'} me-2"></i>
            ${message}
            <button type="button" class="btn-close" data-bs-dismiss="alert"></button>
        `;
        
        document.body.appendChild(toast);
        
        // Auto remove after 5 seconds
        setTimeout(() => {
            if (toast.parentNode) {
                toast.remove();
            }
        }, 5000);
    };

    const resetForm = () => {
        setName("");
        setSlug("");
        setParentCategory("");
        setLevel(1);
        setIsActive(true);
        setEditingId(null);
        setError("");
    };

    const handleEdit = (category) => {
        setName(category.name);
        setSlug(category.slug);
        setParentCategory(category.parentCategory || "");
        setLevel(category.level || 1);
        setIsActive(category.isActive ?? true);
        setEditingId(category._id);
        setShowModal(true);
        setError("");
    };

    const handleDelete = async (id) => {
        const category = categories.find(cat => cat._id === id);
        const hasChildren = categories.some(cat => cat.parentCategory === id);
        
        const confirmMessage = hasChildren 
            ? `Are you sure you want to delete "${category?.name}"? This will also delete all its subcategories.`
            : `Are you sure you want to delete "${category?.name}"?`;
            
        if (!window.confirm(confirmMessage)) return;

        setLoading(true);
        try {
            await axios.delete(`${apiConfig.baseURL}/category/${id}`, {
                headers: apiConfig.headers,
            });

            // Remove deleted category and its children from state
            setCategories(prev => {
                const removeCategory = (cats, targetId) => {
                    return cats.filter(cat => {
                        if (cat._id === targetId) return false;
                        if (cat.parentCategory === targetId) return false;
                        return true;
                    });
                };
                return removeCategory(prev, id);
            });

            showNotification("Category deleted successfully!", "success");
        } catch (err) {
            const errorMessage = handleApiError(err, "Failed to delete category");
            showNotification(errorMessage, "danger");
        } finally {
            setLoading(false);
        }
    };

    // Enhanced category organization with search filtering
    const organizeCategories = useCallback((cats) => {
        const categoryMap = {};
        const rootCategories = [];

        // Filter categories based on search and level
        const filteredCats = cats.filter(cat => {
            const matchesSearch = !searchTerm || 
                cat.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                cat.slug.toLowerCase().includes(searchTerm.toLowerCase());
            
            const matchesLevel = selectedLevel === "all" || 
                cat.level.toString() === selectedLevel;
            
            return matchesSearch && matchesLevel;
        });

        // Create a map of all filtered categories
        filteredCats.forEach(cat => {
            categoryMap[cat._id] = { ...cat, children: [] };
        });

        // Organize them hierarchically
        filteredCats.forEach(cat => {
            if (cat.parentCategory && categoryMap[cat.parentCategory]) {
                categoryMap[cat.parentCategory].children.push(categoryMap[cat._id]);
            } else {
                rootCategories.push(categoryMap[cat._id]);
            }
        });

        return rootCategories;
    }, [searchTerm, selectedLevel]);

    const toggleExpand = (categoryId) => {
        setExpandedCategories(prev => ({
            ...prev,
            [categoryId]: !prev[categoryId]
        }));
    };

    // Enhanced category tree rendering with better styling
    const renderCategoryTree = (cats, depth = 0) => {
        return cats.map((category, index) => (
            <React.Fragment key={category._id}>
                <tr className={!category.isActive ? "table-secondary" : ""}>
                    <td>{index + 1}</td>
                    <td style={{ paddingLeft: `${depth * 20}px` }}>
                        <div className="d-flex align-items-center">
                            {category.children.length > 0 && (
                                <button
                                    className="btn btn-sm btn-link p-0 me-2"
                                    onClick={() => toggleExpand(category._id)}
                                    disabled={loading}
                                    title={expandedCategories[category._id] ? "Collapse" : "Expand"}
                                >
                                    <i className={`fas ${expandedCategories[category._id] ? 'fa-chevron-down' : 'fa-chevron-right'}`}></i>
                                </button>
                            )}
                            <span className={`badge me-2 ${
                                category.level === 1 ? 'bg-primary' :
                                category.level === 2 ? 'bg-success' : 'bg-warning'
                            }`}>
                                {category.level === 1 ? 'Main' : category.level === 2 ? 'Sub' : 'Sub-Sub'}
                            </span>
                            <span className={!category.isActive ? "text-muted" : ""}>
                                {category.name}
                                {!category.isActive && <small className="ms-1">(Inactive)</small>}
                            </span>
                        </div>
                    </td>
                    <td>
                        <code className={!category.isActive ? "text-muted" : ""}>{category.slug}</code>
                    </td>
                    <td>
                        <span className={`badge ${category.isActive ? 'bg-success' : 'bg-secondary'}`}>
                            {category.isActive ? 'Active' : 'Inactive'}
                        </span>
                    </td>
                    <td>
                        <div className="btn-group" role="group">
                            <button
                                className="btn btn-sm btn-outline-success"
                                onClick={() => {
                                    resetForm();
                                    setParentCategory(category._id);
                                    setLevel(Math.min(category.level + 1, 3));
                                    setShowModal(true);
                                }}
                                disabled={loading || category.level >= 3}
                                title={category.level >= 3 ? "Maximum depth reached" : "Add subcategory"}
                            >
                                <i className="fas fa-plus me-1"></i>
                                Add Sub
                            </button>
                            <button
                                className="btn btn-sm btn-outline-primary"
                                onClick={() => handleEdit(category)}
                                disabled={loading}
                                title="Edit category"
                            >
                                <i className="fas fa-edit"></i>
                            </button>
                            <button
                                className="btn btn-sm btn-outline-danger"
                                onClick={() => handleDelete(category._id)}
                                disabled={loading}
                                title="Delete category"
                            >
                                <i className="fas fa-trash"></i>
                            </button>
                        </div>
                    </td>
                </tr>
                {expandedCategories[category._id] && category.children.length > 0 &&
                    renderCategoryTree(category.children, depth + 1)
                }
            </React.Fragment>
        ));
    };

    // Get available parent categories based on selected level
    const getAvailableParents = () => {
        if (level === 1) return [];
        if (level === 2) return categories.filter(cat => cat.level === 1 && cat.isActive);
        if (level === 3) return categories.filter(cat => cat.level === 2 && cat.isActive);
        return [];
    };

    const organizedCategories = organizeCategories(categories);

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2 className="mb-0">
                    <i className="fas fa-sitemap me-2"></i>
                    Manage Hierarchical Categories
                </h2>
                <button
                    className="btn btn-primary"
                    onClick={() => {
                        resetForm();
                        setShowModal(true);
                    }}
                    disabled={loading}
                >
                    <i className="fas fa-plus me-2"></i>
                    Add New Category
                </button>
            </div>

            {/* Search and Filter Bar */}
            <div className="card mb-4">
                <div className="card-body">
                    <div className="row g-3">
                        <div className="col-md-6">
                            <div className="input-group">
                                <span className="input-group-text">
                                    <i className="fas fa-search"></i>
                                </span>
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Search categories..."
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className="col-md-3">
                            <select
                                className="form-select"
                                value={selectedLevel}
                                onChange={(e) => setSelectedLevel(e.target.value)}
                            >
                                <option value="all">All Levels</option>
                                <option value="1">Main Categories</option>
                                <option value="2">Subcategories</option>
                                <option value="3">Sub-subcategories</option>
                            </select>
                        </div>
                        <div className="col-md-3">
                            <button
                                className="btn btn-outline-secondary w-100"
                                onClick={fetchCategories}
                                disabled={loading}
                            >
                                <i className="fas fa-sync-alt me-1"></i>
                                Refresh
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            {/* Error Alert */}
            {error && (
                <div className="alert alert-danger alert-dismissible fade show" role="alert">
                    <i className="fas fa-exclamation-triangle me-2"></i>
                    {error}
                    <button
                        type="button"
                        className="btn-close"
                        onClick={() => setError("")}
                        aria-label="Close"
                    ></button>
                </div>
            )}

            {/* Loading Spinner */}
            {loading && (
                <div className="text-center mb-3">
                    <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            )}

            {/* Category Tree Table */}
            <div className="card">
                <div className="card-header d-flex justify-content-between align-items-center">
                    <h5 className="mb-0">
                        <i className="fas fa-sitemap me-2"></i>
                        Category Hierarchy ({organizedCategories.length} categories)
                    </h5>
                    <div className="btn-group">
                        <button
                            className="btn btn-sm btn-outline-primary"
                            onClick={() => setExpandedCategories(
                                categories.reduce((acc, cat) => ({ ...acc, [cat._id]: true }), {})
                            )}
                            disabled={loading}
                            title="Expand all"
                        >
                            <i className="fas fa-expand-arrows-alt"></i>
                        </button>
                        <button
                            className="btn btn-sm btn-outline-secondary"
                            onClick={() => setExpandedCategories({})}
                            disabled={loading}
                            title="Collapse all"
                        >
                            <i className="fas fa-compress-arrows-alt"></i>
                        </button>
                    </div>
                </div>
                <div className="card-body p-0">
                    <div className="table-responsive">
                        <table className="table table-hover mb-0">
                            <thead className="table-light">
                                <tr>
                                    <th width="60">No</th>
                                    <th>Name</th>
                                    <th width="200">Slug</th>
                                    <th width="80">Status</th>
                                    <th width="200">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {organizedCategories.length > 0 ? (
                                    renderCategoryTree(organizedCategories)
                                ) : (
                                    <tr>
                                        <td colSpan="5" className="text-center text-muted py-5">
                                            <i className="fas fa-folder-open fa-3x mb-3 d-block"></i>
                                            {loading ? "Loading categories..." : 
                                             searchTerm || selectedLevel !== "all" ? "No categories match your filters." :
                                             "No categories found. Add your first category!"}
                                        </td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            {/* Enhanced Modal */}
            {showModal && (
                <div className="modal" style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}>
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">
                                    <i className={`fas ${editingId ? 'fa-edit' : 'fa-plus'} me-2`}></i>
                                    {editingId ? "Edit Category" : "Add New Category"}
                                </h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    onClick={() => setShowModal(false)}
                                    disabled={loading}
                                ></button>
                            </div>
                            <div className="modal-body">
                                {error && (
                                    <div className="alert alert-danger">
                                        <i className="fas fa-exclamation-triangle me-2"></i>
                                        {error}
                                    </div>
                                )}
                                <form onSubmit={handleSubmit}>
                                    <div className="row">
                                        <div className="col-md-6 mb-3">
                                            <label className="form-label">
                                                <i className="fas fa-layer-group me-2"></i>
                                                Category Level
                                            </label>
                                            <select
                                                className="form-select"
                                                value={level}
                                                onChange={(e) => {
                                                    setLevel(parseInt(e.target.value));
                                                    setParentCategory("");
                                                }}
                                                disabled={editingId || loading}
                                            >
                                                <option value={1}>Main Category</option>
                                                <option value={2}>Subcategory</option>
                                                <option value={3}>Sub-subcategory</option>
                                            </select>
                                        </div>

                                        {level > 1 && (
                                            <div className="col-md-6 mb-3">
                                                <label className="form-label">
                                                    <i className="fas fa-sitemap me-2"></i>
                                                    Parent Category
                                                </label>
                                                <select
                                                    className="form-select"
                                                    value={parentCategory}
                                                    onChange={(e) => setParentCategory(e.target.value)}
                                                    required
                                                    disabled={loading}
                                                >
                                                    <option value="">Select Parent Category</option>
                                                    {getAvailableParents().map((cat) => (
                                                        <option key={cat._id} value={cat._id}>
                                                            {cat.name}
                                                        </option>
                                                    ))}
                                                </select>
                                            </div>
                                        )}
                                    </div>

                                    <div className="mb-3">
                                        <label className="form-label">
                                            <i className="fas fa-tag me-2"></i>
                                            Category Name
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={name}
                                            onChange={(e) => setName(e.target.value)}
                                            placeholder="Enter category name"
                                            required
                                            disabled={loading}
                                        />
                                    </div>

                                    <div className="mb-3">
                                        <label className="form-label">
                                            <i className="fas fa-link me-2"></i>
                                            Slug
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={slug}
                                            onChange={(e) => setSlug(e.target.value)}
                                            placeholder="Auto-generated from name"
                                            disabled={loading}
                                        />
                                        <div className="form-text">
                                            Preview: <code>{slug || "auto-generated"}</code>
                                        </div>
                                    </div>

                                    <div className="mb-3">
                                        <div className="form-check">
                                            <input
                                                className="form-check-input"
                                                type="checkbox"
                                                id="isActive"
                                                checked={isActive}
                                                onChange={(e) => setIsActive(e.target.checked)}
                                                disabled={loading}
                                            />
                                            <label className="form-check-label" htmlFor="isActive">
                                                <i className="fas fa-eye me-2"></i>
                                                Active (visible to users)
                                            </label>
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={() => setShowModal(false)}
                                    disabled={loading}
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    onClick={handleSubmit}
                                    disabled={loading}
                                >
                                    {loading ? (
                                        <>
                                            <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                                            {editingId ? "Updating..." : "Saving..."}
                                        </>
                                    ) : (
                                        <>
                                            <i className={`fas ${editingId ? 'fa-save' : 'fa-plus'} me-2`}></i>
                                            {editingId ? "Update" : "Save"}
                                        </>
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Enhanced Legend */}
            <div className="mt-4">
                <div className="card">
                    <div className="card-body">
                        <div className="row">
                            <div className="col-md-6">
                                <h6>
                                    <i className="fas fa-info-circle me-2"></i>
                                    Category Levels:
                                </h6>
                                <div className="d-flex gap-3 flex-wrap">
                                    <span>
                                        <span className="badge bg-primary">Main</span> - Top level categories
                                    </span>
                                    <span>
                                        <span className="badge bg-success">Sub</span> - Subcategories
                                    </span>
                                    <span>
                                        <span className="badge bg-warning">Sub-Sub</span> - Sub-subcategories (max depth)
                                    </span>
                                </div>
                            </div>
                            <div className="col-md-6">
                                <h6>
                                    <i className="fas fa-chart-bar me-2"></i>
                                    Statistics:
                                </h6>
                                <div className="d-flex gap-3 flex-wrap">
                                    <span>Total: <strong>{categories.length}</strong></span>
                                    <span>Active: <strong>{categories.filter(cat => cat.isActive).length}</strong></span>
                                    <span>Main: <strong>{categories.filter(cat => cat.level === 1).length}</strong></span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ManageCategories;
