import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import CourseCard from '../../../../mern-auth/src/components/CourseCard';


const Instructors = () => {
  const { name } = useParams();
  const [instructor, setInstructor] = useState(null);
  const [courses, setCourses] = useState([]);
  const [filter, setFilter] = useState("all");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchInstructorData = async () => {
      try {
        setLoading(true);
        
        // Fetch instructor and their courses
        const response = await fetch(`/api/instructors/name/${encodeURIComponent(name)}`);
        
        if (!response.ok) {
          if (response.status === 404) {
            setError('Instructor not found');
          } else {
            throw new Error('Failed to fetch instructor data');
          }
          return;
        }

        const data = await response.json();
        
        if (data.success) {
          setInstructor(data.data.instructor);
          setCourses(data.data.courses);
        } else {
          setError(data.message || 'Failed to load instructor data');
        }
      } catch (err) {
        console.error('Error fetching instructor:', err);
        setError('Something went wrong. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (name) {
      fetchInstructorData();
    }
  }, [name]);

  if (loading) {
    return (
      <div className="container my-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-2">Loading instructor profile...</p>
      </div>
    );
  }

  if (error || !instructor) {
    return (
      <div className="container my-5 text-center">
        <h2 className="text-danger">{error || 'Instructor not found'}</h2>
        <p className="text-muted">The instructor you're looking for might not exist or has been removed.</p>
      </div>
    );
  }

  const filteredCourses = courses.filter((course) => {
    if (filter === "free") return course.isFree;
    if (filter === "paid") return !course.isFree;
    return true;
  });

  return (
    <div className="container my-5">
      {/* Hero section */}
      <div className="card shadow rounded-4 overflow-hidden mb-5 p-4 d-flex flex-column flex-md-row align-items-center gap-4">
        {/* profile image */}
        <div
          className="d-flex justify-content-center align-items-center bg-primary-subtle p-4"
          style={{ borderRadius: "20px", flexShrink: 0 }}
        >
          {instructor.profileImage ? (
            <img
              src={instructor.profileImage}
              alt={instructor.name}
              className="rounded-circle shadow"
              style={{ width: "120px", height: "120px", objectFit: "cover" }}
            />
          ) : (
            <div
              className="bg-primary rounded-circle d-flex align-items-center justify-content-center text-white shadow"
              style={{ width: "120px", height: "120px", fontSize: "32px" }}
            >
              {instructor.name
                .split(" ")
                .map((n) => n[0])
                .join("")}
            </div>
          )}
        </div>

        {/* info block */}
        <div className="flex-grow-1 text-center text-md-start">
          <h2 className="fw-bold mb-2">{instructor.name}</h2>
          <p className="text-muted mb-3">{instructor.bio}</p>
          
          {/* Specializations */}
          {instructor.specializations && instructor.specializations.length > 0 && (
            <div className="mb-3">
              {instructor.specializations.map((spec, index) => (
                <span key={index} className="badge bg-primary-subtle text-primary me-2 mb-1">
                  {spec}
                </span>
              ))}
            </div>
          )}
          
          <div className="d-flex flex-wrap justify-content-center justify-content-md-start gap-3 mb-2">
            <span className="text-muted">
              <i className="bi bi-envelope me-1"></i>
              {instructor.email}
            </span>
            {instructor.experience > 0 && (
              <span className="text-muted">
                <i className="bi bi-award me-1"></i>
                {instructor.experience} years experience
              </span>
            )}
          </div>
          
          <p className="text-muted mb-1">
            <i className="bi bi-calendar me-1"></i>Joined on{" "}
            {new Date(instructor.dateJoined).toLocaleDateString()}
          </p>
          
          <div className="d-flex flex-wrap justify-content-center justify-content-md-start gap-3">
            <p className="text-muted mb-0">
              <i className="bi bi-book me-1"></i>
              {courses.length} course{courses.length !== 1 && "s"} published
            </p>
            
            {instructor.rating > 0 && (
              <p className="text-muted mb-0">
                <i className="bi bi-star-fill text-warning me-1"></i>
                {instructor.rating.toFixed(1)} rating
              </p>
            )}
            
            {instructor.totalStudents > 0 && (
              <p className="text-muted mb-0">
                <i className="bi bi-people me-1"></i>
                {instructor.totalStudents} students
              </p>
            )}
          </div>
        </div>
      </div>

      {/* Filter buttons */}
      <div className="mb-4 d-flex justify-content-center gap-2">
        <button
          className={`btn btn-outline-primary ${
            filter === "all" ? "active" : ""
          }`}
          onClick={() => setFilter("all")}
        >
          All Courses ({courses.length})
        </button>
        <button
          className={`btn btn-outline-success ${
            filter === "free" ? "active" : ""
          }`}
          onClick={() => setFilter("free")}
        >
          Free ({courses.filter(c => c.isFree).length})
        </button>
        <button
          className={`btn btn-outline-warning ${
            filter === "paid" ? "active" : ""
          }`}
          onClick={() => setFilter("paid")}
        >
          Paid ({courses.filter(c => !c.isFree).length})
        </button>
      </div>

      {/* Courses list */}
      <div className="row">
        {filteredCourses.length === 0 ? (
          <div className="text-center text-muted py-5">
            <i className="bi bi-collection display-4 mb-2"></i>
            <p>No courses found for this filter.</p>
          </div>
        ) : (
          filteredCourses.map((course) => (
            <div className="col-6 col-md-4 col-lg-3 mb-4" key={course._id}>
              <CourseCard course={course} />
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Instructors;
