import React, { useEffect, useState } from "react";

const Instructors = () => {
  const [instructors, setInstructors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [showViewModal, setShowViewModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedInstructor, setSelectedInstructor] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    bio: '',
    specializations: '',
    experience: '',
    profileImage: ''
  });

  useEffect(() => {
    fetchInstructors();
  }, []);

  const fetchInstructors = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/instructors');
      
      if (!response.ok) {
        throw new Error('Failed to fetch instructors');
      }

      const data = await response.json();
      
      if (data.success) {
        setInstructors(data.data);
      } else {
        setError(data.message || 'Failed to load instructors');
      }
    } catch (err) {
      console.error('Error fetching instructors:', err);
      setError('Something went wrong. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddInstructor = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/instructors', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          specializations: formData.specializations.split(',').map(s => s.trim()),
          experience: parseInt(formData.experience) || 0
        })
      });

      if (response.ok) {
        setShowAddForm(false);
        setFormData({
          name: '',
          email: '',
          bio: '',
          specializations: '',
          experience: '',
          profileImage: ''
        });
        fetchInstructors();
      } else {
        alert('Failed to add instructor');
      }
    } catch (err) {
      console.error('Error adding instructor:', err);
      alert('Error adding instructor');
    }
  };

  const handleEditInstructor = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`/api/instructors/${selectedInstructor._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          specializations: formData.specializations.split(',').map(s => s.trim()),
          experience: parseInt(formData.experience) || 0
        })
      });

      if (response.ok) {
        setShowEditModal(false);
        setSelectedInstructor(null);
        setFormData({
          name: '',
          email: '',
          bio: '',
          specializations: '',
          experience: '',
          profileImage: ''
        });
        fetchInstructors();
      } else {
        alert('Failed to update instructor');
      }
    } catch (err) {
      console.error('Error updating instructor:', err);
      alert('Error updating instructor');
    }
  };

  const handleDeleteInstructor = async (id) => {
    if (window.confirm('Are you sure you want to delete this instructor?')) {
      try {
        const response = await fetch(`/api/instructors/${id}`, {
          method: 'DELETE'
        });

        if (response.ok) {
          fetchInstructors();
        } else {
          alert('Failed to delete instructor');
        }
      } catch (err) {
        console.error('Error deleting instructor:', err);
        alert('Error deleting instructor');
      }
    }
  };

  const openViewModal = (instructor) => {
    setSelectedInstructor(instructor);
    setShowViewModal(true);
  };

  const openEditModal = (instructor) => {
    setSelectedInstructor(instructor);
    setFormData({
      name: instructor.name,
      email: instructor.email,
      bio: instructor.bio,
      specializations: instructor.specializations ? instructor.specializations.join(', ') : '',
      experience: instructor.experience || '',
      profileImage: instructor.profileImage || ''
    });
    setShowEditModal(true);
  };

  const openAddForm = () => {
    setFormData({
      name: '',
      email: '',
      bio: '',
      specializations: '',
      experience: '',
      profileImage: ''
    });
    setShowAddForm(true);
  };

  if (loading) {
    return (
      <div className="container my-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-2">Loading instructors...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container my-5 text-center">
        <h2 className="text-danger">{error}</h2>
        <p className="text-muted">Failed to load instructors data.</p>
      </div>
    );
  }

  return (
    <div className="container my-5">
      {/* Header */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fw-bold">Instructors Management</h2>
        <button 
          className="btn btn-primary"
          onClick={openAddForm}
        >
          <i className="bi bi-plus-circle me-2"></i>
          Add Instructor
        </button>
      </div>

      {/* Instructors Table */}
      <div className="card shadow rounded-4">
        <div className="card-body">
          {instructors.length === 0 ? (
            <div className="text-center text-muted py-5">
              <i className="bi bi-person-plus display-4 mb-2"></i>
              <p>No instructors found. Add your first instructor!</p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Profile</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Experience</th>
                    <th>Specializations</th>
                    <th>Joined</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {instructors.map((instructor) => (
                    <tr key={instructor._id}>
                      <td>
                        {instructor.profileImage ? (
                          <img
                            src={instructor.profileImage}
                            alt={instructor.name}
                            className="rounded-circle"
                            style={{ width: "40px", height: "40px", objectFit: "cover" }}
                          />
                        ) : (
                          <div
                            className="bg-primary rounded-circle d-flex align-items-center justify-content-center text-white"
                            style={{ width: "40px", height: "40px", fontSize: "14px" }}
                          >
                            {instructor.name
                              .split(" ")
                              .map((n) => n[0])
                              .join("")}
                          </div>
                        )}
                      </td>
                      <td className="fw-semibold">{instructor.name}</td>
                      <td className="text-muted">{instructor.email}</td>
                      <td>
                        {instructor.experience > 0 ? (
                          <span className="badge bg-info">{instructor.experience} years</span>
                        ) : (
                          <span className="text-muted">-</span>
                        )}
                      </td>
                      <td>
                        {instructor.specializations && instructor.specializations.length > 0 ? (
                          <div>
                            {instructor.specializations.slice(0, 2).map((spec, index) => (
                              <span key={index} className="badge bg-primary-subtle text-primary me-1 mb-1">
                                {spec}
                              </span>
                            ))}
                            {instructor.specializations.length > 2 && (
                              <span className="text-muted">+{instructor.specializations.length - 2} more</span>
                            )}
                          </div>
                        ) : (
                          <span className="text-muted">-</span>
                        )}
                      </td>
                      <td className="text-muted">
                        {new Date(instructor.dateJoined).toLocaleDateString()}
                      </td>
                      <td>
                        <div className="d-flex gap-2">
                          <button
                            className="btn btn-sm btn-outline-info"
                            onClick={() => openViewModal(instructor)}
                            title="View Details"
                          >
                            <i className="bi bi-eye"></i>
                          </button>
                          <button
                            className="btn btn-sm btn-outline-warning"
                            onClick={() => openEditModal(instructor)}
                            title="Edit"
                          >
                            <i className="bi bi-pencil"></i>
                          </button>
                          <button
                            className="btn btn-sm btn-outline-danger"
                            onClick={() => handleDeleteInstructor(instructor._id)}
                            title="Delete"
                          >
                            <i className="bi bi-trash"></i>
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Add Instructor Modal */}
      {showAddForm && (
        <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Add New Instructor</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowAddForm(false)}
                ></button>
              </div>
              <form onSubmit={handleAddInstructor}>
                <div className="modal-body">
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Name *</label>
                      <input
                        type="text"
                        className="form-control"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                        required
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Email *</label>
                      <input
                        type="email"
                        className="form-control"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                      />
                    </div>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Bio</label>
                    <textarea
                      className="form-control"
                      name="bio"
                      value={formData.bio}
                      onChange={handleInputChange}
                      rows="3"
                    ></textarea>
                  </div>
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Experience (years)</label>
                      <input
                        type="number"
                        className="form-control"
                        name="experience"
                        value={formData.experience}
                        onChange={handleInputChange}
                        min="0"
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Specializations</label>
                      <input
                        type="text"
                        className="form-control"
                        name="specializations"
                        value={formData.specializations}
                        onChange={handleInputChange}
                        placeholder="React, Node.js, JavaScript (comma separated)"
                      />
                    </div>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Profile Image URL</label>
                    <input
                      type="url"
                      className="form-control"
                      name="profileImage"
                      value={formData.profileImage}
                      onChange={handleInputChange}
                    />
                  </div>
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowAddForm(false)}
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Add Instructor
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* View Modal */}
      {showViewModal && selectedInstructor && (
        <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Instructor Details</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowViewModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <div className="row">
                  <div className="col-md-4 text-center mb-3">
                    {selectedInstructor.profileImage ? (
                      <img
                        src={selectedInstructor.profileImage}
                        alt={selectedInstructor.name}
                        className="rounded-circle shadow"
                        style={{ width: "120px", height: "120px", objectFit: "cover" }}
                      />
                    ) : (
                      <div
                        className="bg-primary rounded-circle d-flex align-items-center justify-content-center text-white shadow mx-auto"
                        style={{ width: "120px", height: "120px", fontSize: "32px" }}
                      >
                        {selectedInstructor.name
                          .split(" ")
                          .map((n) => n[0])
                          .join("")}
                      </div>
                    )}
                  </div>
                  <div className="col-md-8">
                    <h4 className="fw-bold">{selectedInstructor.name}</h4>
                    <p className="text-muted mb-3">{selectedInstructor.email}</p>
                    
                    {selectedInstructor.bio && (
                      <div className="mb-3">
                        <strong>Bio:</strong>
                        <p className="text-muted">{selectedInstructor.bio}</p>
                      </div>
                    )}
                    
                    <div className="mb-3">
                      <strong>Experience:</strong> {selectedInstructor.experience || 0} years
                    </div>
                    
                    {selectedInstructor.specializations && selectedInstructor.specializations.length > 0 && (
                      <div className="mb-3">
                        <strong>Specializations:</strong>
                        <div className="mt-2">
                          {selectedInstructor.specializations.map((spec, index) => (
                            <span key={index} className="badge bg-primary-subtle text-primary me-2 mb-1">
                              {spec}
                            </span>
                          ))}
                        </div>
                      </div>
                    )}
                    
                    <div className="mb-3">
                      <strong>Joined:</strong> {new Date(selectedInstructor.dateJoined).toLocaleDateString()}
                    </div>
                    
                    {selectedInstructor.rating > 0 && (
                      <div className="mb-3">
                        <strong>Rating:</strong> 
                        <span className="text-warning ms-2">
                          <i className="bi bi-star-fill"></i> {selectedInstructor.rating.toFixed(1)}
                        </span>
                      </div>
                    )}
                    
                    {selectedInstructor.totalStudents > 0 && (
                      <div className="mb-3">
                        <strong>Total Students:</strong> {selectedInstructor.totalStudents}
                      </div>
                    )}
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowViewModal(false)}
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Edit Modal */}
      {showEditModal && selectedInstructor && (
        <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Edit Instructor</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowEditModal(false)}
                ></button>
              </div>
              <form onSubmit={handleEditInstructor}>
                <div className="modal-body">
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Name *</label>
                      <input
                        type="text"
                        className="form-control"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                        required
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Email *</label>
                      <input
                        type="email"
                        className="form-control"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                      />
                    </div>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Bio</label>
                    <textarea
                      className="form-control"
                      name="bio"
                      value={formData.bio}
                      onChange={handleInputChange}
                      rows="3"
                    ></textarea>
                  </div>
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Experience (years)</label>
                      <input
                        type="number"
                        className="form-control"
                        name="experience"
                        value={formData.experience}
                        onChange={handleInputChange}
                        min="0"
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Specializations</label>
                      <input
                        type="text"
                        className="form-control"
                        name="specializations"
                        value={formData.specializations}
                        onChange={handleInputChange}
                        placeholder="React, Node.js, JavaScript (comma separated)"
                      />
                    </div>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Profile Image URL</label>
                    <input
                      type="url"
                      className="form-control"
                      name="profileImage"
                      value={formData.profileImage}
                      onChange={handleInputChange}
                    />
                  </div>
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowEditModal(false)}
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Update Instructor
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Instructors;
