import React, { useState, useEffect } from "react";
import { Plus, Edit2, Trash2, ChevronDown, ChevronRight, Search, Filter, RotateCcw } from "lucide-react";

const ManageCategories = () => {
  const [categories, setCategories] = useState([
    { id: 1, name: "Technology", isActive: true, parentId: null, level: 0, order: 1 },
    { id: 2, name: "Web Development", isActive: true, parentId: 1, level: 1, order: 1 },
    { id: 3, name: "React.js", isActive: true, parentId: 2, level: 2, order: 1 },
    { id: 4, name: "Vue.js", isActive: true, parentId: 2, level: 2, order: 2 },
    { id: 5, name: "Mobile Development", isActive: true, parentId: 1, level: 1, order: 2 },
    { id: 6, name: "React Native", isActive: true, parentId: 5, level: 2, order: 1 },
    { id: 7, name: "Flutter", isActive: true, parentId: 5, level: 2, order: 2 },
    { id: 8, name: "Business", isActive: true, parentId: null, level: 0, order: 2 },
    { id: 9, name: "Marketing", isActive: true, parentId: 8, level: 1, order: 1 },
    { id: 10, name: "Digital Marketing", isActive: true, parentId: 9, level: 2, order: 1 },
  ]);

  const [formData, setFormData] = useState({
    name: "",
    parentId: null,
    level: 0,
    isActive: true
  });

  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [expandedItems, setExpandedItems] = useState(new Set([1, 8])); // Default expanded
  const [searchTerm, setSearchTerm] = useState("");
  const [filterLevel, setFilterLevel] = useState("all");
  const [sortBy, setSortBy] = useState("name");

  // Helper functions
  const getCategoriesByLevel = (level) => {
    return categories.filter(cat => cat.level === level && cat.isActive);
  };

  const getChildCategories = (parentId) => {
    return categories
      .filter(cat => cat.parentId === parentId && cat.isActive)
      .sort((a, b) => {
        if (sortBy === "name") return a.name.localeCompare(b.name);
        if (sortBy === "order") return a.order - b.order;
        return a.id - b.id;
      });
  };

  const getCategoryPath = (categoryId) => {
    const category = categories.find(cat => cat.id === categoryId);
    if (!category) return "";
    
    const path = [];
    let current = category;
    
    while (current) {
      path.unshift(current.name);
      current = categories.find(cat => cat.id === current.parentId);
    }
    
    return path.join(" â†’ ");
  };

  const getChildrenCount = (categoryId) => {
    return categories.filter(cat => cat.parentId === categoryId).length;
  };

  const hasChildren = (categoryId) => {
    return categories.some(cat => cat.parentId === categoryId);
  };

  // Toggle expand/collapse
  const toggleExpanded = (categoryId) => {
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(categoryId)) {
      newExpanded.delete(categoryId);
    } else {
      newExpanded.add(categoryId);
    }
    setExpandedItems(newExpanded);
  };

  // Form handlers
  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value,
      ...(field === 'level' && value === 0 ? { parentId: null } : {})
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!formData.name.trim()) {
      alert("Please enter a category name");
      return;
    }

    if (formData.level > 0 && !formData.parentId) {
      alert("Please select a parent category");
      return;
    }

    const categoryData = {
      ...formData,
      name: formData.name.trim(),
      order: editingId ? 
        categories.find(cat => cat.id === editingId)?.order || 1 :
        Math.max(...categories.filter(cat => cat.parentId === formData.parentId).map(cat => cat.order), 0) + 1
    };

    if (editingId) {
      setCategories(prev => 
        prev.map(cat => 
          cat.id === editingId ? { ...cat, ...categoryData } : cat
        )
      );
    } else {
      const newCategory = {
        ...categoryData,
        id: Math.max(...categories.map(c => c.id), 0) + 1,
      };
      setCategories(prev => [...prev, newCategory]);
    }

    resetForm();
  };

  const resetForm = () => {
    setFormData({
      name: "",
      parentId: null,
      level: 0,
      isActive: true
    });
    setEditingId(null);
    setShowModal(false);
  };

  const handleEdit = (category) => {
    setFormData({
      name: category.name,
      parentId: category.parentId,
      level: category.level,
      isActive: category.isActive
    });
    setEditingId(category.id);
    setShowModal(true);
  };

  const handleDelete = (categoryId) => {
    if (hasChildren(categoryId)) {
      alert("Cannot delete category with subcategories. Please delete subcategories first.");
      return;
    }

    if (window.confirm("Are you sure you want to delete this category?")) {
      setCategories(prev => prev.filter(cat => cat.id !== categoryId));
    }
  };

  // Filter and search
  const getFilteredCategories = () => {
    let filtered = categories.filter(cat => cat.isActive);

    if (searchTerm) {
      filtered = filtered.filter(cat => 
        cat.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (filterLevel !== "all") {
      filtered = filtered.filter(cat => cat.level === parseInt(filterLevel));
    }

    return filtered;
  };

  // Render category tree
  const renderCategoryTree = (parentId = null, level = 0) => {
    const childCategories = getChildCategories(parentId);
    const filteredCategories = getFilteredCategories();
    
    const categoriesToShow = childCategories.filter(cat => 
      searchTerm || filterLevel !== "all" ? 
      filteredCategories.some(filtered => filtered.id === cat.id) : 
      true
    );

    if (categoriesToShow.length === 0) return null;

    return categoriesToShow.map(category => {
      const isExpanded = expandedItems.has(category.id);
      const childrenExist = hasChildren(category.id);
      const indentClass = level === 0 ? "" : level === 1 ? "ml-6" : "ml-12";
      
      const levelConfig = {
        0: { bg: "bg-blue-50", badge: "bg-blue-500", icon: "ğŸ¢" },
        1: { bg: "bg-green-50", badge: "bg-green-500", icon: "ğŸ“" },
        2: { bg: "bg-purple-50", badge: "bg-purple-500", icon: "ğŸ“„" }
      };

      const config = levelConfig[category.level] || levelConfig[0];

      return (
        <div key={category.id} className={`${indentClass} mb-2`}>
          <div className={`border border-gray-200 rounded-lg ${config.bg}`}>
            <div className="p-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  {childrenExist && (
                    <button
                      onClick={() => toggleExpanded(category.id)}
                      className="text-gray-500 hover:text-gray-700 transition-colors"
                    >
                      {isExpanded ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
                    </button>
                  )}
                  
                  <div className="flex items-center space-x-2">
                    <span className="text-lg">{config.icon}</span>
                    <span className={`px-2 py-1 rounded text-xs text-white font-medium ${config.badge}`}>
                      {category.level === 0 ? "Main" : category.level === 1 ? "Sub" : "Sub-Sub"}
                    </span>
                  </div>
                  
                  <div>
                    <h4 className="font-semibold text-gray-900">{category.name}</h4>
                    {category.level > 0 && (
                      <p className="text-sm text-gray-600 mt-1">
                        {getCategoryPath(category.id)}
                      </p>
                    )}
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  {childrenExist && (
                    <span className="bg-gray-200 text-gray-700 px-2 py-1 rounded-full text-xs">
                      {getChildrenCount(category.id)} items
                    </span>
                  )}
                  
                  <button
                    onClick={() => handleEdit(category)}
                    className="p-2 text-blue-600 hover:bg-blue-100 rounded-lg transition-colors"
                    title="Edit"
                  >
                    <Edit2 size={16} />
                  </button>
                  
                  <button
                    onClick={() => handleDelete(category.id)}
                    className="p-2 text-red-600 hover:bg-red-100 rounded-lg transition-colors"
                    title="Delete"
                  >
                    <Trash2 size={16} />
                  </button>
                </div>
              </div>
            </div>

            {isExpanded && childrenExist && (
              <div className="border-t border-gray-200 p-2">
                {renderCategoryTree(category.id, level + 1)}
              </div>
            )}
          </div>
        </div>
      );
    });
  };

  const getAvailableParents = (level) => {
    if (level === 0) return [];
    if (level === 1) return getCategoriesByLevel(0);
    if (level === 2) return getCategoriesByLevel(1);
    return [];
  };

  return (
    <div className="max-w-7xl mx-auto p-6 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Category Management</h1>
          <p className="text-gray-600 mt-2">Organize your categories in a hierarchical structure</p>
        </div>
        <button
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
          className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg flex items-center space-x-2 transition-colors"
        >
          <Plus size={20} />
          <span>Add Category</span>
        </button>
      </div>

      {/* Statistics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex items-center">
            <div className="p-3 bg-blue-100 rounded-lg">
              <span className="text-2xl">ğŸ¢</span>
            </div>
            <div className="ml-4">
              <p className="text-gray-600 text-sm">Main Categories</p>
              <p className="text-2xl font-bold text-blue-600">{getCategoriesByLevel(0).length}</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex items-center">
            <div className="p-3 bg-green-100 rounded-lg">
              <span className="text-2xl">ğŸ“</span>
            </div>
            <div className="ml-4">
              <p className="text-gray-600 text-sm">Sub Categories</p>
              <p className="text-2xl font-bold text-green-600">{getCategoriesByLevel(1).length}</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex items-center">
            <div className="p-3 bg-purple-100 rounded-lg">
              <span className="text-2xl">ğŸ“„</span>
            </div>
            <div className="ml-4">
              <p className="text-gray-600 text-sm">Sub-Sub Categories</p>
              <p className="text-2xl font-bold text-purple-600">{getCategoriesByLevel(2).length}</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex items-center">
            <div className="p-3 bg-gray-100 rounded-lg">
              <span className="text-2xl">ğŸ“Š</span>
            </div>
            <div className="ml-4">
              <p className="text-gray-600 text-sm">Total Categories</p>
              <p className="text-2xl font-bold text-gray-600">{categories.filter(cat => cat.isActive).length}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Filters and Search */}
      <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200 mb-8">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between space-y-4 md:space-y-0">
          <div className="flex flex-col md:flex-row space-y-4 md:space-y-0 md:space-x-4">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
              <input
                type="text"
                placeholder="Search categories..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            <select
              value={filterLevel}
              onChange={(e) => setFilterLevel(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="all">All Levels</option>
              <option value="0">Main Categories</option>
              <option value="1">Sub Categories</option>
              <option value="2">Sub-Sub Categories</option>
            </select>

            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="name">Sort by Name</option>
              <option value="order">Sort by Order</option>
              <option value="id">Sort by ID</option>
            </select>
          </div>

          <button
            onClick={() => {
              setSearchTerm("");
              setFilterLevel("all");
              setSortBy("name");
            }}
            className="flex items-center space-x-2 px-4 py-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <RotateCcw size={16} />
            <span>Reset Filters</span>
          </button>
        </div>
      </div>

      {/* Categories Tree */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold text-gray-900">Categories Structure</h2>
          <div className="flex space-x-2">
            <button
              onClick={() => setExpandedItems(new Set(categories.map(cat => cat.id)))}
              className="text-sm text-blue-600 hover:text-blue-800 px-3 py-1 rounded"
            >
              Expand All
            </button>
            <button
              onClick={() => setExpandedItems(new Set())}
              className="text-sm text-gray-600 hover:text-gray-800 px-3 py-1 rounded"
            >
              Collapse All
            </button>
          </div>
        </div>

        <div className="space-y-2">
          {renderCategoryTree()}
        </div>

        {getFilteredCategories().length === 0 && (
          <div className="text-center py-12">
            <div className="text-gray-400 mb-4">
              <Filter size={48} className="mx-auto" />
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">No categories found</h3>
            <p className="text-gray-600">Try adjusting your search or filter criteria</p>
          </div>
        )}
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-center mb-6">
                <h3 className="text-xl font-semibold text-gray-900">
                  {editingId ? "Edit Category" : "Add New Category"}
                </h3>
                <button
                  onClick={resetForm}
                  className="text-gray-400 hover:text-gray-600"
                >
                  âœ•
                </button>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Category Level
                  </label>
                  <select
                    value={formData.level}
                    onChange={(e) => handleInputChange('level', parseInt(e.target.value))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value={0}>Main Category</option>
                    <option value={1}>Sub Category</option>
                    <option value={2}>Sub-Sub Category</option>
                  </select>
                </div>

                {formData.level > 0 && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Parent Category
                    </label>
                    <select
                      value={formData.parentId || ""}
                      onChange={(e) => handleInputChange('parentId', parseInt(e.target.value) || null)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      required
                    >
                      <option value="">Select Parent Category</option>
                      {getAvailableParents(formData.level).map(parent => (
                        <option key={parent.id} value={parent.id}>
                          {getCategoryPath(parent.id)}
                        </option>
                      ))}
                    </select>
                  </div>
                )}

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Category Name
                  </label>
                  <input
                    type="text"
                    value={formData.name}
                    onChange={(e) => handleInputChange('name', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Enter category name"
                    required
                  />
                </div>

                {formData.name && formData.parentId && (
                  <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
                    <p className="text-sm text-blue-800">
                      <strong>Preview:</strong> {getCategoryPath(formData.parentId)} â†’ {formData.name}
                    </p>
                  </div>
                )}

                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={resetForm}
                    className="px-4 py-2 text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-lg transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    type="button"
                    onClick={handleSubmit}
                    className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
                  >
                    {editingId ? "Update" : "Create"} Category
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageCategories;
