import React, { useState } from "react";

function AdminLogin() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState(""); // Unified for both success/error messages

    const handleLogin = (e) => {
        e.preventDefault();
        setMessage(""); // Clear previous messages

        // --- SIMULATED LOGIN LOGIC (NO API CALLS) ---
        if (email === "admin@example.com" && password === "password") {
            setMessage("Login successful! (Simulated)");
            console.log("Admin Login Success (Simulated)!");
            // In a real application, you would typically redirect the user here
            // or update a global authentication state after a successful login.
        } else {
            setMessage("Invalid email or password. (Simulated)");
        }
        // --- END SIMULATED LOGIN LOGIC ---
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100 font-inter">
            <div className="bg-white shadow-xl p-8 rounded-lg w-full max-w-sm md:max-w-md">
                <h2 className="text-3xl font-bold text-center mb-7 text-gray-800">Admin Login</h2>

                {message && (
                    <div className={`p-3 mb-5 rounded-md text-sm font-medium ${
                        message.includes("successful") ? "bg-green-100 text-green-700" : "bg-red-100 text-red-700"
                    } shadow-sm`}>
                        {message}
                    </div>
                )}

                <form onSubmit={handleLogin}>
                    <div className="mb-5">
                        <label className="block text-gray-700 text-sm font-medium mb-2" htmlFor="email">
                            Email address
                        </label>
                        <input
                            type="email"
                            id="email"
                            className="shadow-sm appearance-none border border-gray-300 rounded-md w-full py-2.5 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            placeholder="Enter email"
                        />
                    </div>
                    <div className="mb-6">
                        <label className="block text-gray-700 text-sm font-medium mb-2" htmlFor="password">
                            Password
                        </label>
                        <input
                            type="password"
                            id="password"
                            className="shadow-sm appearance-none border border-gray-300 rounded-md w-full py-2.5 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            placeholder="Enter password"
                        />
                    </div>
                    <button
                        type="submit"
                        className="bg-gray-800 hover:bg-gray-700 text-white font-bold py-2.5 px-4 rounded-md w-full focus:outline-none focus:shadow-outline transition duration-300 ease-in-out transform hover:scale-105"
                    >
                        Login
                    </button>
                </form>
            </div>
        </div>
    );
}

export default AdminLogin;
