import { Outlet, useNavigate, useLocation } from "react-router-dom";

const AdminDashboard = () => {
    const navigate = useNavigate();
    const location = useLocation();

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            {/* Sidebar */}
            <div style={{ width: "200px", borderRight: "1px solid #ccc", padding: "20px" }}>
                <h3>Admin Panel</h3>
                <ul style={{ listStyle: "none", padding: 0 }}>
                    <li
                        onClick={() => navigate("/admin-dashboard")}
                        style={{
                            cursor: "pointer",
                            margin: "10px 0",
                            fontWeight: location.pathname === "/admin-dashboard" ? "bold" : "normal",
                        }}
                    >
                        ðŸ“Š Dashboard
                    </li>
                    <li
                        onClick={() => navigate("/admin-dashboard/add-course")}
                        style={{
                            cursor: "pointer",
                            margin: "10px 0",
                            fontWeight: location.pathname === "/admin-dashboard/add-course" ? "bold" : "normal",
                        }}
                    >
                        âž• Add Course
                    </li>
                    <li
                        onClick={() => navigate("/admin-dashboard/manage-courses")}
                        style={{
                            cursor: "pointer",
                            margin: "10px 0",
                            fontWeight: location.pathname === "/admin-dashboard/manage-courses" ? "bold" : "normal",
                        }}
                    >
                        ðŸ“‹ Manage Courses
                    </li>
                </ul>
            </div>

            {/* Main content area */}
            <div style={{ flex: 1, padding: "20px", overflowY: "auto" }}>
                <Outlet />
            </div>
        </div>
    );
};

export default AdminDashboard;
