import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageCategories = () => {
    const [categories, setCategories] = useState([]);
    const [name, setName] = useState("");
    const [slug, setSlug] = useState("");
    const [parentCategory, setParentCategory] = useState("");
    const [level, setLevel] = useState(1);
    const [editingId, setEditingId] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [showBulkModal, setShowBulkModal] = useState(false);
    const [expandedCategories, setExpandedCategories] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    // Bulk category creation state
    const [bulkCategory, setBulkCategory] = useState({
        mainCategory: { name: "", slug: "" },
        subcategories: [{ name: "", slug: "", subSubcategories: [{ name: "", slug: "" }] }]
    });

    // Auto-generate slug when name changes
    useEffect(() => {
        if (name && !editingId) {
            const generatedSlug = name.toLowerCase()
                .replace(/\s+/g, "-")
                .replace(/[^a-z0-9-]/g, "");
            setSlug(generatedSlug);
        }
    }, [name, editingId]);

    // Generate slug helper function
    const generateSlug = (text) => {
        return text.toLowerCase()
            .replace(/\s+/g, "-")
            .replace(/[^a-z0-9-]/g, "");
    };

    // Fetch categories from backend
    const fetchCategories = useCallback(async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem("adminToken");
            if (!token) {
                setError("No admin token found. Please login again.");
                return;
            }

            const res = await axios.get("http://localhost:5000/api/admin/categories", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setCategories(res.data);
            setError("");
        } catch (err) {
            console.error("Error fetching categories", err);
            setError(err.response?.data?.message || "Failed to fetch categories");
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchCategories();
    }, [fetchCategories]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        const token = localStorage.getItem("adminToken");
        if (!token) {
            setError("No admin token found. Please login again.");
            setLoading(false);
            return;
        }

        const categoryPayload = {
            name: name.trim(),
            slug: slug.trim() || name.toLowerCase().replace(/\s+/g, "-"),
            parentCategory: parentCategory || null,
            level: level,
        };

        // Validation
        if (!categoryPayload.name) {
            setError("Category name is required");
            setLoading(false);
            return;
        }

        if (level > 1 && !parentCategory) {
            setError("Parent category is required for subcategories");
            setLoading(false);
            return;
        }

        try {
            if (editingId) {
                const res = await axios.put(
                    `http://localhost:5000/api/admin/category/${editingId}`,
                    categoryPayload,
                    {
                        headers: { Authorization: `Bearer ${token}` }
                    }
                );

                setCategories(prev => prev.map(cat =>
                    cat._id === editingId ? res.data.category : cat
                ));
                setError("");
                alert("Category updated successfully!");
            } else {
                const res = await axios.post(
                    "http://localhost:5000/api/admin/add-category",
                    categoryPayload,
                    {
                        headers: { Authorization: `Bearer ${token}` },
                    }
                );

                setCategories(prev => [...prev, res.data.category]);
                setError("");
                alert("Category added successfully!");
            }

            resetForm();
            setShowModal(false);
        } catch (err) {
            console.error("Error submitting category:", err);
            const errorMessage = err.response?.data?.message || "Failed to submit category";
            setError(errorMessage);
            alert(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    // Handle bulk category creation
    const handleBulkSubmit = async () => {
        setLoading(true);
        setError("");

        const token = localStorage.getItem("adminToken");
        if (!token) {
            setError("No admin token found. Please login again.");
            setLoading(false);
            return;
        }

        // Validation
        if (!bulkCategory.mainCategory.name.trim()) {
            setError("Main category name is required");
            setLoading(false);
            return;
        }

        try {
            // Create main category first
            const mainCategoryData = {
                name: bulkCategory.mainCategory.name.trim(),
                slug: bulkCategory.mainCategory.slug || generateSlug(bulkCategory.mainCategory.name),
                level: 1,
                parentCategory: null
            };

            const mainCategoryRes = await axios.post(
                "http://localhost:5000/api/admin/add-category",
                mainCategoryData,
                { headers: { Authorization: `Bearer ${token}` } }
            );

            const mainCategoryId = mainCategoryRes.data.category._id;
            let allNewCategories = [mainCategoryRes.data.category];

            // Create subcategories
            for (const subcategory of bulkCategory.subcategories) {
                if (subcategory.name.trim()) {
                    const subcategoryData = {
                        name: subcategory.name.trim(),
                        slug: subcategory.slug || generateSlug(subcategory.name),
                        level: 2,
                        parentCategory: mainCategoryId
                    };

                    const subcategoryRes = await axios.post(
                        "http://localhost:5000/api/admin/add-category",
                        subcategoryData,
                        { headers: { Authorization: `Bearer ${token}` } }
                    );

                    const subcategoryId = subcategoryRes.data.category._id;
                    allNewCategories.push(subcategoryRes.data.category);

                    // Create sub-subcategories
                    for (const subSubcategory of subcategory.subSubcategories) {
                        if (subSubcategory.name.trim()) {
                            const subSubcategoryData = {
                                name: subSubcategory.name.trim(),
                                slug: subSubcategory.slug || generateSlug(subSubcategory.name),
                                level: 3,
                                parentCategory: subcategoryId
                            };

                            const subSubcategoryRes = await axios.post(
                                "http://localhost:5000/api/admin/add-category",
                                subSubcategoryData,
                                { headers: { Authorization: `Bearer ${token}` } }
                            );

                            allNewCategories.push(subSubcategoryRes.data.category);
                        }
                    }
                }
            }

            // Update categories state
            setCategories(prev => [...prev, ...allNewCategories]);
            alert("Category hierarchy created successfully!");
            resetBulkForm();
            setShowBulkModal(false);

        } catch (err) {
            console.error("Error creating bulk categories:", err);
            const errorMessage = err.response?.data?.message || "Failed to create category hierarchy";
            setError(errorMessage);
            alert(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    const resetForm = () => {
        setName("");
        setSlug("");
        setParentCategory("");
        setLevel(1);
        setEditingId(null);
        setError("");
    };

    const resetBulkForm = () => {
        setBulkCategory({
            mainCategory: { name: "", slug: "" },
            subcategories: [{ name: "", slug: "", subSubcategories: [{ name: "", slug: "" }] }]
        });
        setError("");
    };

    // Bulk form handlers
    const updateMainCategory = (field, value) => {
        setBulkCategory(prev => ({
            ...prev,
            mainCategory: {
                ...prev.mainCategory,
                [field]: value,
                ...(field === 'name' && { slug: generateSlug(value) })
            }
        }));
    };

    const addSubcategory = () => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: [...prev.subcategories, { name: "", slug: "", subSubcategories: [{ name: "", slug: "" }] }]
        }));
    };

    const updateSubcategory = (index, field, value) => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: prev.subcategories.map((sub, i) =>
                i === index ? {
                    ...sub,
                    [field]: value,
                    ...(field === 'name' && { slug: generateSlug(value) })
                } : sub
            )
        }));
    };

    const removeSubcategory = (index) => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: prev.subcategories.filter((_, i) => i !== index)
        }));
    };

    const addSubSubcategory = (subcategoryIndex) => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: prev.subcategories.map((sub, i) =>
                i === subcategoryIndex ? {
                    ...sub,
                    subSubcategories: [...sub.subSubcategories, { name: "", slug: "" }]
                } : sub
            )
        }));
    };

    const updateSubSubcategory = (subcategoryIndex, subSubIndex, field, value) => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: prev.subcategories.map((sub, i) =>
                i === subcategoryIndex ? {
                    ...sub,
                    subSubcategories: sub.subSubcategories.map((subSub, j) =>
                        j === subSubIndex ? {
                            ...subSub,
                            [field]: value,
                            ...(field === 'name' && { slug: generateSlug(value) })
                        } : subSub
                    )
                } : sub
            )
        }));
    };

    const removeSubSubcategory = (subcategoryIndex, subSubIndex) => {
        setBulkCategory(prev => ({
            ...prev,
            subcategories: prev.subcategories.map((sub, i) =>
                i === subcategoryIndex ? {
                    ...sub,
                    subSubcategories: sub.subSubcategories.filter((_, j) => j !== subSubIndex)
                } : sub
            )
        }));
    };

    const handleEdit = (category) => {
        setName(category.name);
        setSlug(category.slug);
        setParentCategory(category.parentCategory || "");
        setLevel(category.level || 1);
        setEditingId(category._id);
        setShowModal(true);
        setError("");
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Are you sure you want to delete this category? This will also delete all its subcategories.")) return;

        setLoading(true);
        try {
            const token = localStorage.getItem("adminToken");
            await axios.delete(`http://localhost:5000/api/admin/category/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setCategories(prev => {
                const removeCategory = (cats, targetId) => {
                    return cats.filter(cat => {
                        if (cat._id === targetId) return false;
                        if (cat.parentCategory === targetId) return false;
                        return true;
                    });
                };
                return removeCategory(prev, id);
            });

            alert("Category deleted successfully!");
        } catch (err) {
            console.error("Delete error:", err);
            const errorMessage = err.response?.data?.message || "Failed to delete category";
            alert(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    // Organize categories into hierarchical structure
    const organizeCategories = (cats) => {
        const categoryMap = {};
        const rootCategories = [];

        cats.forEach(cat => {
            categoryMap[cat._id] = { ...cat, children: [] };
        });

        cats.forEach(cat => {
            if (cat.parentCategory && categoryMap[cat.parentCategory]) {
                categoryMap[cat.parentCategory].children.push(categoryMap[cat._id]);
            } else {
                rootCategories.push(categoryMap[cat._id]);
            }
        });

        return rootCategories;
    };

    const toggleExpand = (categoryId) => {
        setExpandedCategories(prev => ({
            ...prev,
            [categoryId]: !prev[categoryId]
        }));
    };

    const renderCategoryTree = (cats, depth = 0) => {
        return cats.map((category, index) => (
            <React.Fragment key={category._id}>
                <tr>
                    <td>{index + 1}</td>
                    <td style={{ paddingLeft: `${depth * 20}px` }}>
                        <div className="d-flex align-items-center">
                            {category.children.length > 0 && (
                                <button
                                    className="btn btn-sm btn-link p-0 me-2"
                                    onClick={() => toggleExpand(category._id)}
                                    disabled={loading}
                                >
                                    <i className={`fas ${expandedCategories[category._id] ? 'fa-minus' : 'fa-plus'}`}></i>
                                </button>
                            )}
                            {category.children.length === 0 && (
                                <span className="me-3" style={{ width: '24px' }}></span>
                            )}
                            <span className={`badge me-2 ${category.level === 1 ? 'bg-primary' :
                                category.level === 2 ? 'bg-success' : 'bg-warning'
                                }`}>
                                {category.level === 1 ? 'Main' : category.level === 2 ? 'Sub' : 'Sub-Sub'}
                            </span>
                            {category.name}
                        </div>
                    </td>
                    <td>
                        <code>{category.slug}</code>
                    </td>
                    <td>
                        <div className="btn-group" role="group">
                            <button
                                className="btn btn-sm btn-outline-success"
                                onClick={() => {
                                    setParentCategory(category._id);
                                    setLevel(Math.min(category.level + 1, 3));
                                    setShowModal(true);
                                    resetForm();
                                }}
                                disabled={loading || category.level >= 3}
                                title={category.level >= 3 ? "Maximum depth reached" : "Add subcategory"}
                            >
                                Add Sub
                            </button>
                            <button
                                className="btn btn-sm btn-outline-primary"
                                onClick={() => handleEdit(category)}
                                disabled={loading}
                            >
                                Edit
                            </button>
                            <button
                                className="btn btn-sm btn-outline-danger"
                                onClick={() => handleDelete(category._id)}
                                disabled={loading}
                            >
                                Delete
                            </button>
                        </div>
                    </td>
                </tr>
                {expandedCategories[category._id] && category.children.length > 0 && (
                    <>
                        {renderCategoryTree(category.children, depth + 1)}
                    </>
                )}
            </React.Fragment>
        ));
    };
    const getAvailableParents = () => {
        if (level === 1) return [];
        if (level === 2) return categories.filter(cat => cat.level === 1);
        if (level === 3) return categories.filter(cat => cat.level === 2);
        return [];
    };

    const organizedCategories = organizeCategories(categories);

    return (
        
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2 className="mb-0">Manage Hierarchical Categories</h2>
                <div className="btn-group">
                    <button
                        className="btn btn-success"
                        onClick={() => {
                            resetBulkForm();
                            setShowBulkModal(true);
                        }}
                        disabled={loading}
                    >
                        <i className="fas fa-sitemap me-2"></i>
                        Add Category Hierarchy
                    </button>
                    <button
                        className="btn btn-primary"
                        onClick={() => {
                            resetForm();
                            setShowModal(true);
                        }}
                        disabled={loading}
                    >
                        <i className="fas fa-plus me-2"></i>
                        Add Single Category
                    </button>
                </div>
            </div>

            {/* Error Alert */}
            {error && (
                <div className="alert alert-danger alert-dismissible fade show" role="alert">
                    <i className="fas fa-exclamation-triangle me-2"></i>
                    {error}
                    <button
                        type="button"
                        className="btn-close"
                        onClick={() => setError("")}
                        aria-label="Close"
                    ></button>
                </div>
            )}

            {/* Loading Spinner */}
            {loading && (
                <div className="text-center mb-3">
                    <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            )}

            {/* Category Tree Table */}
            <div className="card">
                <div className="card-header d-flex justify-content-between align-items-center">
                    <h5 className="mb-0">
                        <i className="fas fa-sitemap me-2"></i>
                        Category Hierarchy
                    </h5>
                    <button
                        className="btn btn-sm btn-outline-secondary"
                        onClick={fetchCategories}
                        disabled={loading}
                    >
                        <i className="fas fa-sync-alt me-1"></i>
                        Refresh
                    </button>
                </div>
                <div className="card-body p-0">
                    <div className="table-responsive">
                        <table className="table table-hover mb-0">
                            <thead className="table-light">
                                <tr>
                                    <th width="60">No</th>
                                    <th>Name</th>
                                    <th width="200">Slug</th>
                                    <th width="200">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {organizedCategories.length > 0 ? (
                                    renderCategoryTree(organizedCategories)
                                ) : (
                                    <tr>
                                        <td colSpan="4" className="text-center text-muted py-5">
                                            <i className="fas fa-folder-open fa-3x mb-3 d-block"></i>
                                            {loading ? "Loading categories..." : "No categories found. Add your first category!"}
                                        </td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            {/* Bulk Category Creation Modal */}
            {showBulkModal && (
                <div className="modal" style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}>
                    <div className="modal-dialog modal-xl">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">
                                    <i className="fas fa-sitemap me-2"></i>
                                    Create Category Hierarchy
                                </h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    onClick={() => setShowBulkModal(false)}
                                    disabled={loading}
                                ></button>
                            </div>
                            <div className="modal-body" style={{ maxHeight: '70vh', overflowY: 'auto' }}>
                                {error && (
                                    <div className="alert alert-danger">
                                        <i className="fas fa-exclamation-triangle me-2"></i>
                                        {error}
                                    </div>
                                )}

                                {/* Main Category Section */}
                                <div className="card mb-4">
                                    <div className="card-header bg-primary text-white">
                                        <h6 className="mb-0">
                                            <i className="fas fa-layer-group me-2"></i>
                                            Main Category
                                        </h6>
                                    </div>
                                    <div className="card-body">
                                        <div className="row">
                                            <div className="col-md-8">
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    placeholder="Enter main category name (e.g., Web Development)"
                                                    value={bulkCategory.mainCategory.name}
                                                    onChange={(e) => updateMainCategory('name', e.target.value)}
                                                    disabled={loading}
                                                />
                                            </div>
                                            <div className="col-md-4">
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    placeholder="Slug (auto-generated)"
                                                    value={bulkCategory.mainCategory.slug}
                                                    onChange={(e) => updateMainCategory('slug', e.target.value)}
                                                    disabled={loading}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                {/* Subcategories Section */}
                                <div className="card">
                                    <div className="card-header bg-success text-white d-flex justify-content-between align-items-center">
                                        <h6 className="mb-0">
                                            <i className="fas fa-sitemap me-2"></i>
                                            Subcategories
                                        </h6>
                                        <button
                                            type="button"
                                            className="btn btn-light btn-sm"
                                            onClick={addSubcategory}
                                            disabled={loading}
                                        >
                                            <i className="fas fa-plus me-1"></i>
                                            Add Subcategory
                                        </button>
                                    </div>
                                    <div className="card-body">
                                        {bulkCategory.subcategories.map((subcategory, subIndex) => (
                                            <div key={subIndex} className="border rounded p-3 mb-3">
                                                <div className="d-flex justify-content-between align-items-center mb-3">
                                                    <h6 className="mb-0 text-success">
                                                        <i className="fas fa-folder me-2"></i>
                                                        Subcategory {subIndex + 1}
                                                    </h6>
                                                    {bulkCategory.subcategories.length > 1 && (
                                                        <button
                                                            type="button"
                                                            className="btn btn-outline-danger btn-sm"
                                                            onClick={() => removeSubcategory(subIndex)}
                                                            disabled={loading}
                                                        >
                                                            <i className="fas fa-trash"></i>
                                                        </button>
                                                    )}
                                                </div>

                                                <div className="row mb-3">
                                                    <div className="col-md-8">
                                                        <input
                                                            type="text"
                                                            className="form-control"
                                                            placeholder="Enter subcategory name (e.g., Frontend)"
                                                            value={subcategory.name}
                                                            onChange={(e) => updateSubcategory(subIndex, 'name', e.target.value)}
                                                            disabled={loading}
                                                        />
                                                    </div>
                                                    <div className="col-md-4">
                                                        <input
                                                            type="text"
                                                            className="form-control"
                                                            placeholder="Slug (auto-generated)"
                                                            value={subcategory.slug}
                                                            onChange={(e) => updateSubcategory(subIndex, 'slug', e.target.value)}
                                                            disabled={loading}
                                                        />
                                                    </div>
                                                </div>

                                                {/* Sub-subcategories */}
                                                <div className="ms-3">
                                                    <div className="d-flex justify-content-between align-items-center mb-2">
                                                        <small className="text-muted fw-bold">
                                                            <i className="fas fa-arrow-right me-1"></i>
                                                            Sub-subcategories:
                                                        </small>
                                                        <button
                                                            type="button"
                                                            className="btn btn-warning btn-sm"
                                                            onClick={() => addSubSubcategory(subIndex)}
                                                            disabled={loading}
                                                        >
                                                            <i className="fas fa-plus me-1"></i>
                                                            Add Sub-sub
                                                        </button>
                                                    </div>

                                                    {subcategory.subSubcategories.map((subSub, subSubIndex) => (
                                                        <div key={subSubIndex} className="row mb-2">
                                                            <div className="col-md-5">
                                                                <input
                                                                    type="text"
                                                                    className="form-control form-control-sm"
                                                                    placeholder="Sub-subcategory name (e.g., React)"
                                                                    value={subSub.name}
                                                                    onChange={(e) => updateSubSubcategory(subIndex, subSubIndex, 'name', e.target.value)}
                                                                    disabled={loading}
                                                                />
                                                            </div>
                                                            <div className="col-md-5">
                                                                <input
                                                                    type="text"
                                                                    className="form-control form-control-sm"
                                                                    placeholder="Slug (auto-generated)"
                                                                    value={subSub.slug}
                                                                    onChange={(e) => updateSubSubcategory(subIndex, subSubIndex, 'slug', e.target.value)}
                                                                    disabled={loading}
                                                                />
                                                            </div>
                                                            <div className="col-md-2">
                                                                {subcategory.subSubcategories.length > 1 && (
                                                                    <button
                                                                        type="button"
                                                                        className="btn btn-outline-danger btn-sm"
                                                                        onClick={() => removeSubSubcategory(subIndex, subSubIndex)}
                                                                        disabled={loading}
                                                                    >
                                                                        <i className="fas fa-trash"></i>
                                                                    </button>
                                                                )}
                                                            </div>
                                                        </div>
                                                    ))}
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={() => setShowBulkModal(false)}
                                    disabled={loading}
                                >
                                    Cancel
                                </button>
                                <button
                                    type="button"
                                    className="btn btn-success"
                                    onClick={handleBulkSubmit}
                                    disabled={loading || !bulkCategory.mainCategory.name.trim()}
                                >
                                    {loading ? (
                                        <>
                                            <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                                            Creating Hierarchy...
                                        </>
                                    ) : (
                                        <>
                                            <i className="fas fa-save me-2"></i>
                                            Create Category Hierarchy
                                        </>
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Single Category Modal (existing) */}
            {showModal && (
                <div className="modal" style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}>
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">
                                    <i className={`fas ${editingId ? 'fa-edit' : 'fa-plus'} me-2`}></i>
                                    {editingId ? "Edit Category" : "Add New Category"}
                                </h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    onClick={() => setShowModal(false)}
                                    disabled={loading}
                                ></button>
                            </div>
                            <div className="modal-body">
                                {error && (
                                    <div className="alert alert-danger">
                                        <i className="fas fa-exclamation-triangle me-2"></i>
                                        {error}
                                    </div>
                                )}
                                <form onSubmit={handleSubmit}>
                                    <div className="row">
                                        <div className="col-md-6 mb-3">
                                            <label className="form-label">
                                                <i className="fas fa-layer-group me-2"></i>
                                                Category Level
                                            </label>
                                            <select
                                                className="form-select"
                                                value={level}
                                                onChange={(e) => {
                                                    setLevel(parseInt(e.target.value));
                                                    setParentCategory("");
                                                }}
                                                disabled={editingId || loading}
                                            >
                                                <option value={1}>Main Category</option>
                                                <option value={2}>Subcategory</option>
                                                <option value={3}>Sub-subcategory</option>
                                            </select>
                                        </div>

                                        {level > 1 && (
                                            <div className="col-md-6 mb-3">
                                                <label className="form-label">
                                                    <i className="fas fa-sitemap me-2"></i>
                                                    Parent Category
                                                </label>
                                                <select
                                                    className="form-select"
                                                    value={parentCategory}
                                                    onChange={(e) => setParentCategory(e.target.value)}
                                                    required
                                                    disabled={loading}
                                                >
                                                    <option value="">Select Parent Category</option>
                                                    {getAvailableParents().map((cat) => (
                                                        <option key={cat._id} value={cat._id}>
                                                            {cat.name}
                                                        </option>
                                                    ))}
                                                </select>
                                            </div>
                                        )}
                                    </div>

                                    <div className="mb-3">
                                        <label className="form-label">
                                            <i className="fas fa-tag me-2"></i>
                                            Category Name
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={name}
                                            onChange={(e) => setName(e.target.value)}
                                            placeholder="Enter category name"
                                            required
                                            disabled={loading}
                                        />
                                    </div>

                                    <div className="mb-3">
                                        <label className="form-label">
                                            <i className="fas fa-link me-2"></i>
                                            Slug
                                        </label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={slug}
                                            onChange={(e) => setSlug(e.target.value)}
                                            placeholder="Auto-generated from name"
                                            disabled={loading}
                                        />
                                        <div className="form-text">
                                            Preview: <code>{slug || "auto-generated"}</code>
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={() => setShowModal(false)}
                                    disabled={loading}
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    onClick={handleSubmit}
                                    disabled={loading}
                                >
                                    {loading ? (
                                        <>
                                            <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                                            {editingId ? "Updating..." : "Saving..."}
                                        </>
                                    ) : (
                                        <>
                                            <i className={`fas ${editingId ? 'fa-save' : 'fa-plus'} me-2`}></i>
                                            {editingId ? "Update" : "Save"}
                                        </>
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Legend */}
            <div className="mt-4">
                <div className="card">
                    <div className="card-body">
                        <h6>
                            <i className="fas fa-info-circle me-2"></i>
                            Category Levels:
                        </h6>
                        <div className="d-flex gap-3 flex-wrap">
                            <span>
                                <span className="badge bg-primary">Main</span> - Top level categories
                            </span>
                            <span>
                                <span className="badge bg-success">Sub</span> - Subcategories
                            </span>
                            <span>
                                <span className="badge bg-warning">Sub-Sub</span> - Sub-subcategories (max depth)
                            </span>
                        </div>
                        <div className="mt-3">
                            <h6>
                                <i className="fas fa-lightbulb me-2"></i>
                                Quick Tips:
                            </h6>
                            <ul className="list-unstyled mb-0">
                                <li><i className="fas fa-check text-success me-2"></i>Use "Add Category Hierarchy" for creating complete structures at once</li>
                                <li><i className="fas fa-check text-success me-2"></i>Use "Add Single Category" for individual additions</li>
                                <li><i className="fas fa-check text-success me-2"></i>Slugs are auto-generated but can be customized</li>
                                <li><i className="fas fa-check text-success me-2"></i>Maximum depth is 3 levels (Main  Sub  Sub-Sub)</li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ManageCategories;
